/**
 * Advanced Code Analysis Engine for DevAgent
 * 
 * Provides sophisticated code analysis capabilities including:
 * - Context7 integration for documentation lookup
 * - Pattern recognition and learning
 * - Memory-driven code insights
 * - Constitutional AI validation for code quality
 * - Cross-agent learning from code interactions
 * 
 * @version 1.0.0
 * @created June 14, 2025
 */

import { UnifiedContext7MCPIntegration, DocumentationQuery, DocumentationResult } from '../mcp/UnifiedContext7MCPIntegration';
import { MemoryDrivenAgentCommunication, MemoryQuery } from './communication/MemoryDrivenAgentCommunication';

export interface CodeAnalysisRequest {
  code: string;
  language: string;
  context?: string;
  filePath?: string;
  problemDescription?: string;
  requestType: 'review' | 'debug' | 'optimize' | 'explain' | 'test' | 'refactor';
  userId: string;
  sessionId?: string;
}

export interface CodeAnalysisResult {
  analysis: string;
  suggestions: CodeSuggestion[];
  patterns: CodePattern[];
  documentation: DocumentationResult[];
  qualityScore: number;
  constitutionalCompliance: boolean;
  memoryInsights: MemoryInsight[];
  metadata: {
    analysisType: string;
    confidence: number;
    processingTime: number;
    context7Used: boolean;
    memoryEnhanced: boolean;
  };
}

export interface CodeSuggestion {
  type: 'fix' | 'improvement' | 'best-practice' | 'security' | 'performance';
  description: string;
  code?: string;
  lineNumber?: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
  reasoning: string;
  documentationRef?: string;
}

export interface CodePattern {
  name: string;
  description: string;
  frequency: number;
  quality: number;
  lastSeen: Date;
  examples: string[];
  relatedDocumentation: string[];
}

export interface MemoryInsight {
  type: 'pattern' | 'solution' | 'best-practice' | 'anti-pattern';
  content: string;
  confidence: number;
  source: string;
  timestamp: Date;
}

/**
 * Advanced Code Analysis Engine
 */
export class AdvancedCodeAnalysisEngine {
  private context7Integration: UnifiedContext7MCPIntegration;
  private memoryComm: MemoryDrivenAgentCommunication;
  private agentId: string;
  
  // Analysis patterns and metrics
  private languagePatterns: Map<string, CodePattern[]> = new Map();
  private analysisMetrics = {
    totalAnalyses: 0,
    successfulAnalyses: 0,
    averageQualityScore: 0,
    context7Usage: 0,
    memoryEnhancements: 0
  };

  constructor(agentId: string, memoryComm: MemoryDrivenAgentCommunication) {
    this.agentId = agentId;
    this.memoryComm = memoryComm;
    this.context7Integration = new UnifiedContext7MCPIntegration(agentId);
    
    this.initializeLanguagePatterns();
  }

  /**
   * Perform comprehensive code analysis
   */
  async analyzeCode(request: CodeAnalysisRequest): Promise<CodeAnalysisResult> {
    const startTime = Date.now();
    this.analysisMetrics.totalAnalyses++;

    try {
      console.log(`[CodeAnalysis] Starting ${request.requestType} analysis for ${request.language}`);

      // Step 1: Get memory insights from previous similar analyses
      const memoryInsights = await this.getMemoryInsights(request);

      // Step 2: Perform language-specific analysis
      const baseAnalysis = await this.performBaseAnalysis(request);

      // Step 3: Get relevant documentation via context7
      const documentation = await this.getRelevantDocumentation(request, baseAnalysis);

      // Step 4: Extract and learn patterns
      const patterns = await this.extractCodePatterns(request);

      // Step 5: Generate suggestions with Constitutional AI validation
      const suggestions = await this.generateSuggestions(request, baseAnalysis, documentation, memoryInsights);

      // Step 6: Calculate quality score
      const qualityScore = await this.calculateQualityScore(request, suggestions);

      // Step 7: Validate constitutional compliance
      const constitutionalCompliance = await this.validateConstitutionalCompliance(baseAnalysis, suggestions);

      const result: CodeAnalysisResult = {
        analysis: baseAnalysis,
        suggestions,
        patterns,
        documentation,
        qualityScore,
        constitutionalCompliance,
        memoryInsights,
        metadata: {
          analysisType: request.requestType,
          confidence: this.calculateConfidence(suggestions, documentation, memoryInsights),
          processingTime: Date.now() - startTime,
          context7Used: documentation.length > 0,
          memoryEnhanced: memoryInsights.length > 0
        }
      };

      // Step 8: Store analysis in memory for learning
      await this.storeAnalysisForLearning(request, result);

      this.analysisMetrics.successfulAnalyses++;
      this.updateMetrics(result);

      return result;

    } catch (error) {
      console.error(`[CodeAnalysis] Analysis failed:`, error);
      return this.generateFallbackAnalysis(request);
    }
  }

  /**
   * Get memory insights from previous similar analyses
   */  private async getMemoryInsights(request: CodeAnalysisRequest): Promise<MemoryInsight[]> {
    try {
      const searchQuery: MemoryQuery = {
        query: `${request.language} ${request.requestType} ${request.problemDescription || ''} code analysis`,
        limit: 5,
        minQualityScore: 0.6
      };
      
      const memories = await this.memoryComm.searchCommunicationHistory(searchQuery);
      
      return memories.map(memory => ({
        type: this.classifyInsightType(memory.content),
        content: memory.content,
        confidence: memory.metadata?.confidenceLevel || 0.7,
        source: memory.fromAgent || 'unknown',
        timestamp: new Date(memory.timestamp)
      }));
    } catch (error) {
      console.warn(`[CodeAnalysis] Memory insights retrieval failed:`, error);
      return [];
    }
  }

  /**
   * Perform base code analysis
   */
  private async performBaseAnalysis(request: CodeAnalysisRequest): Promise<string> {
    const analysis = [];

    // Language-specific analysis
    switch (request.language.toLowerCase()) {
      case 'typescript':
      case 'javascript':
        analysis.push(await this.analyzeJavaScriptTypeScript(request));
        break;
      case 'python':
        analysis.push(await this.analyzePython(request));
        break;
      case 'java':
        analysis.push(await this.analyzeJava(request));
        break;
      default:
        analysis.push(await this.analyzeGenericCode(request));
    }

    // Common analysis patterns
    analysis.push(await this.analyzeCodeStructure(request));
    analysis.push(await this.analyzeSecurityPatterns(request));
    analysis.push(await this.analyzePerformancePatterns(request));

    return analysis.filter(Boolean).join('\n\n');
  }

  /**
   * Get relevant documentation via context7
   */
  private async getRelevantDocumentation(request: CodeAnalysisRequest, analysis: string): Promise<DocumentationResult[]> {
    try {
      this.analysisMetrics.context7Usage++;

      const queries: DocumentationQuery[] = [];

      // Language-specific documentation
      if (request.language) {        queries.push({
          source: this.getDocumentationSource(request.language),
          query: `${request.requestType} ${request.problemDescription || ''}`,
          context: request.context || '',
          userId: request.userId,
          sessionId: request.sessionId || 'unknown'
        });
      }

      // Framework-specific documentation
      const detectedFrameworks = this.detectFrameworks(request.code);
      for (const framework of detectedFrameworks) {        queries.push({
          source: framework,
          query: `${request.requestType} best practices`,
          context: analysis,
          userId: request.userId,
          sessionId: request.sessionId || 'unknown'
        });
      }

      const documentationResults: DocumentationResult[] = [];
      for (const query of queries) {
        const results = await this.context7Integration.queryDocumentation(query);
        documentationResults.push(...results);
      }

      return documentationResults
        .sort((a, b) => b.relevanceScore - a.relevanceScore)
        .slice(0, 5); // Top 5 most relevant

    } catch (error) {
      console.warn(`[CodeAnalysis] Documentation retrieval failed:`, error);
      return [];
    }
  }

  /**
   * Extract and learn code patterns
   */
  private async extractCodePatterns(request: CodeAnalysisRequest): Promise<CodePattern[]> {
    const patterns: CodePattern[] = [];
    const language = request.language.toLowerCase();

    // Get existing patterns for this language
    const existingPatterns = this.languagePatterns.get(language) || [];

    // Detect common patterns in the code
    const detectedPatterns = this.detectCodePatterns(request.code, language);

    for (const detected of detectedPatterns) {
      const existing = existingPatterns.find(p => p.name === detected.name);
      if (existing) {
        existing.frequency++;
        existing.lastSeen = new Date();
        existing.examples.push(detected.example);
      } else {
        patterns.push({
          name: detected.name,
          description: detected.description,
          frequency: 1,
          quality: detected.quality,
          lastSeen: new Date(),
          examples: [detected.example],
          relatedDocumentation: []
        });
      }
    }

    // Update language patterns
    this.languagePatterns.set(language, [...existingPatterns, ...patterns]);

    return patterns;
  }

  /**
   * Generate suggestions with Constitutional AI validation
   */
  private async generateSuggestions(
    request: CodeAnalysisRequest,
    analysis: string,
    documentation: DocumentationResult[],
    memoryInsights: MemoryInsight[]
  ): Promise<CodeSuggestion[]> {
    const suggestions: CodeSuggestion[] = [];

    // Generate suggestions based on analysis type
    switch (request.requestType) {
      case 'review':
        suggestions.push(...await this.generateReviewSuggestions(request, analysis, documentation));
        break;
      case 'debug':
        suggestions.push(...await this.generateDebugSuggestions(request, analysis, memoryInsights));
        break;
      case 'optimize':
        suggestions.push(...await this.generateOptimizationSuggestions(request, analysis, documentation));
        break;
      case 'refactor':
        suggestions.push(...await this.generateRefactoringSuggestions(request, analysis, documentation));
        break;
      case 'test':
        suggestions.push(...await this.generateTestSuggestions(request, analysis, documentation));
        break;
      case 'explain':
        suggestions.push(...await this.generateExplanationSuggestions(request, analysis, documentation));
        break;
    }

    // Validate each suggestion for constitutional compliance
    return suggestions.filter(suggestion => this.validateSuggestion(suggestion));
  }

  /**
   * Language-specific analysis methods
   */
  private async analyzeJavaScriptTypeScript(request: CodeAnalysisRequest): Promise<string> {
    const issues = [];
    const code = request.code;

    // TypeScript specific checks
    if (request.language.toLowerCase() === 'typescript') {
      if (!code.includes('interface') && !code.includes('type') && code.length > 100) {
        issues.push('Consider adding TypeScript interfaces for better type safety');
      }
      if (code.includes('any')) {
        issues.push('Avoid using "any" type - use specific types for better type safety');
      }
    }

    // General JavaScript/TypeScript checks
    if (code.includes('var ')) {
      issues.push('Use "let" or "const" instead of "var" for better scoping');
    }
    if (code.includes('== ') || code.includes('!= ')) {
      issues.push('Use strict equality (=== and !==) instead of loose equality');
    }

    return `JavaScript/TypeScript Analysis:\n${issues.join('\n')}`;
  }

  private async analyzePython(request: CodeAnalysisRequest): Promise<string> {
    const issues = [];
    const code = request.code;

    // Python specific checks
    if (!code.includes('def ') && code.length > 50) {
      issues.push('Consider breaking code into functions for better organization');
    }
    if (code.includes('except:') && !code.includes('except Exception:')) {
      issues.push('Use specific exception types instead of bare except clauses');
    }

    return `Python Analysis:\n${issues.join('\n')}`;
  }

  private async analyzeJava(request: CodeAnalysisRequest): Promise<string> {
    const issues = [];
    const code = request.code;

    // Java specific checks
    if (code.includes('System.out.println') && request.requestType === 'review') {
      issues.push('Consider using a logging framework instead of System.out.println');
    }

    return `Java Analysis:\n${issues.join('\n')}`;
  }

  private async analyzeGenericCode(request: CodeAnalysisRequest): Promise<string> {
    return `Generic code analysis for ${request.language} - basic structural review completed`;
  }

  private async analyzeCodeStructure(request: CodeAnalysisRequest): Promise<string> {
    const lines = request.code.split('\n');
    const analysis = [];

    if (lines.length > 100) {
      analysis.push('Function is quite long - consider breaking into smaller functions');
    }

    const commentLines = lines.filter(line => line.trim().startsWith('//') || line.trim().startsWith('#')).length;
    const codeLines = lines.filter(line => line.trim().length > 0).length;
    
    if (commentLines / codeLines < 0.1) {
      analysis.push('Consider adding more comments to explain complex logic');
    }

    return analysis.length > 0 ? `Code Structure:\n${analysis.join('\n')}` : '';
  }

  private async analyzeSecurityPatterns(request: CodeAnalysisRequest): Promise<string> {
    const issues = [];
    const code = request.code.toLowerCase();

    if (code.includes('password') && code.includes('=')) {
      issues.push('Potential hardcoded password detected - use environment variables');
    }
    if (code.includes('eval(')) {
      issues.push('Using eval() can be dangerous - consider alternatives');
    }

    return issues.length > 0 ? `Security Issues:\n${issues.join('\n')}` : '';
  }

  private async analyzePerformancePatterns(request: CodeAnalysisRequest): Promise<string> {
    const issues = [];
    const code = request.code;

    if (code.includes('for') && code.includes('for')) {
      issues.push('Nested loops detected - consider optimization if performance is critical');
    }

    return issues.length > 0 ? `Performance Considerations:\n${issues.join('\n')}` : '';
  }

  /**
   * Helper methods
   */
  private getDocumentationSource(language: string): string {
    const mapping: Record<string, string> = {
      typescript: 'typescript',
      javascript: 'nodejs',
      python: 'python',
      java: 'java',
      react: 'react',
      node: 'nodejs'
    };
    return mapping[language.toLowerCase()] || 'generic';
  }

  private detectFrameworks(code: string): string[] {
    const frameworks = [];
    if (code.includes('import React') || code.includes('from "react"')) {
      frameworks.push('react');
    }
    if (code.includes('express') || code.includes('app.get')) {
      frameworks.push('express');
    }
    if (code.includes('@nestjs') || code.includes('@Controller')) {
      frameworks.push('nestjs');
    }
    return frameworks;
  }

  private detectCodePatterns(code: string, _language: string): Array<{name: string, description: string, quality: number, example: string}> {
    const patterns = [];
    
    // Common patterns
    if (code.includes('async') && code.includes('await')) {
      patterns.push({
        name: 'async-await',
        description: 'Asynchronous programming pattern',
        quality: 0.8,
        example: code.substring(0, 100)
      });
    }

    if (code.includes('try') && code.includes('catch')) {
      patterns.push({
        name: 'error-handling',
        description: 'Error handling pattern',
        quality: 0.9,
        example: code.substring(0, 100)
      });
    }

    return patterns;
  }

  private classifyInsightType(content: string): MemoryInsight['type'] {
    if (content.includes('best practice') || content.includes('recommended')) {
      return 'best-practice';
    }
    if (content.includes('avoid') || content.includes('anti-pattern')) {
      return 'anti-pattern';
    }
    if (content.includes('solution') || content.includes('fix')) {
      return 'solution';
    }
    return 'pattern';
  }

  private calculateConfidence(suggestions: CodeSuggestion[], documentation: DocumentationResult[], memoryInsights: MemoryInsight[]): number {
    let confidence = 0.5; // Base confidence
    
    confidence += Math.min(suggestions.length * 0.1, 0.3); // More suggestions = higher confidence
    confidence += Math.min(documentation.length * 0.05, 0.2); // Documentation = higher confidence
    confidence += Math.min(memoryInsights.length * 0.03, 0.1); // Memory insights = higher confidence
    
    return Math.min(confidence, 1.0);
  }

  private async calculateQualityScore(_request: CodeAnalysisRequest, suggestions: CodeSuggestion[]): Promise<number> {
    let score = 85; // Base score
    
    // Deduct points for issues
    for (const suggestion of suggestions) {
      switch (suggestion.severity) {
        case 'critical': score -= 15; break;
        case 'high': score -= 10; break;
        case 'medium': score -= 5; break;
        case 'low': score -= 2; break;
      }
    }
    
    return Math.max(score, 0);
  }

  private async validateConstitutionalCompliance(_analysis: string, suggestions: CodeSuggestion[]): Promise<boolean> {
    // Check if analysis and suggestions follow Constitutional AI principles
    // - Accuracy: Are suggestions technically sound?
    // - Transparency: Are explanations clear?
    // - Helpfulness: Do suggestions provide actionable guidance?
    // - Safety: Do suggestions avoid harmful recommendations?
    
    return !suggestions.some(s => s.type === 'security' && s.severity === 'critical');
  }

  private validateSuggestion(suggestion: CodeSuggestion): boolean {
    // Validate suggestion meets Constitutional AI standards
    return suggestion.reasoning.length > 10 && 
           suggestion.description.length > 10 &&
           !suggestion.description.includes('just') && // Avoid vague language
           !suggestion.description.includes('simply'); // Avoid oversimplification
  }

  private async generateReviewSuggestions(_request: CodeAnalysisRequest, _analysis: string, _documentation: DocumentationResult[]): Promise<CodeSuggestion[]> {
    // Generate code review suggestions
    return [];
  }

  private async generateDebugSuggestions(_request: CodeAnalysisRequest, _analysis: string, _memoryInsights: MemoryInsight[]): Promise<CodeSuggestion[]> {
    // Generate debugging suggestions
    return [];
  }

  private async generateOptimizationSuggestions(_request: CodeAnalysisRequest, _analysis: string, _documentation: DocumentationResult[]): Promise<CodeSuggestion[]> {
    // Generate optimization suggestions
    return [];
  }

  private async generateRefactoringSuggestions(_request: CodeAnalysisRequest, _analysis: string, _documentation: DocumentationResult[]): Promise<CodeSuggestion[]> {
    // Generate refactoring suggestions
    return [];
  }

  private async generateTestSuggestions(_request: CodeAnalysisRequest, _analysis: string, _documentation: DocumentationResult[]): Promise<CodeSuggestion[]> {
    // Generate testing suggestions
    return [];
  }

  private async generateExplanationSuggestions(_request: CodeAnalysisRequest, _analysis: string, _documentation: DocumentationResult[]): Promise<CodeSuggestion[]> {
    // Generate explanation suggestions
    return [];
  }

  private generateFallbackAnalysis(request: CodeAnalysisRequest): CodeAnalysisResult {
    return {
      analysis: `Fallback analysis for ${request.language} ${request.requestType}`,
      suggestions: [],
      patterns: [],
      documentation: [],
      qualityScore: 0,
      constitutionalCompliance: true,
      memoryInsights: [],
      metadata: {
        analysisType: request.requestType,
        confidence: 0.3,
        processingTime: 0,
        context7Used: false,
        memoryEnhanced: false
      }
    };
  }

  private updateMetrics(result: CodeAnalysisResult): void {
    this.analysisMetrics.averageQualityScore = 
      (this.analysisMetrics.averageQualityScore * (this.analysisMetrics.successfulAnalyses - 1) + result.qualityScore) / 
      this.analysisMetrics.successfulAnalyses;
    
    if (result.metadata.context7Used) {
      this.analysisMetrics.context7Usage++;
    }
    
    if (result.metadata.memoryEnhanced) {
      this.analysisMetrics.memoryEnhancements++;
    }
  }
  private async storeAnalysisForLearning(request: CodeAnalysisRequest, result: CodeAnalysisResult): Promise<void> {
    try {
      const learningContent = `Code analysis: ${request.language} ${request.requestType} - Quality: ${result.qualityScore}% - Patterns: ${result.patterns.length} - Documentation used: ${result.documentation.length}`;
      
      const message = {
        id: `analysis-${Date.now()}`,
        fromAgent: this.agentId,
        messageType: 'learning' as const,
        content: learningContent,
        priority: 'medium' as const,
        timestamp: new Date(),
        metadata: {
          context: {
            type: 'code_analysis',
            language: request.language,
            requestType: request.requestType,
            qualityScore: result.qualityScore,
            patterns: result.patterns.map(p => p.name),
            context7Used: result.metadata.context7Used
          }
        }
      };
      
      await this.memoryComm.sendMessage(message);
    } catch (error) {
      console.warn(`[CodeAnalysis] Failed to store analysis for learning:`, error);
    }
  }

  private async initializeLanguagePatterns(): Promise<void> {
    // Initialize with common patterns for different languages
    this.languagePatterns.set('typescript', []);
    this.languagePatterns.set('javascript', []);
    this.languagePatterns.set('python', []);
    this.languagePatterns.set('java', []);
  }

  /**
   * Get analysis metrics
   */
  getMetrics() {
    return {
      ...this.analysisMetrics,
      languagePatterns: Object.fromEntries(
        Array.from(this.languagePatterns.entries()).map(([lang, patterns]) => [
          lang,
          patterns.length
        ])
      )
    };
  }
}

/**
 * DevAgent Adaptive Learning Engine
 * 
 * Core learning system that enables DevAgent to:
 * - Store and retrieve learned patterns from interactions
 * - Build a local knowledge base of successful solutions
 * - Learn from context7 documentation and store useful insights
 * - Continuously improve through pattern recognition
 * - Maintain institutional memory across sessions
 * 
 * This creates a truly adaptive agent that gets better over time.
 * 
 * @version 1.0.0
 * @created June 14, 2025
 */

import { MemoryDrivenAgentCommunication } from './communication/MemoryDrivenAgentCommunication';
import { UnifiedContext7MCPIntegration, DocumentationResult } from '../mcp/UnifiedContext7MCPIntegration';
import { CodeAnalysisResult } from './AdvancedCodeAnalysisEngine';
import { timeAwareness, getEnhancedTimeContext } from '../utils/EnhancedTimeAwareness.js';
import { OneAgentUnifiedBackbone } from '../utils/UnifiedBackboneService.js';
import { OneAgentMemory } from '../memory/OneAgentMemory';

export interface LearnedPattern {
  id: string;
  name: string;
  description: string;
  category: 'solution' | 'anti-pattern' | 'best-practice' | 'framework' | 'library' | 'debugging';
  language: string;
  framework?: string;
  
  // Pattern details
  problem: string;
  solution: string;
  reasoning: string;
  codeExample?: string;
  
  // Learning metadata
  confidence: number; // 0-1
  successRate: number; // How often this pattern worked
  timesUsed: number;
  timesSuccessful: number;
  
  // Context
  contexts: string[]; // Where this pattern applies
  dependencies: string[]; // Required libraries/frameworks
  complexity: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  
  // Quality and validation
  qualityScore: number;
  constitutionallyValid: boolean;
  lastValidated: Date;
  lastUsed: Date;
  
  // Sources
  learnedFrom: 'context7' | 'user-interaction' | 'analysis-engine' | 'cross-agent';
  sourceDetails?: {
    documentationUrl?: string;
    sessionId?: string;
    agentId?: string;
    userFeedback?: 'positive' | 'negative' | 'neutral';
  };
  
  // Relationships
  relatedPatterns: string[]; // IDs of related patterns
  supersedes?: string[]; // Patterns this one replaces
  supersededBy?: string; // If this pattern is outdated
}

export interface LearningContext {
  sessionId: string;
  userId: string;
  language: string;
  framework?: string;
  problemType: string;
  successfulOutcome: boolean;
  userSatisfaction?: 'high' | 'medium' | 'low';
  analysisResult?: CodeAnalysisResult;
  documentationUsed?: DocumentationResult[];
}

export interface LearningMetrics {
  totalPatterns: number;
  patternsByCategory: Record<string, number>;
  patternsByLanguage: Record<string, number>;
  averageConfidence: number;
  averageSuccessRate: number;
  recentLearnings: number; // Last 7 days
  qualityTrend: number; // Improvement over time
  mostUsedPatterns: LearnedPattern[];
  emergingPatterns: LearnedPattern[]; // Recently learned, high potential
}

/**
 * Adaptive Learning Engine for DevAgent
 */
export class DevAgentLearningEngine {
  private memoryComm: MemoryDrivenAgentCommunication;
  private context7Integration: UnifiedContext7MCPIntegration;
  private agentId: string;
  private unifiedBackbone: OneAgentUnifiedBackbone;
  private memoryBridge = new OneAgentMemory({});
  
  // In-memory cache for fast access
  private patternCache: Map<string, LearnedPattern> = new Map();
  private categoryIndex: Map<string, string[]> = new Map(); // category -> pattern IDs
  private languageIndex: Map<string, string[]> = new Map(); // language -> pattern IDs
  
  private metrics: LearningMetrics = {
    totalPatterns: 0,
    patternsByCategory: {},
    patternsByLanguage: {},
    averageConfidence: 0,
    averageSuccessRate: 0,
    recentLearnings: 0,
    qualityTrend: 0,
    mostUsedPatterns: [],
    emergingPatterns: []
  };
  constructor(agentId: string, memoryComm: MemoryDrivenAgentCommunication) {
    this.agentId = agentId;
    this.memoryComm = memoryComm;
    this.context7Integration = new UnifiedContext7MCPIntegration(agentId);
    this.unifiedBackbone = OneAgentUnifiedBackbone.getInstance();
  }

  /**
   * Initialize the learning engine and load existing patterns
   */
  async initialize(): Promise<void> {
    console.log('[LearningEngine] Initializing adaptive learning system...');
    
    try {
      // Load existing patterns from memory
      await this.loadExistingPatterns();
      
      // Update metrics
      await this.updateMetrics();
      
      console.log(`[LearningEngine] Loaded ${this.patternCache.size} existing patterns`);
      console.log(`[LearningEngine] Average confidence: ${this.metrics.averageConfidence.toFixed(2)}`);
      console.log(`[LearningEngine] Average success rate: ${this.metrics.averageSuccessRate.toFixed(2)}`);
      
    } catch (error) {
      console.warn('[LearningEngine] Failed to load existing patterns:', error);
    }
  }

  /**
   * Learn from a successful interaction
   */
  async learnFromInteraction(
    problem: string,
    solution: string,
    language: string,
    context: LearningContext
  ): Promise<LearnedPattern | null> {
    try {
      console.log('[LearningEngine] Learning from interaction...');
      
      // Extract pattern from interaction
      const pattern = await this.extractPattern(problem, solution, language, context);
      
      if (!pattern) {
        return null;
      }
      
      // Check if we already have a similar pattern
      const existingPattern = await this.findSimilarPattern(pattern);
      
      if (existingPattern) {
        // Update existing pattern with new learning
        return await this.updateExistingPattern(existingPattern.id, context);
      } else {
        // Store new pattern
        return await this.storeNewPattern(pattern);
      }
      
    } catch (error) {
      console.error('[LearningEngine] Failed to learn from interaction:', error);
      return null;
    }
  }

  /**
   * Learn from context7 documentation
   */
  async learnFromDocumentation(
    documentationResults: DocumentationResult[],
    language: string,
    problemContext: string
  ): Promise<LearnedPattern[]> {
    const learnedPatterns: LearnedPattern[] = [];
    
    try {
      console.log('[LearningEngine] Learning from context7 documentation...');
      
      for (const doc of documentationResults) {
        if (doc.relevanceScore > 0.7) { // Only learn from highly relevant docs
          const patterns = await this.extractPatternsFromDocumentation(doc, language, problemContext);
          
          for (const pattern of patterns) {
            const stored = await this.storeNewPattern(pattern);
            if (stored) {
              learnedPatterns.push(stored);
            }
          }
        }
      }
      
      console.log(`[LearningEngine] Learned ${learnedPatterns.length} patterns from documentation`);
      return learnedPatterns;
      
    } catch (error) {
      console.error('[LearningEngine] Failed to learn from documentation:', error);
      return [];
    }
  }

  /**
   * Find relevant patterns for a given problem
   */
  async findRelevantPatterns(
    problem: string,
    language: string,
    category?: string,
    maxResults: number = 5
  ): Promise<LearnedPattern[]> {
    try {
      // First check cache
      const cached = this.searchPatternCache(problem, language, category);
      
      // Also search in persistent memory
      const memoryResults = await this.searchPatternsInMemory(problem, language, category, maxResults);
      
      // Combine and deduplicate
      const combined = [...cached, ...memoryResults];
      const unique = this.deduplicatePatterns(combined);
      
      // Sort by relevance and confidence
      return unique
        .sort((a, b) => {
          const scoreA = a.confidence * a.successRate * this.calculateRelevance(a, problem);
          const scoreB = b.confidence * b.successRate * this.calculateRelevance(b, problem);
          return scoreB - scoreA;
        })
        .slice(0, maxResults);
        
    } catch (error) {
      console.error('[LearningEngine] Failed to find relevant patterns:', error);
      return [];
    }
  }
  /**
   * Record pattern usage and outcome
   */
  async recordPatternUsage(
    patternId: string,
    wasSuccessful: boolean,
    _userFeedback?: 'positive' | 'negative' | 'neutral'
  ): Promise<void> {
    try {
      const pattern = this.patternCache.get(patternId);
      if (!pattern) {
        console.warn(`[LearningEngine] Pattern ${patternId} not found for usage recording`);
        return;
      }
      
      // Update pattern metrics
      pattern.timesUsed++;      if (wasSuccessful) {
        pattern.timesSuccessful++;
      }
      pattern.successRate = pattern.timesSuccessful / pattern.timesUsed;
      const usageTimestamp = this.unifiedBackbone.getServices().timeService.now();
      pattern.lastUsed = new Date(usageTimestamp.utc);
      
      // Adjust confidence based on success
      if (wasSuccessful) {
        pattern.confidence = Math.min(1.0, pattern.confidence + 0.05);
      } else {
        pattern.confidence = Math.max(0.1, pattern.confidence - 0.1);
      }
      
      // Store updated pattern
      await this.updatePatternInMemory(pattern);
      
      // Update cache
      this.patternCache.set(patternId, pattern);
      
      console.log(`[LearningEngine] Updated pattern ${pattern.name}: confidence=${pattern.confidence.toFixed(2)}, success rate=${pattern.successRate.toFixed(2)}`);
      
    } catch (error) {
      console.error('[LearningEngine] Failed to record pattern usage:', error);
    }
  }

  /**
   * Get learning metrics and insights
   */
  async getLearningMetrics(): Promise<LearningMetrics> {
    await this.updateMetrics();
    return { ...this.metrics };
  }

  /**
   * Validate and clean up patterns (remove outdated, low-performing ones)
   */
  async cleanupPatterns(): Promise<{ removed: number; updated: number }> {
    let removed = 0;
    let updated = 0;
    
    try {
      console.log('[LearningEngine] Starting pattern cleanup...');
      
      for (const [patternId, pattern] of this.patternCache) {
        // Remove patterns with very low success rate and high usage
        if (pattern.timesUsed > 10 && pattern.successRate < 0.3) {
          await this.removePattern(patternId);
          removed++;
          continue;
        }
          // Mark old patterns for review
        const currentTime = this.unifiedBackbone.getServices().timeService.now();
        const daysSinceLastUsed = (currentTime.unix - pattern.lastUsed.getTime()) / (1000 * 60 * 60 * 24);
        if (daysSinceLastUsed > 90 && pattern.timesUsed < 3) {
          pattern.confidence = Math.max(0.1, pattern.confidence - 0.2);
          await this.updatePatternInMemory(pattern);
          updated++;
        }
      }
      
      console.log(`[LearningEngine] Cleanup complete: removed ${removed}, updated ${updated}`);
      return { removed, updated };
      
    } catch (error) {
      console.error('[LearningEngine] Pattern cleanup failed:', error);
      return { removed: 0, updated: 0 };
    }
  }

  // PRIVATE IMPLEMENTATION METHODS
  /**
   * Load existing patterns from persistent memory (canonical format only)
   */
  private async loadExistingPatterns(): Promise<void> {
    try {
      // Query for all DevAgent learning patterns globally, not user-specific
      const memoryResult = await this.memoryBridge.searchMemory({
        query: 'learned pattern solution best practice devagent',
        user_id: 'global',
        limit: 100,
        semanticSearch: true,
        type: 'learned-patterns'
      });
      const loadedPatterns: string[] = [];
      if (Array.isArray(memoryResult)) {
        for (const memory of memoryResult) {
          // Canonical: expect metadata.type === 'learned_pattern' and content is valid JSON
          if (memory.metadata?.type === 'learned_pattern') {
            try {
              const pattern: LearnedPattern = JSON.parse(memory.content);
              this.patternCache.set(pattern.id, pattern);
              this.updateIndexes(pattern);
              loadedPatterns.push(pattern.name);
            } catch (parseError) {
              console.warn('[LearningEngine] Failed to parse stored pattern (canonical):', parseError);
            }
          }
        }
      }
      console.log(`[LearningEngine] Loaded ${this.patternCache.size} existing patterns from global repository: ${loadedPatterns.join(', ')}`);
    } catch (error) {
      console.warn('[LearningEngine] Failed to load existing patterns:', error);
    }
  }

  /**
   * Extract a pattern from a successful interaction
   */
  private async extractPattern(
    problem: string,
    solution: string,
    language: string,
    context: LearningContext
  ): Promise<LearnedPattern | null> {    // Use AI to extract meaningful patterns
    const unifiedMetadata = this.unifiedBackbone.getServices().metadataService.create(
      'learning-pattern',
      'DevAgentLearningEngine',
      { 
        content: { 
          category: 'dev-pattern',
          tags: [language, context.framework].filter(Boolean) as string[],
          sensitivity: 'internal',
          relevanceScore: 0.8,
          contextDependency: 'session'
        }
      }
    );
    const patternId = unifiedMetadata.id;
    
    // Determine category based on problem type
    const category = this.determinePatternCategory(problem, solution);
    
    // Determine complexity
    const complexity = this.determineComplexity(solution, context);
      const pattern: LearnedPattern = {
      id: patternId,
      name: this.generatePatternName(problem, language),
      description: `Solution for ${problem} in ${language}`,
      category,
      language,
      ...(context.framework && { framework: context.framework }),
      
      problem: problem,
      solution: solution,
      reasoning: `Learned from successful interaction in session ${context.sessionId}`,
      
      confidence: context.successfulOutcome ? 0.8 : 0.5,
      successRate: 1.0, // First time
      timesUsed: 1,
      timesSuccessful: context.successfulOutcome ? 1 : 0,
      
      contexts: [context.problemType],
      dependencies: this.extractDependencies(solution),
      complexity,
        qualityScore: context.analysisResult?.qualityScore || 80,
      constitutionallyValid: context.analysisResult?.constitutionalCompliance || true,
      lastValidated: new Date(this.unifiedBackbone.getServices().timeService.now().utc),
      lastUsed: new Date(this.unifiedBackbone.getServices().timeService.now().utc),
      
      learnedFrom: 'user-interaction',
      sourceDetails: {
        sessionId: context.sessionId,
        userFeedback: context.userSatisfaction === 'high' ? 'positive' : 
                     context.userSatisfaction === 'low' ? 'negative' : 'neutral'
      },      
      relatedPatterns: [],
      supersedes: []
    };
    
    return pattern;
  }

  /**
   * Extract patterns from context7 documentation
   */
  private async extractPatternsFromDocumentation(
    doc: DocumentationResult,
    language: string,
    problemContext: string
  ): Promise<LearnedPattern[]> {
    const patterns: LearnedPattern[] = [];
    
    // Look for code examples and best practices in documentation
    const codeBlocks = this.extractCodeBlocks(doc.content);
    const bestPractices = this.extractBestPractices(doc.content);
      for (const codeBlock of codeBlocks) {
      const patternMetadata = this.unifiedBackbone.getServices().metadataService.create(
        'context7-pattern',
        'DevAgentLearningEngine',
        { 
          content: { 
            category: 'context7-learning',
            tags: [language, 'context7', 'documentation'],
            sensitivity: 'internal',
            relevanceScore: 0.85,
            contextDependency: 'session'
          }
        }
      );
      const patternId = patternMetadata.id;
      
      const pattern: LearnedPattern = {
        id: patternId,
        name: `${doc.title} - ${language} Pattern`,
        description: `Best practice from ${doc.source} documentation`,
        category: 'best-practice',
        language,
        
        problem: problemContext,
        solution: codeBlock,
        reasoning: `Documented best practice from ${doc.source}`,
        codeExample: codeBlock,
        
        confidence: 0.9, // High confidence for official docs
        successRate: 0.95, // Assume documentation is reliable
        timesUsed: 0,
        timesSuccessful: 0,
        
        contexts: [problemContext],
        dependencies: this.extractDependencies(codeBlock),
        complexity: 'intermediate',
          qualityScore: 90,
        constitutionallyValid: true,
        lastValidated: new Date(getEnhancedTimeContext().realTime.utc),
        lastUsed: new Date(0), // Never used yet
          learnedFrom: 'context7',
        ...(doc.url && { sourceDetails: { documentationUrl: doc.url } }),
        
        relatedPatterns: [],
        supersedes: []
      };
      
      patterns.push(pattern);
    }
    
    return patterns;
  }
  /**
   * Store a new pattern in persistent memory
   */
  private async storeNewPattern(pattern: LearnedPattern): Promise<LearnedPattern> {
    try {
      await this.memoryBridge.addMemory({
        id: `learned_pattern_${pattern.id}_${Date.now()}`,
        agentId: this.agentId,
        learningType: 'pattern',
        content: JSON.stringify(pattern),
        confidence: pattern.confidence,
        applicationCount: pattern.timesUsed,
        lastApplied: new Date(),
        sourceConversations: [],
        metadata: {
          type: 'learned_pattern',
          language: pattern.language,
          framework: pattern.framework,
          category: pattern.category
        },
        type: 'learned-patterns'
      });
      return pattern;
    } catch (error) {
      console.error('[LearningEngine] Failed to store new pattern:', error);
      throw error;
    }
  }

  /**
   * Update indexes for fast pattern lookup
   */
  private updateIndexes(pattern: LearnedPattern): void {
    // Category index
    if (!this.categoryIndex.has(pattern.category)) {
      this.categoryIndex.set(pattern.category, []);
    }
    this.categoryIndex.get(pattern.category)!.push(pattern.id);
    
    // Language index
    if (!this.languageIndex.has(pattern.language)) {
      this.languageIndex.set(pattern.language, []);
    }
    this.languageIndex.get(pattern.language)!.push(pattern.id);
  }

  /**
   * Calculate relevance score for a pattern given a problem
   */
  private calculateRelevance(pattern: LearnedPattern, problem: string): number {
    const problemWords = problem.toLowerCase().split(' ');
    const patternWords = [
      ...pattern.problem.toLowerCase().split(' '),
      ...pattern.description.toLowerCase().split(' '),
      ...pattern.contexts.join(' ').toLowerCase().split(' ')
    ];
    
    let matches = 0;
    for (const word of problemWords) {
      if (patternWords.some(pw => pw.includes(word) || word.includes(pw))) {
        matches++;
      }
    }
    
    return matches / problemWords.length;
  }

  /**
   * Helper methods for pattern analysis
   */
  private determinePatternCategory(problem: string, solution: string): LearnedPattern['category'] {
    const lowerProblem = problem.toLowerCase();
    const lowerSolution = solution.toLowerCase();
    
    if (lowerProblem.includes('debug') || lowerProblem.includes('error') || lowerProblem.includes('fix')) {
      return 'debugging';
    }
    if (lowerSolution.includes('avoid') || lowerSolution.includes('don\'t') || lowerSolution.includes('never')) {
      return 'anti-pattern';
    }
    if (lowerSolution.includes('best practice') || lowerSolution.includes('recommended')) {
      return 'best-practice';
    }
    if (lowerProblem.includes('framework') || lowerSolution.includes('framework')) {
      return 'framework';
    }
    if (lowerProblem.includes('library') || lowerSolution.includes('import')) {
      return 'library';
    }
    
    return 'solution';
  }

  private determineComplexity(solution: string, _context: LearningContext): LearnedPattern['complexity'] {
    const solutionLength = solution.length;
    const codeLines = solution.split('\n').length;
    
    if (solutionLength < 200 && codeLines < 10) return 'beginner';
    if (solutionLength < 500 && codeLines < 25) return 'intermediate';
    if (solutionLength < 1000 && codeLines < 50) return 'advanced';
    return 'expert';
  }

  private generatePatternName(problem: string, language: string): string {
    const words = problem.split(' ').slice(0, 4);
    return `${language} ${words.join(' ')} Pattern`;
  }

  private extractDependencies(solution: string): string[] {
    const dependencies = [];
    const importMatches = solution.match(/import.*from ['"]([^'"]+)['"]/g);
    if (importMatches) {
      for (const match of importMatches) {
        const dep = match.match(/from ['"]([^'"]+)['"]/)?.[1];
        if (dep) dependencies.push(dep);
      }
    }
    return dependencies;
  }

  private extractCodeBlocks(content: string): string[] {
    const codeBlocks = [];
    const matches = content.match(/```[\s\S]*?```/g);
    if (matches) {
      for (const match of matches) {
        codeBlocks.push(match.replace(/```\w*\n?/, '').replace(/```$/, ''));
      }
    }
    return codeBlocks;
  }

  private extractBestPractices(content: string): string[] {
    // Extract sentences that contain best practice indicators
    const sentences = content.split(/[.!?]+/);
    return sentences.filter(sentence =>
      sentence.toLowerCase().includes('best practice') ||
      sentence.toLowerCase().includes('recommended') ||
      sentence.toLowerCase().includes('should')
    );
  }

  private searchPatternCache(problem: string, language: string, category?: string): LearnedPattern[] {
    const results = [];
    
    for (const pattern of this.patternCache.values()) {
      if (pattern.language === language) {
        if (category && pattern.category !== category) continue;
        
        const relevance = this.calculateRelevance(pattern, problem);
        if (relevance > 0.3) {
          results.push(pattern);
        }
      }
    }
    
    return results;
  }
  private async searchPatternsInMemory(
    problem: string,
    language: string,
    category?: string,
    maxResults: number = 5
  ): Promise<LearnedPattern[]> {
    try {
      const searchQuery = `${language} ${category || ''} ${problem}`.trim();
      const memoryResult = await this.memoryBridge.searchMemory({
        query: searchQuery,
        user_id: this.agentId,
        limit: maxResults,
        semanticSearch: true,
        type: 'learned-patterns'
      });
      const patterns: LearnedPattern[] = [];
      for (const memory of (memoryResult || []).slice(0, maxResults)) {
        // Canonical: expect metadata.type === 'learned_pattern' and content is valid JSON
        if (memory.metadata?.type === 'learned_pattern') {
          try {
            const pattern: LearnedPattern = JSON.parse(memory.content);
            patterns.push(pattern);
          } catch (error) {
            console.warn('[LearningEngine] Failed to parse stored pattern (canonical):', error);
          }
        }
      }
      return patterns;
    } catch (error) {
      console.error('[LearningEngine] Failed to search patterns in memory:', error);
      return [];
    }
  }

  private deduplicatePatterns(patterns: LearnedPattern[]): LearnedPattern[] {
    const seen = new Set<string>();
    return patterns.filter(pattern => {
      if (seen.has(pattern.id)) return false;
      seen.add(pattern.id);
      return true;
    });
  }

  private async findSimilarPattern(pattern: LearnedPattern): Promise<LearnedPattern | null> {
    // Simple similarity check - can be enhanced with semantic analysis
    for (const existing of this.patternCache.values()) {
      if (existing.language === pattern.language &&
          existing.category === pattern.category &&
          this.calculateSimilarity(existing.problem, pattern.problem) > 0.8) {
        return existing;
      }
    }
    return null;
  }

  private calculateSimilarity(text1: string, text2: string): number {
    const words1 = new Set(text1.toLowerCase().split(' '));
    const words2 = new Set(text2.toLowerCase().split(' '));
    
    const intersection = new Set([...words1].filter(x => words2.has(x)));
    const union = new Set([...words1, ...words2]);
    
    return intersection.size / union.size;
  }

  private async updateExistingPattern(patternId: string, context: LearningContext): Promise<LearnedPattern> {
    const pattern = this.patternCache.get(patternId)!;
    
    // Update metrics
    pattern.timesUsed++;    if (context.successfulOutcome) {
      pattern.timesSuccessful++;
    }
    pattern.successRate = pattern.timesSuccessful / pattern.timesUsed;
    const contextUsageTimestamp = this.unifiedBackbone.getServices().timeService.now();
    pattern.lastUsed = new Date(contextUsageTimestamp.utc);
    
    // Update confidence
    if (context.successfulOutcome) {
      pattern.confidence = Math.min(1.0, pattern.confidence + 0.02);
    }
    
    await this.updatePatternInMemory(pattern);
    return pattern;
  }  private async updatePatternInMemory(pattern: LearnedPattern): Promise<void> {
    await this.memoryBridge.addMemory({
      id: `learned_pattern_${pattern.id}_${Date.now()}`,
      agentId: this.agentId,
      learningType: 'pattern',
      content: JSON.stringify(pattern),
      confidence: pattern.confidence,
      applicationCount: pattern.timesUsed,
      lastApplied: new Date(),
      sourceConversations: [],
      metadata: {
        type: 'learned_pattern',
        language: pattern.language,
        framework: pattern.framework,
        category: pattern.category
      },
      type: 'learned-patterns'
    });
  }

  private async removePattern(patternId: string): Promise<void> {
    this.patternCache.delete(patternId);
    // Note: We don't actually delete from memory to preserve history
    console.log(`[LearningEngine] Removed pattern ${patternId} from active cache`);
  }

  private async updateMetrics(): Promise<void> {
    const patterns = Array.from(this.patternCache.values());
    
    this.metrics.totalPatterns = patterns.length;
    this.metrics.averageConfidence = patterns.reduce((sum, p) => sum + p.confidence, 0) / patterns.length || 0;
    this.metrics.averageSuccessRate = patterns.reduce((sum, p) => sum + p.successRate, 0) / patterns.length || 0;
    
    // Update categories and languages
    this.metrics.patternsByCategory = {};
    this.metrics.patternsByLanguage = {};
    
    for (const pattern of patterns) {
      this.metrics.patternsByCategory[pattern.category] = (this.metrics.patternsByCategory[pattern.category] || 0) + 1;
      this.metrics.patternsByLanguage[pattern.language] = (this.metrics.patternsByLanguage[pattern.language] || 0) + 1;
    }
    
    // Most used patterns
    this.metrics.mostUsedPatterns = patterns
      .sort((a, b) => b.timesUsed - a.timesUsed)
      .slice(0, 5);      // Emerging patterns (recent, low usage, high confidence)
    const currentTime = this.unifiedBackbone.getServices().timeService.now();
    const recentCutoff = currentTime.unix - (7 * 24 * 60 * 60 * 1000); // 7 days
    this.metrics.emergingPatterns = patterns
      .filter(p => {
        const lastValidatedTime = p.lastValidated instanceof Date 
          ? p.lastValidated.getTime() 
          : new Date(p.lastValidated).getTime();
        return lastValidatedTime > recentCutoff && p.timesUsed < 5 && p.confidence > 0.7;
      })
      .sort((a, b) => b.confidence - a.confidence)
      .slice(0, 5);
    
    this.metrics.recentLearnings = this.metrics.emergingPatterns.length;
  }
}

export default DevAgentLearningEngine;

