   * Check if bootstrap service is active
   */
  isActive(): boolean {
    return this.isBootstrapped;
  }

  /**
   * Add a new REAL agent dynamically
   */
  async addAgent(agentId: string, agent: BaseAgent): Promise<void> {
    if (this.agents.has(agentId)) {
      console.log(`‚ö†Ô∏è Replacing existing agent: ${agentId}`);
      await this.agents.get(agentId)?.cleanup();
    }

    this.agents.set(agentId, agent);
    console.log(`‚úÖ Agent ${agentId} added successfully`);
  }

  /**
   * Remove an agent
   */
  async removeAgent(agentId: string): Promise<boolean> {
    const agent = this.agents.get(agentId);
    
    if (!agent) {
      console.log(`‚ö†Ô∏è Agent ${agentId} not found for removal`);
      return false;
    }

    console.log(`üóëÔ∏è Removing agent: ${agentId}`);
    
    await agent.cleanup();
    this.agents.delete(agentId);

    console.log(`‚úÖ Agent ${agentId} removed successfully`);
    return true;
  }

  /**
   * Centrally register an agent with the communication protocol
   * This ensures all agents are discoverable through the multi-agent system
   */
  private async registerAgentWithCommunicationProtocol(agent: BaseAgent): Promise<void> {
    if (!this.communicationProtocol) {
      console.warn(`‚ö†Ô∏è Communication protocol not available for ${agent.getConfig().id} registration`);
      return;
    }

    try {
      const config = agent.getConfig();
      
      // Create agent registration data
      const registration = {
        agentId: config.id,
        agentType: config.name.toLowerCase().replace(/agent/i, '').replace(/\s+/g, ''),        capabilities: config.capabilities.map(cap => ({
          name: cap,
          description: `${cap} capability provided by ${config.name}`,
          version: '1.0.0',
          parameters: {},
          qualityThreshold: 85,
          constitutionalCompliant: true
        })),
        endpoint: `${oneAgentConfig.mcpUrl}/agent/${config.id}`,
        status: 'online' as const,
        loadLevel: 0,
        qualityScore: 90,
        lastSeen: new Date()
      };

      // Register with communication protocol
      const success = await this.communicationProtocol.registerAgent(registration);
      
      if (success) {
        console.log(`‚úÖ Agent ${config.id} registered with communication protocol`);
      } else {
        console.warn(`‚ö†Ô∏è Failed to register ${config.id} with communication protocol`);
      }
    } catch (error) {
      console.error(`‚ùå Error registering ${agent.getConfig().id} with communication protocol:`, error);
    }
  }

  /**
   * Register all existing agents with the communication protocol
   * Called when the communication protocol is set after agents are already created
   */
  async registerAllExistingAgents(): Promise<void> {
    if (!this.communicationProtocol) {
      console.warn('‚ö†Ô∏è Communication protocol not available for bulk registration');
      return;
    }    console.log('üîÑ Registering all existing agents with communication protocol...');
    
    const agents = Array.from(this.agents.values());
    for (const agent of agents) {
      await this.registerAgentWithCommunicationProtocol(agent);
    }
    
    console.log(`‚úÖ Registered ${this.agents.size} agents with communication protocol`);
  }
}

