} from './AgentProfile';
import { ProfileManager } from './ProfileManager';
// Canonical memory bridge for all memory operations
import { OneAgentMemory, OneAgentMemoryConfig } from '../../memory/OneAgentMemory';

export interface EvolutionAnalysis {
