      return {
        qualityScores: [85],
        userSatisfaction: [80],
        errorRates: [5],
        responseTime: [1000],
        capabilityUsage: {}
      };
    }
  }

  private async getUserFeedback(): Promise<any> {
    try {
      // No direct method; fallback to empty feedback structure
      return { positive: [], negative: [], suggestions: [] };
    } catch (error) {
      console.error('Failed to get user feedback:', error);
      return { positive: [], negative: [], suggestions: [] };
    }
  }

  private async getMemoryInsights(): Promise<any> {
    try {
      // Use canonical memory bridge to fetch system analytics
      return await this.memorySystem.searchMemory({ collection: 'analytics', query: {} });
    } catch (error) {
      console.error('Failed to get memory insights:', error);
      return { patterns: [], successfulStrategies: [], problematicAreas: [] };
    }
  }

  private async storeEvolutionRecord(record: any): Promise<void> {
    try {
      // Store as a learning in the canonical memory system
      // Canonical memory usage: single object argument
      await this.memorySystem.addMemory({ collection: 'learnings', record: { ...record } });
      console.log('Evolution record stored:', record);
    } catch (error) {
      console.error('Failed to store evolution record:', error);
    }
  }

  // Analysis helper methods
  private calculateAverageScore(scores: number[]): number {
    return scores.length > 0 ? scores.reduce((a, b) => a + b, 0) / scores.length : 0;
  }
  private assessConstitutionalCompliance(): number {
    // Simplified constitutional compliance assessment
    return 95; // High compliance by default
  }

  private analyzeCapabilityEffectiveness(profile: AgentProfile): Record<string, number> {
    const effectiveness: Record<string, number> = {};
    
    profile.capabilities.forEach(cap => {
      effectiveness[cap.name] = cap.usage.averageQuality || 85;
    });

    return effectiveness;
  }

  private identifyPerformanceIssues(): any[] {
    // Simplified issue identification
    return [
      {
        category: 'quality',
        severity: 'medium' as const,
        description: 'Occasional quality scores below threshold',
        frequency: 0.1,
        examples: ['Response lacked sufficient detail', 'Unclear reasoning provided']
      }
    ];
  }

  private findImprovementOpportunities(): any[] {
    // Simplified opportunity identification
    return [
      {
        category: 'instructions',
        potential: 85,
        effort: 60,
        risk: 30,
        description: 'Enhance instruction clarity for better task understanding'
      }
    ];
  }

  private generateSpecificRecommendations(): EvolutionChange[] {
    // Simplified recommendation generation
    return [
      {
        category: 'instructions',
        field: 'coreCapabilities',
        oldValue: 'Current capabilities',
        newValue: 'Enhanced capabilities with clarity improvements',
        reasoning: 'Improve instruction clarity based on performance analysis',
        expectedImprovement: 'Better task understanding and execution',
        confidence: 85
      }
    ];
  }

  private getChangeRisk(change: EvolutionChange): 'low' | 'medium' | 'high' {
    if (change.category === 'personality') return 'medium';
    if (change.category === 'instructions') return 'low';
    return 'low';
  }
  private getImpactScore(change: EvolutionChange): number {
    // Calculate impact score based on change characteristics
    let score = 0.5; // Base score
    
    // Higher confidence = higher impact
    score += (change.confidence / 100) * 0.3;
    
    // Different categories have different base impacts
    if (change.category === 'instructions') score += 0.2;
    if (change.category === 'personality') score += 0.1;
    if (change.category === 'capabilities') score += 0.3;
    
    return Math.min(score, 1.0); // Cap at 1.0
  }
  private validateAccuracy(change: EvolutionChange, profile: AgentProfile): boolean {
    // Check if change maintains accuracy standards based on profile
    const meetsBasicCriteria = change.reasoning.length > 10 && change.confidence > 50;
    
    // Check against profile's quality thresholds
    const accuracyThreshold = profile.qualityThresholds?.qualityDimensions?.accuracy || 80;
    const meetsThreshold = change.confidence >= accuracyThreshold;
    
    return meetsBasicCriteria && meetsThreshold;
  }

  private validateTransparency(change: EvolutionChange): boolean {
    return change.reasoning.length > 20 && change.expectedImprovement.length > 10;
  }

  private validateHelpfulness(change: EvolutionChange): boolean {
    return change.expectedImprovement.length > 0;
  }

  private validateSafety(change: EvolutionChange, profile: AgentProfile): boolean {
    // Ensure change doesn't disable safety features
    if (change.category === 'instructions' && change.field === 'prohibitions') {
      return Array.isArray(change.newValue) && change.newValue.length > 0;
    }
      // Check against profile safety requirements
    const safetyThreshold = profile.qualityThresholds?.qualityDimensions?.safety || 95;
    const isSafe = change.confidence >= safetyThreshold || change.category !== 'instructions';
    
    return isSafe;
  }

  private applyChange(profile: AgentProfile, change: EvolutionChange): void {
    // Apply change to the profile based on category and field
    console.log(`Applying change to profile ${profile.metadata.name}: ${change.category}.${change.field}`);
    
    // This would need proper path-based property setting
    // For now, just log the change and basic validation
    if (change.category === 'instructions') {
      console.log(`Updating instructions field: ${change.field}`);
    } else if (change.category === 'personality') {
      console.log(`Updating personality field: ${change.field}`);
    } else if (change.category === 'capabilities') {
      console.log(`Updating capabilities field: ${change.field}`);
    }
  }

  private assessOverallRisk(changes: EvolutionChange[]): 'low' | 'medium' | 'high' {
    const riskScores = changes.map(c => this.getChangeRisk(c));
    if (riskScores.includes('high')) return 'high';
    if (riskScores.includes('medium')) return 'medium';
    return 'low';
  }
  /**
   * Get evolution engine status
   */  getStatus(): { isEvolving: boolean; lastEvolution?: string } {
    const profile = this.profileManager.getCurrentProfile();
    const result: { isEvolving: boolean; lastEvolution?: string } = {
      isEvolving: this.isEvolving
    };
    
    if (profile?.metadata.lastEvolved) {
      result.lastEvolution = profile.metadata.lastEvolved;
    }
    
    return result;
  }
}

/**
 * Evolution Validator for ALITA Evolution Engine
 * Ensures evolution plans are safe, effective, and constitutionally compliant
 * 
 * @version 1.0.0
 * @date 2025-06-15
 */

import { ConstitutionalValidator } from '../../validation/ConstitutionalValidator';
import { PerformanceMonitor } from '../../monitoring/PerformanceMonitor';
import { EvolutionPlan } from './ALITAAutoEvolution';

export interface SafetyValidation {
  passed: boolean;
  score: number;
  requiredSafeguards: string[];
  riskFactors: string[];
  mitigations: string[];
}

export interface HypothesisTest {
  hypothesis: string;
  testResult: boolean;
  projectedPerformance: PerformanceProjection;
  statisticalSignificance: number;
  testMethod: string;
}

export interface PerformanceProjection {
  expectedImprovement: number;
  confidenceLevel: number;
  projectedMetrics: Record<string, number>;
  uncertaintyRange: [number, number];
}

export interface RegressionAnalysis {
  riskLevel: number; // 0-1 scale
  riskFactors: string[];
  mitigations: string[];
  rollbackTriggers: string[];
}

export class EvolutionValidator {
  constructor(
    private constitutionalValidator: ConstitutionalValidator,
    private performanceMonitor: PerformanceMonitor
  ) {}

  /**
   * Validate evolution plan for safety compliance
   * WHY: Safety validation prevents harmful changes that could damage user experience
   */
  async validateSafetyCompliance(plan: EvolutionPlan): Promise<SafetyValidation> {
    const startTime = Date.now();

    try {
      const riskFactors: string[] = [];
      const requiredSafeguards: string[] = [];
      const mitigations: string[] = [];

      // Check for high-risk changes
      for (const improvement of plan.targetImprovements) {
        // Large improvements are inherently risky
        const improvementPercentage = (improvement.targetValue - improvement.currentValue) / improvement.currentValue;
        
        if (improvementPercentage > 0.5) { // >50% improvement
          riskFactors.push(`Large improvement target: ${improvement.metric} (+${Math.round(improvementPercentage * 100)}%)`);
          requiredSafeguards.push(`Gradual rollout for ${improvement.metric}`);
          mitigations.push(`Phase ${improvement.metric} improvement over multiple evolution cycles`);
        }

        // Low confidence improvements are risky
        if (improvement.confidence < 0.7) {
          riskFactors.push(`Low confidence improvement: ${improvement.metric} (${improvement.confidence})`);
          requiredSafeguards.push(`Enhanced monitoring for ${improvement.metric}`);
          mitigations.push(`A/B testing for ${improvement.metric} changes`);
        }
      }

      // Constitutional safety check
      const constitutionalValidation = await this.constitutionalValidator.validate(
        `Evolution plan targeting improvements: ${plan.targetImprovements.map(i => `${i.metric}: ${i.improvementStrategy}`).join(', ')}`
      );

      if (!constitutionalValidation.passed) {
        riskFactors.push('Constitutional compliance violation');
        requiredSafeguards.push('Constitutional validator approval required');
        mitigations.push('Revise evolution strategy to ensure constitutional compliance');
      }

      // Check for conflicting improvements
      const metricConflicts = this.detectMetricConflicts(plan.targetImprovements);
      if (metricConflicts.length > 0) {
        riskFactors.push(`Conflicting metrics: ${metricConflicts.join(', ')}`);
        requiredSafeguards.push('Multi-objective optimization validation');
        mitigations.push('Prioritize metrics and phase implementation');
      }

      // Calculate overall safety score
      const baseScore = 100;
      const riskPenalty = Math.min(riskFactors.length * 15, 60); // Max 60 point penalty
      const safetyScore = Math.max(baseScore - riskPenalty, 40); // Minimum 40 score

      const validation: SafetyValidation = {
        passed: safetyScore >= 70 && constitutionalValidation.passed,
        score: safetyScore,
        requiredSafeguards,
        riskFactors,
        mitigations
      };

      await this.performanceMonitor.recordLatency('safety_validation', Date.now() - startTime);
      return validation;

    } catch (error) {
      await this.performanceMonitor.recordError('safety_validation', error as Error);
      throw error;
    }
  }

  /**
   * Test evolution hypothesis with statistical rigor
   * WHY: Hypothesis testing ensures evolution is based on sound evidence
   */
  async testEvolutionHypothesis(plan: EvolutionPlan): Promise<HypothesisTest> {
    const startTime = Date.now();

    try {
      // Formulate hypothesis
      const hypothesis = this.formulateHypothesis(plan);
      
      // Estimate statistical significance based on plan characteristics
      const statisticalSignificance = this.estimateStatisticalSignificance(plan);
      
      // Project performance improvements
      const projectedPerformance = await this.projectPerformanceImprovement(plan);
      
      // Determine test result based on statistical significance and projected impact
      const testResult = statisticalSignificance >= 0.8 && projectedPerformance.expectedImprovement >= 0.05;

      const hypothesisTest: HypothesisTest = {
        hypothesis,
        testResult,
        projectedPerformance,
        statisticalSignificance,
        testMethod: 'Confidence interval analysis with impact projection'
      };

      await this.performanceMonitor.recordLatency('hypothesis_testing', Date.now() - startTime);
      return hypothesisTest;

    } catch (error) {
      await this.performanceMonitor.recordError('hypothesis_testing', error as Error);
      throw error;
    }
  }

  /**
   * Analyze regression risk of evolution plan
   * WHY: Regression analysis prevents evolution from reducing overall performance
   */
  async checkRegressionRisk(plan: EvolutionPlan): Promise<RegressionAnalysis> {
    const startTime = Date.now();

    try {
      const riskFactors: string[] = [];
      const mitigations: string[] = [];
      const rollbackTriggers: string[] = [];

      // Analyze each improvement for regression risk
      for (const improvement of plan.targetImprovements) {
        // High-impact changes have higher regression risk
        const impactMagnitude = Math.abs(improvement.targetValue - improvement.currentValue) / improvement.currentValue;
        
        if (impactMagnitude > 0.3) { // >30% change
          riskFactors.push(`High-impact change in ${improvement.metric}`);
          mitigations.push(`Gradual rollout of ${improvement.metric} changes`);
          rollbackTriggers.push(`${improvement.metric} degradation >10%`);
        }

        // Low confidence improvements are more likely to regress
        if (improvement.confidence < 0.8) {
          riskFactors.push(`Low confidence in ${improvement.metric} improvement`);
          mitigations.push(`Enhanced monitoring during ${improvement.metric} evolution`);
          rollbackTriggers.push(`${improvement.metric} shows no improvement within 48h`);
        }
      }

      // Check for cascading failure risks
      if (plan.targetImprovements.length > 3) {
        riskFactors.push('Multiple simultaneous changes increase complexity risk');
        mitigations.push('Staggered implementation of improvements');
        rollbackTriggers.push('Any metric degrades >5% during evolution');
      }

      // Calculate overall regression risk
      const baseRisk = 0.1; // 10% baseline risk
      const riskMultiplier = 1 + (riskFactors.length * 0.15); // Each risk factor adds 15%
      const riskLevel = Math.min(baseRisk * riskMultiplier, 0.8); // Cap at 80% risk

      const analysis: RegressionAnalysis = {
        riskLevel,
        riskFactors,
        mitigations,
        rollbackTriggers
      };

      await this.performanceMonitor.recordLatency('regression_analysis', Date.now() - startTime);
      return analysis;

    } catch (error) {
      await this.performanceMonitor.recordError('regression_analysis', error as Error);
      throw error;
    }
  }

  // ========================================
  // Private Helper Methods
  // ========================================

  private detectMetricConflicts(improvements: any[]): string[] {
    const conflicts: string[] = [];
    
    // Check for known conflicting metrics
    const hasSpeedImprovement = improvements.some(i => i.metric === 'response_time');
    const hasQualityImprovement = improvements.some(i => i.metric === 'response_quality');
    
    if (hasSpeedImprovement && hasQualityImprovement) {
      conflicts.push('speed_vs_quality');
    }
    
    return conflicts;
  }

  private formulateHypothesis(plan: EvolutionPlan): string {
    const improvements = plan.targetImprovements.map(i => 
      `${i.metric} from ${i.currentValue} to ${i.targetValue}`
    ).join(', ');
    
    return `Implementing evolution plan will improve ${improvements} with ${plan.estimatedImpact.confidenceInterval[0]}% to ${plan.estimatedImpact.confidenceInterval[1]}% confidence`;
  }

  private estimateStatisticalSignificance(plan: EvolutionPlan): number {
    // Simplified significance calculation based on plan characteristics
    let significance = 0.5; // Base significance
    
    // Higher confidence improvements increase significance
    const avgConfidence = plan.targetImprovements.reduce((sum, i) => sum + i.confidence, 0) / plan.targetImprovements.length;
    significance += avgConfidence * 0.3;
    
    // More safeguards increase significance
    significance += Math.min(plan.constitutionalSafeguards.length * 0.05, 0.2);
    
    return Math.min(significance, 0.95); // Cap at 95%
  }

  private async projectPerformanceImprovement(plan: EvolutionPlan): Promise<PerformanceProjection> {
    // Calculate expected improvement based on target improvements
    const avgImprovement = plan.targetImprovements.reduce((sum, i) => {
      const improvement = (i.targetValue - i.currentValue) / i.currentValue;
      return sum + improvement * i.confidence;
    }, 0) / plan.targetImprovements.length;

    // Project individual metrics
    const projectedMetrics: Record<string, number> = {};
    for (const improvement of plan.targetImprovements) {
      projectedMetrics[improvement.metric] = improvement.targetValue;
    }

    // Calculate uncertainty range based on confidence levels
    const avgConfidence = plan.targetImprovements.reduce((sum, i) => sum + i.confidence, 0) / plan.targetImprovements.length;
    const uncertaintyFactor = 1 - avgConfidence;
    const uncertaintyRange: [number, number] = [
      avgImprovement * (1 - uncertaintyFactor),
      avgImprovement * (1 + uncertaintyFactor)
    ];

    return {
      expectedImprovement: avgImprovement,
      confidenceLevel: avgConfidence,
      projectedMetrics,
      uncertaintyRange
    };
  }
}

export default EvolutionValidator;

/**
 * InstructionsConverter.ts - Convert Static Instructions to Dynamic Agent Profile
 * 
 * Migrates the current .instructions.md file to the new AgentProfile JSON format
 * while preserving all functionality and enhancing with evolution capabilities.
 */

import * as fs from 'fs/promises';
import * as path from 'path';
import { AgentProfile } from './AgentProfile';
import { ProfileManager } from './ProfileManager';

export class InstructionsConverter {
  private static instance: InstructionsConverter;
  private profileManager: ProfileManager;

  private constructor() {
    this.profileManager = ProfileManager.getInstance();
  }

  public static getInstance(): InstructionsConverter {
    if (!InstructionsConverter.instance) {
      InstructionsConverter.instance = new InstructionsConverter();
    }
    return InstructionsConverter.instance;
  }

  /**
   * Convert current .instructions.md to AgentProfile format
   */
  async convertInstructionsToProfile(): Promise<AgentProfile> {
    console.log('üîÑ Converting .instructions.md to AgentProfile format...');

    try {
      // Read current instructions file
      const instructionsPath = this.getInstructionsPath();
      const instructionsContent = await this.readInstructionsFile(instructionsPath);

      // Parse instructions into structured format
      const parsedInstructions = await this.parseInstructions(instructionsContent);

      // Create AgentProfile from parsed instructions
      const agentProfile = await this.createProfileFromInstructions(parsedInstructions);

      // Validate and save the new profile
      const validation = await this.profileManager.validateProfile(agentProfile);
      if (!validation.isValid) {
        console.warn('Profile validation warnings:', validation.warnings);
      }

      await this.profileManager.saveProfile(agentProfile, 'oneagent-profile');

      // Archive original instructions for reference
      await this.archiveOriginalInstructions(instructionsContent);

      console.log('‚úÖ Successfully converted instructions to AgentProfile format');
