   */
  private getInstructionsPath(): string {
    // Try to locate the .instructions.md file
    const possiblePaths = [
      'prompts/instructions/.instructions.md',
      '.instructions.md',
      'instructions.md'
    ];

    return possiblePaths[0]; // Use the first path for now
  }

  /**
   * Read instructions file content
   */
  private async readInstructionsFile(instructionsPath: string): Promise<string> {
    try {
      const fullPath = path.join(process.cwd(), instructionsPath);
      return await fs.readFile(fullPath, 'utf8');
    } catch (error) {
      console.error(`Failed to read instructions file: ${instructionsPath}`);
      throw error;
    }
  }

  /**
   * Parse instructions markdown into structured data
   */
  private async parseInstructions(content: string): Promise<any> {
    console.log('üìù Parsing instructions content...');

    const sections: Record<string, string[]> = {};
    const lines = content.split('\n');
    let currentSection = '';
    let currentContent: string[] = [];

    for (const line of lines) {
      // Detect section headers
      if (line.startsWith('## ') || line.startsWith('# ')) {
        // Save previous section
        if (currentSection && currentContent.length > 0) {
          sections[currentSection] = [...currentContent];
        }
        
        // Start new section
        currentSection = line.replace(/^#+\s*/, '').toLowerCase()
          .replace(/[^a-z0-9]+/g, '_')
          .replace(/_+/g, '_')
          .replace(/^_|_$/g, '');
        currentContent = [];
      } else if (line.trim() && currentSection) {
        currentContent.push(line.trim());
      }
    }

    // Save final section
    if (currentSection && currentContent.length > 0) {
      sections[currentSection] = currentContent;
    }

    return sections;
  }

  /**
   * Create AgentProfile from parsed instructions
   */
  private async createProfileFromInstructions(sections: Record<string, string[]>): Promise<AgentProfile> {
    console.log('üèóÔ∏è Creating AgentProfile from parsed instructions...');

    const profile: AgentProfile = {
      metadata: {
        name: 'OneAgent',
