      },
      {
        name: 'Chain-of-Verification',
        description: 'Generate ‚Üí Verify ‚Üí Refine ‚Üí Finalize patterns',
        enabled: true,
        qualityThreshold: 85,
        usage: { frequency: 0, successRate: 90, averageQuality: 87 }
      }
    ];

    return [...coreCapabilities, ...capabilities];
  }

  /**
