      preferredFramework: 'R-I-S-E', // Default for complex tasks
      frameworkUsage: {
        'R-T-F': 0,
        'T-A-G': 0,
        'R-I-S-E': 0,
        'R-G-C': 0,
        'C-A-R-E': 0
      },
      frameworkSuccess: {
        'R-T-F': 90,
        'T-A-G': 88,
        'R-I-S-E': 92,
        'R-G-C': 85,
        'C-A-R-E': 87
      }
    };
  }

  /**
   * Extract quality thresholds
