import { HybridAgentDiscovery } from '../discovery/HybridAgentDiscovery';
import { HybridAgentRegistry } from '../registry/HybridAgentRegistry';
import { IAgentCommunication } from '../interfaces/IAgentCommunication';
import { AgentCard } from '../interfaces/AgentCard';

/**
 * HybridAgentOrchestrator
 * Canonical orchestrator for agent coordination, task assignment, and advanced features.
 * Uses only canonical interfaces and supports MCP as authoritative source with in-memory fallback.
 * BMAD/Constitutional AI validated, modular, and fully auditable.
 */
export class HybridAgentOrchestrator {
  private discovery: HybridAgentDiscovery;
  private registry: HybridAgentRegistry;
  private comm: IAgentCommunication;

  constructor(
    discovery: HybridAgentDiscovery,
    registry: HybridAgentRegistry,
    comm: IAgentCommunication
  ) {
    this.discovery = discovery;
    this.registry = registry;
    this.comm = comm;
  }

  /**
   * Selects the best agent for a given task using QuerySkill and canonical discovery.
   * @param skill - The required skill for the task.
   */
  async selectBestAgent(skill: string): Promise<AgentCard | null> {
    // Query MCP first, fallback to in-memory if needed
    const candidates = await this.discovery.findAgentsBySkill(skill);
    // Map AgentRegistration to AgentCard (normalize status/health, ensure all fields present)
    const normalizeStatus = (status: string): 'active' | 'inactive' | 'pending' | 'error' => {
      if (status === 'active' || status === 'inactive' || status === 'pending' || status === 'error') return status;
      return status === 'retired' ? 'inactive' : 'error';
    };
    const normalizeHealth = (health: string | undefined): 'healthy' | 'degraded' | 'error' => {
      if (!health) return 'error';
      if (health === 'healthy' || health === 'degraded' || health === 'error') return health;
      return 'error';
    };
    const agentCards: AgentCard[] = candidates.map((c) => ({
      agentId: c.agentId,
      displayName: c.displayName || c.agentId,
      agentType: c.agentType,
      version: c.version,
      status: normalizeStatus(c.status),
      capabilities: c.capabilities,
      skills: c.skills ?? undefined,
      health: normalizeHealth(c.health),
      lastHeartbeat: c.lastHeartbeat,
      credentials: c.credentials ?? {},
      authorization: c.authorization ?? {},
      endpoints: c.endpoints ?? undefined,
      metadata: c.metadata ?? undefined,
    }));
    // TODO: Implement advanced selection logic (BMAD-driven, context-aware)
    return agentCards.length > 0 ? agentCards[0] : null;
  }

  /**
   * Assigns a task to the selected agent, with audit logging and error handling.
   * @param agent - The agent to assign the task to.
   * @param taskContext - The context of the task.
   */
  async assignTask(agent: AgentCard, taskContext: any): Promise<boolean> {
    try {
      // Example: Send a canonical task assignment message
      const message: any = {
        from: 'orchestrator',
        to: agent.agentId,
        content: JSON.stringify(taskContext),
        type: 'request' as const,
        context: { taskType: taskContext.type },
        timestamp: Date.now(),
      };
      if (agent.credentials) message.credentials = agent.credentials;
      await this.comm.sendMessage(message);
      return true;
    } catch (e) {
      // Audit log and error handling
      console.warn(`[HybridAgentOrchestrator] Failed to assign task to agent ${agent.agentId}:`, e);
      // Optionally: log to audit system here
      return false;
    }
  }

  /**
   * Coordinates multiple agents for a complex task, supporting advanced features.
   * @param taskContext - The context of the coordination task.
   */
  async coordinateAgentsForTask(taskContext: any): Promise<void> {
    // Example: Find agents by required skills and assign subtasks
    if (!taskContext.skills || !Array.isArray(taskContext.skills)) return;
    for (const skill of taskContext.skills) {
      const agent = await this.selectBestAgent(skill);
      if (agent) {
        await this.assignTask(agent, { ...taskContext, skill });
      }
    }
    // TODO: Support QuerySkill, dynamic UX negotiation, in-task authentication
  }
}

/**
 * PersonaLoader.ts - Dynamic YAML Persona Loading System
 * 
 * Provides enterprise-grade persona loading with:
 * - YAML parsing and validation
 * - File system watching for hot-reload
 * - Constitutional AI integration
 * - Self-improvement capabilities
 * - Memory-backed prompt evolution
 */

import * as yaml from 'js-yaml';
import * as fs from 'fs';
import * as path from 'path';
import { watch, FSWatcher } from 'chokidar';
import { EventEmitter } from 'events';
import { OneAgentMemory, OneAgentMemoryConfig } from '../../memory/OneAgentMemory';

export interface PersonaConfig {
  id: string;
  name: string;
  type: string;
  version: string;
  role: string;
  style: string;
  frameworks: {
    primary: string;
    secondary?: string;
  };
  constitutionalPrinciples: string[];
  framework_implementation: Record<string, string>;
  constitutional_details: Record<string, string>;
  core_principles: string[];
  capabilities: {
    primary: string[];
    frameworks?: string[];
  };
  communication_style: {
    tone: string;
    approach: string;
    complexity: string;
    feedback: string;
  };
  memory_organization: {
    structure: string;
    categories: string[];
  };
  quality_standards: {
    minimum_score: number;
    validation_required: boolean;
    bmad_enhancement: boolean;
    constitutional_check: boolean;
  };
}

export interface PromptTemplate {
  systemPrompt: string;
  userPromptTemplate: string;
  responseInstructions: string;
  qualityChecks: string[];
}

/**
 * Enterprise-grade persona loading and management system
 */
export class PersonaLoader extends EventEmitter {
  private static instance: PersonaLoader;
  private personas: Map<string, PersonaConfig> = new Map();
  private promptTemplates: Map<string, PromptTemplate> = new Map();
  private watchers: Map<string, FSWatcher> = new Map();
  private readonly personasPath: string;
  private isInitialized = false;
  private memorySystem: OneAgentMemory;

  constructor(personasPath: string = 'prompts/personas') {
    super();
    this.personasPath = path.resolve(personasPath);
    const memoryConfig: OneAgentMemoryConfig = {
      apiKey: process.env.MEM0_API_KEY || 'demo-key',
      apiUrl: process.env.MEM0_API_URL
    };
    this.memorySystem = new OneAgentMemory(memoryConfig);
  }

  /**
   * Singleton instance for global access
   */
  public static getInstance(personasPath?: string): PersonaLoader {
    if (!PersonaLoader.instance) {
      PersonaLoader.instance = new PersonaLoader(personasPath);
    }
    return PersonaLoader.instance;
  }

  /**
   * Initialize the persona loading system
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      console.log(`[PersonaLoader] Initializing with path: ${this.personasPath}`);
      
      // Load all existing personas
      await this.loadAllPersonas();
      
      // Setup file system watching for hot-reload
      await this.setupFileWatching();
      
      this.isInitialized = true;
      this.emit('initialized');
      
      console.log(`[PersonaLoader] Initialized successfully. Loaded ${this.personas.size} personas.`);    } catch (error) {
      console.error('[PersonaLoader] Initialization failed:', error);
      throw new Error(`PersonaLoader initialization failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Load all persona YAML files from the personas directory
   */
  private async loadAllPersonas(): Promise<void> {
    if (!fs.existsSync(this.personasPath)) {
      throw new Error(`Personas directory not found: ${this.personasPath}`);
    }

    const files = fs.readdirSync(this.personasPath);
    const yamlFiles = files.filter(file => file.endsWith('.yaml') || file.endsWith('.yml'));

    console.log(`[PersonaLoader] Found ${yamlFiles.length} YAML files to load`);

    for (const file of yamlFiles) {
      try {
        await this.loadPersonaFile(file);
      } catch (error) {
        console.error(`[PersonaLoader] Failed to load persona file ${file}:`, error);
      }
    }
  }

  /**
   * Load and validate a single persona file
   */
  private async loadPersonaFile(filename: string): Promise<void> {
    const filePath = path.join(this.personasPath, filename);
    
    try {
      const fileContent = fs.readFileSync(filePath, 'utf8');
      const personaData = yaml.load(fileContent) as PersonaConfig;
      
      // Validate persona structure
      this.validatePersonaConfig(personaData, filename);
      
      // Generate prompt template from persona
      const promptTemplate = this.generatePromptTemplate(personaData);
      
      // Store persona and template
      this.personas.set(personaData.id, personaData);
      this.promptTemplates.set(personaData.id, promptTemplate);
      
      console.log(`[PersonaLoader] Loaded persona: ${personaData.id} (${personaData.name})`);
      
      // Store in memory for persistence and learning
      await this.storePersonaInMemory(personaData, promptTemplate);
      
      this.emit('personaLoaded', personaData.id, personaData);    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
      throw new Error(`Failed to load persona from ${filename}: ${errorMessage}`);
    }
  }

  /**
   * Validate persona configuration structure
   */
  private validatePersonaConfig(persona: any, filename: string): void {
    const requiredFields = [
      'id', 'name', 'type', 'version', 'role', 'style',
      'constitutionalPrinciples', 'capabilities', 'quality_standards'
    ];

    for (const field of requiredFields) {
      if (!persona[field]) {
        throw new Error(`Missing required field '${field}' in ${filename}`);
      }
    }

    // Validate Constitutional AI principles
    if (!Array.isArray(persona.constitutionalPrinciples) || persona.constitutionalPrinciples.length === 0) {
      throw new Error(`Invalid constitutionalPrinciples in ${filename}`);
    }

    // Validate quality standards
    if (!persona.quality_standards.minimum_score || persona.quality_standards.minimum_score < 0 || persona.quality_standards.minimum_score > 100) {
      throw new Error(`Invalid quality_standards.minimum_score in ${filename}`);
    }
  }

  /**
   * Generate prompt template from persona configuration
   */
  private generatePromptTemplate(persona: PersonaConfig): PromptTemplate {
    const systemPrompt = this.buildSystemPrompt(persona);
    const userPromptTemplate = this.buildUserPromptTemplate();
    const responseInstructions = this.buildResponseInstructions(persona);
    const qualityChecks = this.buildQualityChecks(persona);

    return {
      systemPrompt,
      userPromptTemplate,
      responseInstructions,
      qualityChecks
    };
  }

  /**
   * Build system prompt from persona configuration
   */
  private buildSystemPrompt(persona: PersonaConfig): string {
    const frameworkSection = persona.frameworks 
      ? `\nFramework Integration:\n- Primary: ${persona.frameworks.primary}\n- Secondary: ${persona.frameworks.secondary || 'N/A'}`
      : '';

    const principlesSection = persona.constitutionalPrinciples.length > 0
      ? `\nConstitutional AI Principles:\n${persona.constitutionalPrinciples.map(p => `- ${p}: ${persona.constitutional_details?.[p] || 'Apply with care and transparency'}`).join('\n')}`
      : '';

    const capabilitiesSection = persona.capabilities.primary.length > 0
      ? `\nCore Capabilities:\n${persona.capabilities.primary.map(c => `- ${c}`).join('\n')}`
      : '';

    const principlesListSection = persona.core_principles?.length > 0
      ? `\nCore Principles:\n${persona.core_principles.map(p => `- ${p}`).join('\n')}`
      : '';

    return `You are ${persona.name} (${persona.id}).

Role: ${persona.role}
Communication Style: ${persona.style}
${frameworkSection}
${principlesSection}
${capabilitiesSection}
${principlesListSection}

Quality Standards:
- Minimum Quality Score: ${persona.quality_standards.minimum_score}%
- Constitutional AI Validation: ${persona.quality_standards.constitutional_check ? 'Required' : 'Optional'}
- BMAD Enhancement: ${persona.quality_standards.bmad_enhancement ? 'Enabled' : 'Disabled'}

Communication Approach:
- Tone: ${persona.communication_style?.tone || 'Professional'}
- Approach: ${persona.communication_style?.approach || 'Direct and helpful'}
- Complexity: ${persona.communication_style?.complexity || 'Adapts to user level'}
- Feedback Style: ${persona.communication_style?.feedback || 'Constructive'}`;
  }

  /**
   * Build user prompt template
   */
  private buildUserPromptTemplate(): string {
    return `Request Type: {requestType}
Context: {context}
Memory Context: {memoryContext}

User Request: {userMessage}

Please respond according to your persona configuration and quality standards.`;
  }

  /**
   * Build response instructions
   */
  private buildResponseInstructions(persona: PersonaConfig): string {
    return `Provide a response that:
1. Follows your constitutional principles: ${persona.constitutionalPrinciples.join(', ')}
2. Maintains your communication style (${persona.communication_style?.tone || 'professional'})
3. Meets quality standard of ${persona.quality_standards.minimum_score}%
4. Applies ${persona.frameworks?.primary || 'systematic'} framework approach
5. Provides actionable, helpful guidance`;
  }

  /**
   * Build quality checks list
   */
  private buildQualityChecks(persona: PersonaConfig): string[] {
    const checks = [
      'Accuracy and factual correctness',
      'Transparency in reasoning',
      'Helpfulness and actionability',
      'Safety and best practices'
    ];

    if (persona.quality_standards.constitutional_check) {
      checks.push('Constitutional AI principle compliance');
    }

    if (persona.quality_standards.bmad_enhancement) {
      checks.push('BMAD framework application');
    }

    return checks;
  }

  /**
   * Setup file system watching for hot-reload
   */
  private async setupFileWatching(): Promise<void> {
    const watcher = watch(this.personasPath, {
      ignored: /^\./,
      persistent: true,
      ignoreInitial: true
    });

    watcher.on('change', async (filePath) => {
      const filename = path.basename(filePath);
      if (filename.endsWith('.yaml') || filename.endsWith('.yml')) {
        console.log(`[PersonaLoader] Detected change in ${filename}, reloading...`);
        try {
          await this.loadPersonaFile(filename);
          this.emit('personaReloaded', filename);
        } catch (error) {
          console.error(`[PersonaLoader] Failed to reload ${filename}:`, error);
        }
      }
    });

    watcher.on('add', async (filePath) => {
      const filename = path.basename(filePath);
      if (filename.endsWith('.yaml') || filename.endsWith('.yml')) {
        console.log(`[PersonaLoader] New persona file detected: ${filename}`);
        try {
          await this.loadPersonaFile(filename);
          this.emit('personaAdded', filename);
        } catch (error) {
          console.error(`[PersonaLoader] Failed to load new persona ${filename}:`, error);
        }
      }
    });

    this.watchers.set('personas', watcher);
  }

  /**
   * Store persona in memory for persistence and learning
   */
  private async storePersonaInMemory(persona: PersonaConfig, template: PromptTemplate): Promise<void> {
    try {
      const conversationData = {
        id: `persona_config_${persona.id}_${Date.now()}`,
        agentId: persona.id,
        userId: 'oneagent_system',
        timestamp: new Date(),
        content: `Persona Configuration: ${persona.name} (${persona.id}) - ${persona.role}`,
        context: {
          userId: persona.id,
          agentId: persona.id,
          sessionId: `persona_config_${Date.now()}`,
          conversationId: `persona_config_${Date.now()}`,
          messageType: 'system',
          platform: 'oneagent'
        },
        outcome: {
          success: true,
          satisfaction: 'high',
          learningsExtracted: 1,
          qualityScore: persona.quality_standards.minimum_score / 100
        },
        metadata: {
          type: 'persona_config',
          agentId: persona.id,
          version: persona.version,
          qualityScore: persona.quality_standards.minimum_score,
          constitutionalPrinciples: persona.constitutionalPrinciples,
          capabilities: persona.capabilities.primary,
          systemPrompt: template.systemPrompt
        }
      };

      await this.memorySystem.addMemory({
        ...conversationData,
        type: 'conversations'
      });
    } catch (error) {
      console.error(`[PersonaLoader] Failed to store persona in memory:`, error);
    }
  }

  /**
   * Get persona configuration by ID
   */
  public getPersona(agentId: string): PersonaConfig | null {
    return this.personas.get(agentId) || null;
  }

  /**
   * Get prompt template by agent ID
   */
  public getPromptTemplate(agentId: string): PromptTemplate | null {
    return this.promptTemplates.get(agentId) || null;
  }

  /**
   * Get all loaded personas
   */
  public getAllPersonas(): PersonaConfig[] {
    return Array.from(this.personas.values());
  }

  /**
   * Generate dynamic prompt for agent
   */
  public generatePrompt(agentId: string, userMessage: string, context: any = {}, memoryContext: any[] = []): string {
    const template = this.promptTemplates.get(agentId);
    if (!template) {
      throw new Error(`No prompt template found for agent: ${agentId}`);
    }

    const memoryText = memoryContext.length > 0 
      ? memoryContext.map(m => `- ${m.content}`).join('\n')
      : 'No relevant past context';

    const userPrompt = template.userPromptTemplate
      .replace('{requestType}', context.requestType || 'general')
      .replace('{context}', JSON.stringify(context, null, 2))
      .replace('{memoryContext}', memoryText)
      .replace('{userMessage}', userMessage);

    return `${template.systemPrompt}\n\n${userPrompt}\n\n${template.responseInstructions}`;
  }

  /**
   * Update persona configuration (for self-improvement)
   */
  public async updatePersona(agentId: string, updates: Partial<PersonaConfig>): Promise<void> {
    const currentPersona = this.personas.get(agentId);
    if (!currentPersona) {
      throw new Error(`Persona not found: ${agentId}`);
    }

    const updatedPersona = { ...currentPersona, ...updates };
    
    // Validate updated configuration
    this.validatePersonaConfig(updatedPersona, `${agentId}-update`);
    
    // Generate new template
    const newTemplate = this.generatePromptTemplate(updatedPersona);
    
    // Update in memory
    this.personas.set(agentId, updatedPersona);
    this.promptTemplates.set(agentId, newTemplate);
    
    // Store update in memory for learning
    await this.storePersonaInMemory(updatedPersona, newTemplate);
    
    console.log(`[PersonaLoader] Updated persona: ${agentId}`);
    this.emit('personaUpdated', agentId, updatedPersona);
  }

  /**
   * Cleanup resources
   */
  public async cleanup(): Promise<void> {
    for (const watcher of Array.from(this.watchers.values())) {
      await watcher.close();
    }
    this.watchers.clear();
    this.personas.clear();
    this.promptTemplates.clear();
    this.isInitialized = false;
  }
}

// Export singleton instance
export const personaLoader = PersonaLoader.getInstance();

/**
 * SelfImprovementSystem.ts - Agent Self-Evaluation and Improvement
 * 
 * Provides systematic self-improvement capabilities:
 * - Performance tracking and analysis
 * - Constitutional AI validation feedback
 * - Quality score monitoring
 * - Automatic persona optimization
 * - Learning from interaction patterns
 */

import { EventEmitter } from 'events';
import { personaLoader, PersonaConfig } from './PersonaLoader';
import { OneAgentMemory, OneAgentMemoryConfig } from '../../memory/OneAgentMemory';
// ALITA Integration
import { ALITA } from '../evolution';

export interface PerformanceMetrics {
  agentId: string;
  timestamp: string;
  interactionCount: number;
  averageQualityScore: number;
  constitutionalCompliance: number;
  userSatisfactionScore: number;
  responseTime: number;
  errorRate: number;
  capabilityUtilization: Record<string, number>;
}

export interface ImprovementSuggestion {
  category: 'prompt' | 'capability' | 'communication' | 'quality';
  priority: 'low' | 'medium' | 'high' | 'critical';
  description: string;
  proposedChange: any;
  expectedImprovement: string;
  riskLevel: 'low' | 'medium' | 'high';
}

/**
 * Enhanced self-improvement suggestion with ALITA evolution integration
 */
export interface ALITAImprovementSuggestion extends ImprovementSuggestion {
  evolutionTrigger: boolean;
  evolutionAggressiveness?: 'conservative' | 'moderate' | 'aggressive';
  profileChanges?: any[];
}

export interface SelfEvaluationResult {
  agentId: string;
  timestamp: string;
  currentMetrics: PerformanceMetrics;
  improvements: ImprovementSuggestion[];
  overallHealth: 'excellent' | 'good' | 'needs_attention' | 'critical';
  recommendedActions: string[];
}

/**
 * Enhanced self-evaluation with ALITA evolution capabilities
 */
export interface ALITASelfEvaluationResult extends SelfEvaluationResult {
  evolutionRecommended: boolean;
  evolutionReasoning?: string;
  nextEvolutionThreshold?: number;
}

/**
 * Self-improvement system for agent optimization
 */
export class SelfImprovementSystem extends EventEmitter {
  private static instance: SelfImprovementSystem;
  private performanceHistory: Map<string, PerformanceMetrics[]> = new Map();
  private evaluationSchedule: Map<string, NodeJS.Timeout> = new Map();
  private isActive = false;
  private memorySystem: OneAgentMemory;

  private constructor() {
    super();
    const memoryConfig: OneAgentMemoryConfig = {
      apiKey: process.env.MEM0_API_KEY || 'demo-key',
      apiUrl: process.env.MEM0_API_URL
    };
    this.memorySystem = new OneAgentMemory(memoryConfig);
  }

  public static getInstance(): SelfImprovementSystem {
    if (!SelfImprovementSystem.instance) {
      SelfImprovementSystem.instance = new SelfImprovementSystem();
    }
    return SelfImprovementSystem.instance;
  }

  /**
   * Initialize the self-improvement system
   */
  public async initialize(): Promise<void> {
    if (this.isActive) {
      return;
    }

    console.log('[SelfImprovementSystem] Initializing...');
    
    // Load historical performance data
    await this.loadPerformanceHistory();
    
    // Schedule periodic evaluations for all agents
    await this.scheduleEvaluations();
    
    this.isActive = true;
    this.emit('initialized');
    
    console.log('[SelfImprovementSystem] Initialized successfully');
  }

  /**
   * Register an agent for self-improvement tracking
   */
  public async registerAgent(agentId: string): Promise<void> {
    if (!this.performanceHistory.has(agentId)) {
      this.performanceHistory.set(agentId, []);
    }
    
    // Schedule regular evaluation (every 24 hours)
    this.scheduleAgentEvaluation(agentId, 24 * 60 * 60 * 1000);
    
    console.log(`[SelfImprovementSystem] Registered agent: ${agentId}`);
  }

  /**
   * Record performance metrics for an agent
   */
  public async recordPerformance(metrics: PerformanceMetrics): Promise<void> {
    const history = this.performanceHistory.get(metrics.agentId) || [];
    history.push(metrics);
    
    // Keep only last 100 entries per agent
    if (history.length > 100) {
      history.splice(0, history.length - 100);
    }
    
    this.performanceHistory.set(metrics.agentId, history);
      // Store in memory for persistence
    await this.memorySystem.addMemory({
      id: `performance_metrics_${metrics.agentId}_${Date.now()}`,
      agentId: metrics.agentId,
      metrics,
      timestamp: new Date().toISOString(),
      type: 'learnings',
      metadata: {
        type: 'performance_metrics',
        agentId: metrics.agentId,
        metrics
      }
    });
    
    // Check if immediate evaluation is needed
    if (this.needsImmediateAttention(metrics)) {
      await this.performSelfEvaluation(metrics.agentId);
    }
  }

  /**
   * Perform comprehensive self-evaluation for an agent
   */
  public async performSelfEvaluation(agentId: string): Promise<SelfEvaluationResult> {
    console.log(`[SelfImprovementSystem] Performing self-evaluation for ${agentId}`);
    
    const currentPersona = personaLoader.getPersona(agentId);
    if (!currentPersona) {
      throw new Error(`No persona found for agent: ${agentId}`);
    }
    
    const history = this.performanceHistory.get(agentId) || [];
    const recentMetrics = history.slice(-10); // Last 10 interactions
    
    if (recentMetrics.length === 0) {
      throw new Error(`No performance data available for agent: ${agentId}`);
    }
    
    const currentMetrics = this.calculateAverageMetrics(recentMetrics);
    const improvements = await this.generateImprovementSuggestions(currentPersona, recentMetrics);
    const overallHealth = this.assessOverallHealth(currentMetrics);
    const recommendedActions = this.generateRecommendedActions(improvements, overallHealth);
    
    const evaluation: SelfEvaluationResult = {
      agentId,
      timestamp: new Date().toISOString(),
      currentMetrics,
      improvements,
      overallHealth,
      recommendedActions
    };
      // Store evaluation results
    await this.memorySystem.addMemory({
      id: `self_evaluation_${agentId}_${Date.now()}`,
      agentId,
      userId: 'system',
      timestamp: new Date(),
      content: `Self-evaluation for ${agentId}: ${overallHealth} health, ${improvements.length} improvement suggestions`,
      context: {
        userId: agentId,
        agentId,
        sessionId: `self_evaluation_${Date.now()}`,
        conversationId: `self_evaluation_${Date.now()}`,
        messageType: 'system',
        platform: 'oneagent'
      },
      outcome: {
        success: true,
        satisfaction: 'high',
        learningsExtracted: improvements.length,
        qualityScore: 0.9
      },
      metadata: {
        type: 'self_evaluation',
        agentId,
        evaluation,
        timestamp: evaluation.timestamp
      },
      type: 'conversations'
    });
    
    // Apply high-priority improvements automatically
    await this.applyAutomaticImprovements(agentId, improvements);
    
    this.emit('evaluationCompleted', evaluation);
    
    return evaluation;
  }

  /**
   * Generate improvement suggestions based on performance analysis
   */  private async generateImprovementSuggestions( 
    persona: PersonaConfig, 
    metrics: PerformanceMetrics[]
  ): Promise<ImprovementSuggestion[]> {
    const suggestions: ImprovementSuggestion[] = [];
    const avgMetrics = this.calculateAverageMetrics(metrics);
    
    // Quality score improvements
    if (avgMetrics.averageQualityScore < persona.quality_standards.minimum_score) {
      suggestions.push({
        category: 'quality',
        priority: 'high',
        description: `Quality score (${avgMetrics.averageQualityScore}%) below minimum standard (${persona.quality_standards.minimum_score}%)`,
        proposedChange: {
          quality_standards: {
            ...persona.quality_standards,
            minimum_score: Math.min(persona.quality_standards.minimum_score + 5, 95)
          }
        },
        expectedImprovement: 'Stricter quality validation will improve response quality',
        riskLevel: 'low'
      });
    }
    
    // Constitutional compliance improvements
    if (avgMetrics.constitutionalCompliance < 95) {
      suggestions.push({
        category: 'prompt',
        priority: 'high',
        description: `Constitutional compliance (${avgMetrics.constitutionalCompliance}%) needs improvement`,
        proposedChange: {
          constitutional_details: {
            ...persona.constitutional_details,
            accuracy: 'Verify all information thoroughly and prefer uncertainty to incorrect statements',
            transparency: 'Explain reasoning clearly and acknowledge limitations explicitly',
            helpfulness: 'Provide actionable guidance while maintaining accuracy',
            safety: 'Prioritize user safety and follow best practices consistently'
          }
        },
        expectedImprovement: 'Enhanced constitutional guidance will improve compliance',
        riskLevel: 'low'
      });
    }
    
    // Response time improvements
    if (avgMetrics.responseTime > 5000) { // 5 seconds
      suggestions.push({
        category: 'capability',
        priority: 'medium',
        description: `Response time (${avgMetrics.responseTime}ms) exceeds optimal threshold`,
        proposedChange: {
          communication_style: {
            ...persona.communication_style,
            approach: 'Efficient and focused responses with key insights prioritized'
          }
        },
        expectedImprovement: 'Streamlined communication will reduce response time',
        riskLevel: 'low'
      });
    }
    
    // Capability utilization analysis
    for (const [capability, utilization] of Object.entries(avgMetrics.capabilityUtilization)) {
      if (utilization < 0.1) { // Less than 10% usage
        suggestions.push({
          category: 'capability',
          priority: 'low',
          description: `Capability "${capability}" underutilized (${Math.round(utilization * 100)}%)`,
          proposedChange: {
            capabilities: {
              ...persona.capabilities,
              primary: persona.capabilities.primary.filter(cap => cap !== capability)
            }
          },
          expectedImprovement: 'Removing unused capabilities will improve focus',
          riskLevel: 'medium'
        });
      }
    }
    
    return suggestions;
  }

  /**
   * Apply automatic improvements for high-priority, low-risk suggestions
   */
  private async applyAutomaticImprovements(agentId: string, suggestions: ImprovementSuggestion[]): Promise<void> {
    const autoApplyable = suggestions.filter(s => 
      s.priority === 'high' && 
      s.riskLevel === 'low' && 
      s.category !== 'capability' // Don't auto-remove capabilities
    );
    
    for (const suggestion of autoApplyable) {
      try {
        await personaLoader.updatePersona(agentId, suggestion.proposedChange);
        
        console.log(`[SelfImprovementSystem] Auto-applied improvement for ${agentId}: ${suggestion.description}`);
          // Record the improvement
        await this.memorySystem.addMemory({
          id: `auto_improvement_${agentId}_${Date.now()}`,
          agentId,
          learningType: 'pattern',
          content: `Auto-applied improvement: ${suggestion.description}`,
          confidence: 1.0,
          applicationCount: 1,
          lastApplied: new Date(),
          sourceConversations: [],
          metadata: {
            type: 'auto_improvement',
            agentId,
            suggestion,
            timestamp: new Date().toISOString()
          },
          type: 'learnings'
        });
        
      } catch (error) {
        console.error(`[SelfImprovementSystem] Failed to apply improvement for ${agentId}:`, error);
      }
    }
  }

  /**
   * Calculate average metrics from performance history
   */
  private calculateAverageMetrics(metrics: PerformanceMetrics[]): PerformanceMetrics {
    if (metrics.length === 0) {
      throw new Error('No metrics to calculate average from');
    }
    
    const totals = metrics.reduce((acc, metric) => ({
      interactionCount: acc.interactionCount + metric.interactionCount,
      averageQualityScore: acc.averageQualityScore + metric.averageQualityScore,
      constitutionalCompliance: acc.constitutionalCompliance + metric.constitutionalCompliance,
      userSatisfactionScore: acc.userSatisfactionScore + metric.userSatisfactionScore,
      responseTime: acc.responseTime + metric.responseTime,
      errorRate: acc.errorRate + metric.errorRate,
      capabilityUtilization: this.mergeCapabilityUtilization(acc.capabilityUtilization, metric.capabilityUtilization)
    }), {
      interactionCount: 0,
      averageQualityScore: 0,
      constitutionalCompliance: 0,
      userSatisfactionScore: 0,
      responseTime: 0,
      errorRate: 0,
      capabilityUtilization: {}
    });
    
    return {
      agentId: metrics[0].agentId,
      timestamp: new Date().toISOString(),
      interactionCount: totals.interactionCount,
      averageQualityScore: totals.averageQualityScore / metrics.length,
      constitutionalCompliance: totals.constitutionalCompliance / metrics.length,
      userSatisfactionScore: totals.userSatisfactionScore / metrics.length,
      responseTime: totals.responseTime / metrics.length,
      errorRate: totals.errorRate / metrics.length,
      capabilityUtilization: this.averageCapabilityUtilization(totals.capabilityUtilization, metrics.length)
    };
  }

  /**
   * Merge capability utilization maps
   */
  private mergeCapabilityUtilization(acc: Record<string, number>, current: Record<string, number>): Record<string, number> {
    const result = { ...acc };
    for (const [key, value] of Object.entries(current)) {
      result[key] = (result[key] || 0) + value;
    }
    return result;
  }

  /**
   * Calculate average capability utilization
   */
  private averageCapabilityUtilization(totals: Record<string, number>, count: number): Record<string, number> {
    const result: Record<string, number> = {};
    for (const [key, value] of Object.entries(totals)) {
      result[key] = value / count;
    }
    return result;
  }

  /**
   * Assess overall health based on metrics
   */
  private assessOverallHealth(metrics: PerformanceMetrics): 'excellent' | 'good' | 'needs_attention' | 'critical' {
    let score = 0;
    
    // Quality score (30% weight)
    if (metrics.averageQualityScore >= 90) score += 30;
    else if (metrics.averageQualityScore >= 80) score += 25;
    else if (metrics.averageQualityScore >= 70) score += 15;
    else score += 5;
    
    // Constitutional compliance (25% weight)
    if (metrics.constitutionalCompliance >= 95) score += 25;
    else if (metrics.constitutionalCompliance >= 85) score += 20;
    else if (metrics.constitutionalCompliance >= 75) score += 10;
    else score += 0;
    
    // User satisfaction (20% weight)
    if (metrics.userSatisfactionScore >= 4.5) score += 20;
    else if (metrics.userSatisfactionScore >= 4.0) score += 15;
    else if (metrics.userSatisfactionScore >= 3.5) score += 10;
    else score += 5;
    
    // Response time (15% weight)
    if (metrics.responseTime <= 2000) score += 15;
    else if (metrics.responseTime <= 5000) score += 10;
    else if (metrics.responseTime <= 10000) score += 5;
    else score += 0;
    
    // Error rate (10% weight)
    if (metrics.errorRate <= 0.01) score += 10;
    else if (metrics.errorRate <= 0.05) score += 8;
    else if (metrics.errorRate <= 0.1) score += 5;
    else score += 0;
    
    if (score >= 85) return 'excellent';
    if (score >= 70) return 'good';
    if (score >= 50) return 'needs_attention';
    return 'critical';
  }

  /**
   * Generate recommended actions based on improvements
   */
  private generateRecommendedActions(improvements: ImprovementSuggestion[], health: string): string[] {
    const actions: string[] = [];
    
    if (health === 'critical') {
      actions.push('Immediate manual review required - multiple critical issues detected');
    }
    
    const highPriorityImprovements = improvements.filter(i => i.priority === 'high');
    if (highPriorityImprovements.length > 0) {
      actions.push(`Review ${highPriorityImprovements.length} high-priority improvement suggestions`);
    }
    
    const qualityImprovements = improvements.filter(i => i.category === 'quality');
    if (qualityImprovements.length > 0) {
      actions.push('Focus on quality improvement strategies');
    }
    
    const capabilityImprovements = improvements.filter(i => i.category === 'capability');
    if (capabilityImprovements.length > 0) {
      actions.push('Review capability utilization and optimization');
    }
    
    if (actions.length === 0) {
      actions.push('Continue current performance monitoring');
    }
    
    return actions;
  }

  /**
   * Check if metrics indicate need for immediate attention
   */
  private needsImmediateAttention(metrics: PerformanceMetrics): boolean {
    return (
      metrics.averageQualityScore < 60 ||
      metrics.constitutionalCompliance < 70 ||
      metrics.errorRate > 0.2 ||
      metrics.userSatisfactionScore < 2.0
    );
  }

  /**
   * Schedule periodic evaluations for an agent
   */
  private scheduleAgentEvaluation(agentId: string, intervalMs: number): void {
    // Clear existing schedule
    const existing = this.evaluationSchedule.get(agentId);
    if (existing) {
      clearInterval(existing);
    }
    
    // Schedule new evaluation
    const timer = setInterval(async () => {
      try {
        await this.performSelfEvaluation(agentId);
      } catch (error) {
        console.error(`[SelfImprovementSystem] Scheduled evaluation failed for ${agentId}:`, error);
      }
    }, intervalMs);
    
    this.evaluationSchedule.set(agentId, timer);
  }

  /**
   * Schedule evaluations for all registered agents
   */
  private async scheduleEvaluations(): Promise<void> {
    const personas = personaLoader.getAllPersonas();
    for (const persona of personas) {
      await this.registerAgent(persona.id);
    }
  }

  /**
   * Load historical performance data from memory
   */
  private async loadPerformanceHistory(): Promise<void> {
    try {      const memories = await this.memorySystem.searchMemory({
        query: 'performance_metrics',
        agentIds: ['system'],
        maxResults: 1000,
        type: 'learnings'
      });
      
      // Remove redeclaration and use a unique variable name for the loaded memories
      const loadedMemories = await this.memorySystem.searchMemory({
        query: 'performance_metrics',
        agentIds: ['system'],
        maxResults: 1000,
        type: 'learnings'
      });
      for (const memory of loadedMemories) {
        if (memory.metadata?.type === 'performance_metrics' && memory.metadata?.metrics) {
          const metrics = memory.metadata.metrics as PerformanceMetrics;
          const history = this.performanceHistory.get(metrics.agentId) || [];
          history.push(metrics);
          this.performanceHistory.set(metrics.agentId, history);
        }
      }
      
      console.log(`[SelfImprovementSystem] Loaded performance history for ${this.performanceHistory.size} agents`);
    } catch (error) {
      console.error('[SelfImprovementSystem] Failed to load performance history:', error);
    }
  }

  /**
   * Get performance history for an agent
   */
  public getPerformanceHistory(agentId: string): PerformanceMetrics[] {
    return this.performanceHistory.get(agentId) || [];
  }

  /**
   * Cleanup resources
   */
  public async cleanup(): Promise<void> {
    for (const timer of this.evaluationSchedule.values()) {
      clearInterval(timer);
    }
    this.evaluationSchedule.clear();
    this.performanceHistory.clear();
    this.isActive = false;
  }
}

