/**
 * DevAgent.ts - Development Agent Implementation
 * 
 * BaseAgent instance that:
 * - Inherits from BaseAgent with memory integration
 * - Processes actual user messages
 * - Stores conversations in memory
 * - Uses AI for responses
 * - Has Constitutional AI validation
 * - Provides actual development assistance
 */

import { BaseAgent, AgentConfig, AgentContext, AgentResponse, Message, AgentAction } from '../base/BaseAgent';
import { ISpecializedAgent } from '../base/ISpecializedAgent';
import { v4 as uuidv4 } from 'uuid';

export interface DevAgentCapabilities {
  codeReview: boolean;
  debugging: boolean;
  codeGeneration: boolean;
  architectureGuidance: boolean;
  testingSupport: boolean;
  performanceOptimization: boolean;
}

export interface DevAgentResponse extends AgentResponse {
  codeExamples?: string[];
  suggestions?: string[];
  qualityScore?: number;
}

/**
 * Development Agent - ISpecializedAgent implementation
 */
export class DevAgent extends BaseAgent implements ISpecializedAgent {
  private capabilities: DevAgentCapabilities;
  private conversationHistory: Message[] = [];
  
  constructor(config: AgentConfig, promptConfig?: any) {
    super(config, promptConfig);

    this.capabilities = {
      codeReview: true,
      debugging: true,
      codeGeneration: true,
      architectureGuidance: true,
      testingSupport: true,
      performanceOptimization: true
    };
  }

  /**
   * REAL message processing - not just metadata!
   */
  async processMessage(context: AgentContext, message: string): Promise<DevAgentResponse> {
    this.validateContext(context);

    // Add to conversation history
    const userMessage: Message = {
      id: uuidv4(),
      content: message,
      sender: 'user',
      timestamp: new Date(),
      metadata: {
        sessionId: context.sessionId,
        userId: context.user.id
      }
    };
    this.conversationHistory.push(userMessage);

    // Analyze the request type
    const requestType = this.analyzeRequestType(message);
    
    // Generate AI response using the enhanced prompt system
    const aiResponse = await this.generateDevelopmentResponse(message, context, requestType);

    // Add to conversation history
    const agentMessage: Message = {
      id: uuidv4(),
      content: aiResponse,
      sender: 'agent',
      timestamp: new Date(),
      metadata: {
        sessionId: context.sessionId,
        requestType,
        qualityScore: 85 // TODO: Calculate actual quality score
      }
    };
    this.conversationHistory.push(agentMessage);

    return this.createDevResponse(aiResponse, requestType);
  }

  /**
   * REAL AI-powered development response generation with personality enhancement
   */
  private async generateDevelopmentResponse(
    message: string, 
    context: AgentContext, 
    requestType: string
  ): Promise<string> {
    // Build enhanced development prompt
    const developmentPrompt = this.buildDevelopmentPrompt(message, requestType);
    
    // Generate base response using AI
    const baseResponse = await this.generateResponse(developmentPrompt);
    
    // Apply personality enhancement for authentic DevAgent perspective
    const personalityEnhancedResponse = await this.generatePersonalityResponse(
      baseResponse,
      context,
      message
    );
    
    return personalityEnhancedResponse;
  }

  /**
   * Override domain context for DevAgent personality
   */
  protected getDomainContext(): string {
    return 'software-development';
  }

  /**
   * Override domain keywords for DevAgent expertise assessment
   */
  protected getDomainKeywords(): string[] {
    return [
      'code', 'function', 'class', 'variable', 'method', 'api', 'bug', 'debug',
      'test', 'architecture', 'database', 'algorithm', 'performance', 'security',
      'framework', 'library', 'typescript', 'javascript', 'python', 'node',
      'react', 'git', 'deployment', 'refactor', 'optimize', 'implement'
    ];
  }

  /**
   * Build specialized development prompt
   */
  private buildDevelopmentPrompt(message: string, requestType: string): string {
    const systemPrompt = `You are DevAgent, a professional development assistant with expertise in:
- Code review and quality analysis
- Debugging and troubleshooting
- Code generation and implementation
- Architecture and design guidance
- Testing strategies and implementation
- Performance optimization

Request type: ${requestType}

User request: ${message}

Provide helpful, actionable development guidance with specific examples where appropriate.`;

    return systemPrompt;
  }

  /**
   * Analyze what type of development request this is
   */
  private analyzeRequestType(message: string): string {
    const messageLower = message.toLowerCase();
    
    if (messageLower.includes('review') || messageLower.includes('check')) {
      return 'code_review';
    } else if (messageLower.includes('debug') || messageLower.includes('error') || messageLower.includes('fix')) {
      return 'debugging';
    } else if (messageLower.includes('generate') || messageLower.includes('create') || messageLower.includes('build')) {
      return 'code_generation';
    } else if (messageLower.includes('architecture') || messageLower.includes('design') || messageLower.includes('structure')) {
      return 'architecture_guidance';
    } else if (messageLower.includes('test') || messageLower.includes('testing')) {
      return 'testing_support';
    } else if (messageLower.includes('optimize') || messageLower.includes('performance') || messageLower.includes('speed')) {
      return 'performance_optimization';
    }
    
    return 'general_development';
  }

  /**
   * Create specialized development response
   */
  private createDevResponse(content: string, requestType: string): DevAgentResponse {
    return {
      content,
      actions: [{
        type: 'development_assistance',
        description: `Provided ${requestType} assistance`,
        parameters: { requestType }
      }],
      memories: [], // Memories are handled separately
      metadata: {
        agentId: this.config.id,
        timestamp: new Date().toISOString(),
        requestType,
        capabilities: Object.keys(this.capabilities),
        isRealAgent: true // NOT just metadata!
      }
    };
  }

  /**
   * Get conversation history for this session
   */
  getConversationHistory(): Message[] {
    return [...this.conversationHistory];
  }

  /**
   * Get agent capabilities
   */
  getCapabilities(): DevAgentCapabilities {    return { ...this.capabilities };
  }

  get id(): string {
    return this.config.id;
  }
}

/**
 * RealFitnessAgent - REAL Fitness & Wellness AI Agent
 * 
 * A fully functional BaseAgent implementation with:
 * - Real memory integration for tracking progress
 * - Gemini AI for intelligent fitness guidance
 * - Constitutional AI validation
 * - Specialized fitness and wellness expertise
 */

import { BaseAgent, AgentConfig, AgentContext, AgentResponse, Message, AgentAction } from '../base/BaseAgent';
import { ISpecializedAgent, AgentStatus, AgentHealthStatus } from '../base/ISpecializedAgent';
import { EnhancedPromptConfig, AgentPersona, ConstitutionalPrinciple } from '../base/EnhancedPromptEngine';

export class FitnessAgent extends BaseAgent implements ISpecializedAgent {
  constructor(config: AgentConfig, promptConfig?: any) {
    super(config, promptConfig || FitnessAgent.createFitnessPromptConfig());
  }

  // Only domain-specific action handlers below (e.g., createWorkout, trackProgress, provideNutritionAdvice)
  // All action routing, memory, NLACS, and orchestration logic is inherited from BaseAgent.

  /**
   * Process fitness and wellness related messages
   */
  async processMessage(context: AgentContext, message: string): Promise<AgentResponse> {
    try {
      this.validateContext(context);

      // Search for relevant fitness context in memory
      const relevantMemories = await this.searchMemories(
        context.user.id, 
        message, 
        5
      );

      // Generate AI response with fitness expertise
      const response = await this.generateFitnessResponse(message, relevantMemories, context);

      // Store this interaction in memory for future reference
      await this.addMemory(
        context.user.id,
        `Fitness Query: ${message}\nResponse: ${response}`,
        {
          type: 'fitness_consultation',
          category: this.categorizeQuery(message),
          timestamp: new Date().toISOString(),
          sessionId: context.sessionId
        }
      );

      return this.createResponse(response, [], relevantMemories);

    } catch (error) {
      console.error('RealFitnessAgent: Error processing message:', error);
      return this.createResponse(
        'I apologize, but I encountered an error while processing your fitness query. Please try again.',
        [],
        []
      );
    }
  }

  /**
   * Generate specialized fitness response with AI
   */
  private async generateFitnessResponse(
    message: string, 
    memories: any[], 
    context: AgentContext
  ): Promise<string> {
    const fitnessContext = this.buildFitnessContext(memories, context);
    
    const prompt = `
You are a professional fitness and wellness specialist AI with expertise in:
- Personalized workout planning and exercise science
- Nutrition guidance and meal planning
- Fitness goal setting and progress tracking
- Wellness coaching and lifestyle optimization
- Recovery strategies and injury prevention
- Motivation and behavioral support

Previous context: ${fitnessContext}

User query: ${message}

Provide expert fitness guidance that is:
1. Safe and appropriate for the user's level
2. Evidence-based and scientifically sound
3. Personalized to their goals and preferences
4. Actionable with clear next steps
5. Motivating and supportive

Always recommend consulting healthcare professionals for medical concerns.
`;

    return await this.generateResponse(prompt, memories);
  }
  /**
   * Build fitness-specific context from memories
   */
  private buildFitnessContext(memories: any[], _context: AgentContext): string {
    if (!memories || memories.length === 0) {
      return "No previous fitness history available.";
    }

    const fitnessMemories = memories
      .filter(memory => 
        memory.metadata?.type === 'fitness_consultation' ||
        memory.content?.toLowerCase().includes('workout') ||
        memory.content?.toLowerCase().includes('exercise') ||
        memory.content?.toLowerCase().includes('nutrition')
      )
      .slice(0, 3);

    if (fitnessMemories.length === 0) {
      return "No relevant fitness history found.";
    }

    return fitnessMemories
      .map(memory => `Previous: ${memory.content?.substring(0, 200)}...`)
      .join('\n');
  }

  /**
   * Categorize the fitness query for better memory organization
   */
  private categorizeQuery(message: string): string {
    const messageLower = message.toLowerCase();
    
    if (messageLower.includes('workout') || messageLower.includes('exercise') || messageLower.includes('training')) {
      return 'workout_planning';
    }
    if (messageLower.includes('nutrition') || messageLower.includes('diet') || messageLower.includes('meal')) {
      return 'nutrition_guidance';
    }
    if (messageLower.includes('goal') || messageLower.includes('target')) {
      return 'goal_setting';
    }
    if (messageLower.includes('progress') || messageLower.includes('track')) {
      return 'progress_tracking';
    }
    if (messageLower.includes('recovery') || messageLower.includes('rest') || messageLower.includes('sleep')) {
      return 'recovery_advice';
    }
    if (messageLower.includes('motivation') || messageLower.includes('support')) {
      return 'motivation_support';
    }
    
    return 'general_wellness';
  }

  /**
   * Create enhanced prompt configuration for fitness expertise
   */
  private static createFitnessPromptConfig(): EnhancedPromptConfig {
    return {      agentPersona: FitnessAgent.createFitnessPersona(),
      constitutionalPrinciples: FitnessAgent.createFitnessConstitutionalPrinciples(),
      enabledFrameworks: ['RTF', 'TAG', 'CARE'], // Reasoning, Task, Goals + Care framework
      enableCoVe: true,   // Enable verification for safety-critical fitness advice
      enableRAG: true,    // Use relevant memory context
      qualityThreshold: 88 // High standard for fitness/health advice
    };
  }

  /**
