        storage: {
          cached: false,
          persistToMemory: false,
          ttl: this.config.storage.defaultTTL,
          accessCount: 1,
          lastAccessed: new Date().toISOString(),
          compressed: false
        }
      };

      // Determine storage strategy
      if (finding.classification.importance >= this.config.classification.importanceThreshold) {
