  private setupCleanupInterval(): void {
    if (this.config.storage.autoCleanupInterval > 0) {
      setInterval(() => {
        this.cleanupFindings().catch(console.error);
      }, this.config.storage.autoCleanupInterval);
    }
  }

  private generateFindingId(type: 'search' | 'fetch', input: string): string {
    const hash = crypto.createHash('sha256').update(input + Date.now()).digest('hex');
    return `${type}_${hash.substring(0, 16)}`;
  }

  private hashQuery(query: string): string {
    return crypto.createHash('md5').update(query.toLowerCase().trim()).digest('hex');
  }

  private hashUrl(url: string): string {
    try {
      const urlObj = new URL(url);
      const normalizedUrl = `${urlObj.protocol}//${urlObj.hostname}${urlObj.pathname}`;
      return crypto.createHash('md5').update(normalizedUrl).digest('hex');
    } catch {
      return crypto.createHash('md5').update(url).digest('hex');
    }
  }

  private async classifySearchFinding(query: string, response: BraveSearchResponse): Promise<WebSearchFinding['classification']> {
    // Basic classification based on query patterns
    let category: WebSearchFinding['classification']['category'] = 'general';
    let importance = 0.5;
    const tags: string[] = [];

    const queryLower = query.toLowerCase();

    // Category detection
    if (queryLower.includes('documentation') || queryLower.includes('docs') || queryLower.includes('api')) {
      category = 'documentation';
      importance += 0.2;
      tags.push('documentation');
    } else if (queryLower.includes('error') || queryLower.includes('troubleshoot') || queryLower.includes('fix')) {
      category = 'troubleshooting';
      importance += 0.15;
      tags.push('troubleshooting');
    } else if (queryLower.includes('tutorial') || queryLower.includes('guide') || queryLower.includes('how to')) {
      category = 'research';
      importance += 0.1;
      tags.push('learning');
    }

    // DevAgent relevance boost
    if (queryLower.includes('typescript') || queryLower.includes('react') || queryLower.includes('node') || 
        queryLower.includes('development') || queryLower.includes('programming')) {
      category = 'devagent';
      importance *= this.config.classification.devAgentRelevanceBoost;
      tags.push('development');
    }

    // Quality indicators based on available response data
    const resultCount = response.web?.results?.length || 0;
    if (resultCount > 10) importance += 0.1;

    return {
      category,
      importance: Math.min(1, importance),
      relevanceScore: Math.min(1, resultCount / 20),
      tags
    };
  }

  private async classifyFetchFinding(url: string, response: WebFetchResponse, extracted: any): Promise<WebFetchFinding['classification']> {
    // Basic classification based on URL and content
    let category: WebFetchFinding['classification']['category'] = 'other';
    let importance = 0.5;
    const topics: string[] = [];

    const urlLower = url.toLowerCase();
    const textLower = extracted.keyPoints.join(' ').toLowerCase();

    // Category detection
    if (urlLower.includes('docs') || urlLower.includes('documentation')) {
      category = 'documentation';
      importance += 0.3;
      topics.push('documentation');
    } else if (urlLower.includes('api') || textLower.includes('api reference')) {
      category = 'api-reference';
      importance += 0.25;
      topics.push('api');
    } else if (urlLower.includes('tutorial') || urlLower.includes('guide')) {
      category = 'tutorial';
      importance += 0.2;
      topics.push('tutorial');
    } else if (urlLower.includes('github') || urlLower.includes('gitlab')) {
      category = 'code';
      importance += 0.15;
      topics.push('code');
    }

    // Framework detection
    let framework: string | undefined;
    if (textLower.includes('typescript')) framework = 'TypeScript';
    else if (textLower.includes('react')) framework = 'React';
    else if (textLower.includes('node.js') || textLower.includes('nodejs')) framework = 'Node.js';
    else if (textLower.includes('vue')) framework = 'Vue';

    // DevAgent relevance boost
    if (framework || textLower.includes('development') || textLower.includes('programming')) {
      importance *= this.config.classification.devAgentRelevanceBoost;
      topics.push('development');
    }

    // Quality indicators
    if (extracted.wordCount > 500) importance += 0.1;
    if (response.statusCode === 200) importance += 0.05;
    if (response.metadata?.title) importance += 0.05;

    // Build result object with proper optional property handling
    const result: WebFetchFinding['classification'] = {
      category,
      importance: Math.min(1, importance),
      relevanceScore: Math.min(1, extracted.wordCount / 2000),
      topics
    };

    if (framework) {
      result.framework = framework;
    }

    return result;
  }

  private async extractContentData(response: WebFetchResponse): Promise<WebFetchFinding['extracted']> {
    const text = response.content.text || '';
    const wordCount = text.split(/\s+/).length;

    // Extract key points (simple implementation)
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 20);
    const keyPoints = sentences.slice(0, 5).map(s => s.trim());

    // Extract links (simple implementation)
    const linkRegex = /https?:\/\/[^\s<>"]+/g;
    const links = [...(text.match(linkRegex) || [])].slice(0, 10);

    // Extract images (simple implementation) 
    const imgRegex = /\.(jpg|jpeg|png|gif|svg|webp)/gi;
    const images = [...(text.match(imgRegex) || [])].slice(0, 5);

    return {
      keyPoints,
      links,
      images,
      wordCount
    };
  }

  private async persistToMemorySystem(finding: WebSearchFinding | WebFetchFinding): Promise<void> {
    if (!this.memoryIntelligence) return;

    try {
      let content: string;
      let metadata: Record<string, any>;

      if ('query' in finding) {
        // Search finding
        content = `Web search: "${finding.query}" found ${finding.metadata.totalResults} results`;
        metadata = {
          category: 'web_search',
          query: finding.query,
          resultCount: finding.metadata.totalResults,
          classification: finding.classification.category,
          importance: finding.classification.importance
        };
      } else {
        // Fetch finding
        content = `Web content: ${finding.metadata.title || finding.url} - ${finding.extracted.keyPoints[0] || 'No summary'}`;
        metadata = {
          category: 'web_content',
          url: finding.url,
          title: finding.metadata.title,
          classification: finding.classification.category,
          importance: finding.classification.importance,
          framework: finding.classification.framework
        };
      }

      await this.memoryIntelligence.storeMemory(content, finding.metadata.userId || 'system', metadata);
      console.log(`üíæ Finding persisted to memory system: ${finding.id}`);

    } catch (error) {
      console.error('‚ùå Failed to persist to memory system:', error);
    }
  }

  private async saveToDisk(finding: WebSearchFinding | WebFetchFinding, type: 'search' | 'fetch'): Promise<void> {
    try {
      const filename = `${finding.id}.json`;
      const filepath = path.join(this.persistentPath, type, filename);
      
      await fs.mkdir(path.dirname(filepath), { recursive: true });
      await fs.writeFile(filepath, JSON.stringify(finding, null, 2));

    } catch (error) {
      console.error('‚ùå Failed to save finding to disk:', error);
    }
  }

  private async searchInCache(options: FindingsSearchOptions): Promise<(WebSearchFinding | WebFetchFinding)[]> {
    const findings: (WebSearchFinding | WebFetchFinding)[] = [];

    // Search in search cache
    for (const finding of Array.from(this.searchCache.values())) {
      if (this.matchesSearchOptions(finding, options)) {
        findings.push(finding);
      }
    }

    // Search in fetch cache
    for (const finding of Array.from(this.fetchCache.values())) {
      if (this.matchesSearchOptions(finding, options)) {
        findings.push(finding);
      }
    }

    return findings;
  }

  private async searchInPersistentStorage(options: FindingsSearchOptions): Promise<(WebSearchFinding | WebFetchFinding)[]> {
    try {
      const findings: (WebSearchFinding | WebFetchFinding)[] = [];
      
      // Search in search findings
      const searchDir = path.join(this.persistentPath, 'search');
      try {
        const searchFiles = await fs.readdir(searchDir);
        for (const filename of searchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(searchDir, filename);
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebSearchFinding = JSON.parse(content);
              if (this.matchesSearchOptions(finding, options)) {
                findings.push(finding);
              }
            } catch (error) {
              console.warn(`‚ö†Ô∏è Failed to parse search finding: ${filename}`);
            }
          }
        }
      } catch (error) {
        // Directory might not exist yet
      }

      // Search in fetch findings
      const fetchDir = path.join(this.persistentPath, 'fetch');
      try {
        const fetchFiles = await fs.readdir(fetchDir);
        for (const filename of fetchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(fetchDir, filename);
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebFetchFinding = JSON.parse(content);
              if (this.matchesSearchOptions(finding, options)) {
                findings.push(finding);
              }
            } catch (error) {
              console.warn(`‚ö†Ô∏è Failed to parse fetch finding: ${filename}`);
            }
          }
        }
      } catch (error) {
        // Directory might not exist yet
      }

      return findings;
    } catch (error) {
      console.error('‚ùå Failed to search persistent storage:', error);
      return [];
    }
  }

  private async semanticSearch(_query: string, _options: FindingsSearchOptions): Promise<(WebSearchFinding | WebFetchFinding)[]> {
    try {
      if (!this.memoryIntelligence) {
        return [];
      }

      // For now, return empty array since searchMemories method doesn't exist yet
      // TODO: Implement memory intelligence integration when searchMemories is available
      return [];
    } catch (error) {
      console.error('‚ùå Failed to perform semantic search:', error);
      return [];
    }
  }

  private matchesSearchOptions(finding: WebSearchFinding | WebFetchFinding, options: FindingsSearchOptions): boolean {
    // Basic matching logic
    if (options.query) {
      const query = options.query.toLowerCase();
      if ('query' in finding) {
        if (!finding.query.toLowerCase().includes(query)) return false;
      } else {
        if (!finding.url.toLowerCase().includes(query) && 
            !finding.metadata.title?.toLowerCase().includes(query)) return false;
      }
    }

    if (options.category && finding.classification.category !== options.category) {
      return false;
    }

    if (options.userId && finding.metadata.userId !== options.userId) {
      return false;
    }

    return true;
  }

  private sortFindings(
    findings: (WebSearchFinding | WebFetchFinding)[], 
    sortBy?: string, 
    sortOrder: 'asc' | 'desc' = 'desc'
  ): (WebSearchFinding | WebFetchFinding)[] {
    const multiplier = sortOrder === 'desc' ? -1 : 1;

    return findings.sort((a, b) => {
      switch (sortBy) {
        case 'importance':
          return (a.classification.importance - b.classification.importance) * multiplier;
        case 'date':
          return (new Date(a.metadata.timestamp).getTime() - new Date(b.metadata.timestamp).getTime()) * multiplier;
        case 'access_count':
          return (a.storage.accessCount - b.storage.accessCount) * multiplier;
        default:
          return (a.classification.relevanceScore - b.classification.relevanceScore) * multiplier;
      }
    });
  }

  private calculateCacheSize(): { sizeInMB: number; oldestEntry: string; newestEntry: string } {
    let totalSize = 0;
    let oldestTime = Date.now();
    let newestTime = 0;

    // Calculate search cache size
    for (const finding of Array.from(this.searchCache.values())) {
      const findingSize = JSON.stringify(finding).length;
      totalSize += findingSize;
      
      const timestamp = new Date(finding.metadata.timestamp).getTime();
      oldestTime = Math.min(oldestTime, timestamp);
      newestTime = Math.max(newestTime, timestamp);
    }

    // Calculate fetch cache size
    for (const finding of Array.from(this.fetchCache.values())) {
      const findingSize = JSON.stringify(finding).length;
      totalSize += findingSize;
      
      const timestamp = new Date(finding.metadata.timestamp).getTime();
      oldestTime = Math.min(oldestTime, timestamp);
      newestTime = Math.max(newestTime, timestamp);
    }

    return {
      sizeInMB: totalSize / (1024 * 1024),
      oldestEntry: new Date(oldestTime).toISOString(),
      newestEntry: new Date(newestTime).toISOString()
    };
  }

  private async calculatePersistentStats(): Promise<FindingsStorageStats['persistent']> {
    try {
      let totalFindings = 0;
      let searchFindings = 0;
      let fetchFindings = 0;
      let totalImportance = 0;
      let totalSize = 0;

      // Count search findings
      const searchDir = path.join(this.persistentPath, 'search');
      try {
        const searchFiles = await fs.readdir(searchDir);
        for (const filename of searchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(searchDir, filename);
              const stats = await fs.stat(filepath);
              totalSize += stats.size;
              
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebSearchFinding = JSON.parse(content);
              searchFindings++;
              totalImportance += finding.classification.importance;
            } catch (error) {
              // Skip corrupted files
            }
          }
        }
      } catch (error) {
        // Directory might not exist
      }

      // Count fetch findings
      const fetchDir = path.join(this.persistentPath, 'fetch');
      try {
        const fetchFiles = await fs.readdir(fetchDir);
        for (const filename of fetchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(fetchDir, filename);
              const stats = await fs.stat(filepath);
              totalSize += stats.size;
              
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebFetchFinding = JSON.parse(content);
              fetchFindings++;
              totalImportance += finding.classification.importance;
            } catch (error) {
              // Skip corrupted files
            }
          }
        }
      } catch (error) {
        // Directory might not exist
      }

      totalFindings = searchFindings + fetchFindings;

      return {
        totalFindings,
        searchFindings,
        fetchFindings,
        avgImportance: totalFindings > 0 ? totalImportance / totalFindings : 0,
        storageSize: totalSize / (1024 * 1024) // Convert to MB
      };
    } catch (error) {
      console.error('‚ùå Failed to calculate persistent stats:', error);
      return {
        totalFindings: 0,
        searchFindings: 0,
        fetchFindings: 0,
        avgImportance: 0,
        storageSize: 0
      };
    }
  }

  private async cleanupCache(): Promise<{ expired: number; lowImportance: number }> {
    let expired = 0;
    let lowImportance = 0;
    const now = Date.now();

    // Clean search cache
    for (const [key, finding] of Array.from(this.searchCache.entries())) {
      const age = now - new Date(finding.storage.lastAccessed).getTime();
      if (age > finding.storage.ttl) {
        this.searchCache.delete(key);
        expired++;
      } else if (finding.classification.importance < 0.3) {
        this.searchCache.delete(key);
        lowImportance++;
      }
    }

    // Clean fetch cache
    for (const [key, finding] of Array.from(this.fetchCache.entries())) {
      const age = now - new Date(finding.storage.lastAccessed).getTime();
      if (age > finding.storage.ttl) {
        this.fetchCache.delete(key);
        expired++;
      } else if (finding.classification.importance < 0.3) {
        this.fetchCache.delete(key);
        lowImportance++;
      }
    }

    return { expired, lowImportance };
  }

  private async cleanupPersistentStorage(): Promise<{ expired: number; lowImportance: number; duplicates: number; spaceSaved: number }> {
    let expired = 0;
    let lowImportance = 0;
    let duplicates = 0;
    let spaceSaved = 0;
    const now = Date.now();

    try {
      // Cleanup search findings
      const searchDir = path.join(this.persistentPath, 'search');
      try {
        const searchFiles = await fs.readdir(searchDir);
        for (const filename of searchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(searchDir, filename);
              const stats = await fs.stat(filepath);
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebSearchFinding = JSON.parse(content);
              
              const age = now - new Date(finding.storage.lastAccessed).getTime();
              const shouldDelete = age > finding.storage.ttl || 
                                 finding.classification.importance < 0.3;
              
              if (shouldDelete) {
                await fs.unlink(filepath);
                spaceSaved += stats.size;
                
                if (age > finding.storage.ttl) expired++;
                else lowImportance++;
              }
            } catch (error) {
              // Skip corrupted files
            }
          }
        }
      } catch (error) {
        // Directory might not exist
      }

      // Cleanup fetch findings
      const fetchDir = path.join(this.persistentPath, 'fetch');
      try {
        const fetchFiles = await fs.readdir(fetchDir);
        for (const filename of fetchFiles) {
          if (filename.endsWith('.json')) {
            try {
              const filepath = path.join(fetchDir, filename);
              const stats = await fs.stat(filepath);
              const content = await fs.readFile(filepath, 'utf-8');
              const finding: WebFetchFinding = JSON.parse(content);
              
              const age = now - new Date(finding.storage.lastAccessed).getTime();
              const shouldDelete = age > finding.storage.ttl || 
                                 finding.classification.importance < 0.3;
              
              if (shouldDelete) {
                await fs.unlink(filepath);
                spaceSaved += stats.size;
                
                if (age > finding.storage.ttl) expired++;
                else lowImportance++;
              }
            } catch (error) {
              // Skip corrupted files
            }
          }
        }
      } catch (error) {
        // Directory might not exist
      }

      return { 
        expired, 
        lowImportance, 
        duplicates, 
        spaceSaved: spaceSaved / (1024 * 1024) // Convert to MB
      };
    } catch (error) {
      console.error('‚ùå Failed to cleanup persistent storage:', error);
      return { expired: 0, lowImportance: 0, duplicates: 0, spaceSaved: 0 };
    }
  }

  /**
   * Extract domain from URL for citation purposes (simple, non-overengineered)
   */
  private extractDomain(url: string): string {
    try {
      const urlObj = new URL(url);
      return urlObj.hostname;
    } catch (error) {
      // If URL parsing fails, return the URL as-is
      return url;
    }
  }
}

/**
 * Context7 MCP Integration for OneAgent
 * 
 * Web Development Documentation Tool
 * - Retrieves latest documentation and patterns for coding tasks and languages
 * - Stores findings in mem0 memory to build OneAgent's collective knowledge
 * - Focuses on web technologies: JavaScript, TypeScript, Node.js, React, etc.
 * - Enables OneAgent to accumulate web development expertise over time
 */

import { LocalMCPAdapter, MCPServerConfig } from './adapter';

export interface WebDevelopmentSource {
  name: string;
  type: 'web-framework' | 'programming-language' | 'web-api' | 'build-tool' | 'web-library';
  officialDocsUrl?: string;
  version?: string;
  lastUpdated?: Date;
  priority: number; // Higher priority for more commonly used web technologies
}

export interface WebDocumentationQuery {
  technology: string; // e.g., 'nodejs', 'typescript', 'react', 'express'
  topic: string; // e.g., 'async patterns', 'type definitions', 'hooks'
  version?: string;
  context?: string;
  maxResults?: number;
}

export interface WebDocumentationResult {
  technology: string;
  topic: string;
  title: string;
  content: string;
  sourceUrl: string | undefined;
  version: string | undefined;
  relevanceScore: number;
  bestPractices: string[] | undefined;
  codeExamples: string[] | undefined;
  storageTimestamp: Date;
}

export interface CacheMetrics {
  totalQueries: number;
  cacheHits: number;
  cacheMisses: number;
  averageResponseTime: number;
  lastCacheCleanup: Date;
}

/**
 * Context7 MCP Web Development Documentation Integration
 */
export class Context7MCPIntegration {
  private mcpAdapter: LocalMCPAdapter;
  private documentationCache: Map<string, WebDocumentationResult[]> = new Map();
  private sourceConfigs: Map<string, WebDevelopmentSource> = new Map();
  private cacheMetrics: CacheMetrics;

  constructor() {
    const mcpConfig: MCPServerConfig = {
      name: 'context7-web-docs',
      type: 'local',
      port: 3002
    };
    
    this.mcpAdapter = new LocalMCPAdapter(mcpConfig);
    
    this.cacheMetrics = {
      totalQueries: 0,
      cacheHits: 0,
      cacheMisses: 0,
      averageResponseTime: 0,
      lastCacheCleanup: new Date()
    };

    this.initializeWebDevelopmentSources();
  }

  /**
   * Query web development documentation and store in mem0 for collective learning
   */
  async queryWebDocumentation(query: WebDocumentationQuery): Promise<WebDocumentationResult[]> {
    const startTime = Date.now();
    this.cacheMetrics.totalQueries++;

    try {
      const cacheKey = this.generateCacheKey(query);
      const cachedResults = this.documentationCache.get(cacheKey);
      
      if (cachedResults) {
        this.cacheMetrics.cacheHits++;
        this.updateMetrics(Date.now() - startTime);
        // Store cached access in mem0 for learning patterns
        await this.storeInMem0(query, cachedResults, 'cache-hit');
        return cachedResults;
      }

      this.cacheMetrics.cacheMisses++;
      const results = await this.queryWebDevelopmentSources(query);
      this.documentationCache.set(cacheKey, results);
      this.updateMetrics(Date.now() - startTime);
      
      // Store new findings in mem0 for collective learning
      await this.storeInMem0(query, results, 'new-discovery');
      
      return results;
    } catch (error) {
      console.error('[Context7MCP] Web documentation query failed:', error);
      return this.getFallbackWebDocumentation(query);
    }
  }

  /**
   * Store documentation findings in mem0 for OneAgent's collective learning
   */
  private async storeInMem0(query: WebDocumentationQuery, results: WebDocumentationResult[], accessType: 'cache-hit' | 'new-discovery'): Promise<void> {
    try {
      // This is where we would integrate with OneAgent's mem0 memory system
      // to store web development knowledge for collective learning
      const memoryEntry = {
        type: 'web-development-documentation',
        technology: query.technology,
        topic: query.topic,
        version: query.version,
        findings: results.map(r => ({
          title: r.title,
          content: r.content.substring(0, 1000), // Truncate for storage
          bestPractices: r.bestPractices,
          codeExamples: r.codeExamples,
          sourceUrl: r.sourceUrl,
          relevanceScore: r.relevanceScore
        })),
        context: query.context,
        accessType,
        timestamp: new Date().toISOString(),
        tags: [
          `TECH-${query.technology.toLowerCase()}`,
          `TOPIC-${query.topic.toLowerCase().replace(/\s+/g, '-')}`,
          query.version ? `VERSION-${query.version}` : null,
          'WEB-DEVELOPMENT',
          'CONTEXT7-DISCOVERY'
        ].filter(Boolean)
      };

      // TODO: Integrate with OneAgent's mem0 memory system
      // await oneAgentMemory.store(memoryEntry);
      
      console.log(`[Context7MCP] Stored web documentation in mem0: ${query.technology}/${query.topic} (${accessType})`);
    } catch (error) {
      console.error('[Context7MCP] Failed to store in mem0:', error);
    }
  }

  private async queryWebDevelopmentSources(query: WebDocumentationQuery): Promise<WebDocumentationResult[]> {
    const results: WebDocumentationResult[] = [];
    
    // Query specific technology source if configured
    if (this.sourceConfigs.has(query.technology)) {
      const sourceResults = await this.querySpecificWebSource(query.technology, query);
      results.push(...sourceResults);
    }

    // Query general web development sources
    for (const [sourceName, source] of Array.from(this.sourceConfigs.entries())) {
      if (sourceName !== query.technology && source.priority > 0.5) {
        const sourceResults = await this.querySpecificWebSource(sourceName, query);
        results.push(...sourceResults);
      }
    }

    return results
      .sort((a, b) => b.relevanceScore - a.relevanceScore)
      .slice(0, query.maxResults || 10);
  }

  private async querySpecificWebSource(sourceName: string, query: WebDocumentationQuery): Promise<WebDocumentationResult[]> {
    const source = this.sourceConfigs.get(sourceName);
    if (!source) return [];

    try {
      // Simulate web documentation retrieval
      // In a real implementation, this would query official documentation sites
      const mockResults: WebDocumentationResult[] = [
        {
          technology: query.technology,
          topic: query.topic,
          title: `${query.technology} - ${query.topic}`,
          content: `Latest documentation for ${query.technology} ${query.topic}. This would contain the actual documentation content retrieved from ${source.officialDocsUrl}.`,
          sourceUrl: source.officialDocsUrl,
          version: query.version || source.version || 'latest',
          relevanceScore: 0.9,
          bestPractices: [
            'Follow official documentation patterns',
            'Use TypeScript for better type safety',
            'Implement proper error handling'
          ],
          codeExamples: [
            `// Example ${query.technology} code for ${query.topic}`,
            `console.log('${query.technology} best practice example');`
          ],
          storageTimestamp: new Date()
        }
      ];

      return mockResults;
      
    } catch (error) {
      console.error(`[Context7MCP] Failed to query ${sourceName}:`, error);
      return [];
    }
  }

  /**
   * Initialize web development documentation sources
   */
  private initializeWebDevelopmentSources(): void {
    const webSources: Record<string, WebDevelopmentSource> = {
      'nodejs': {
        name: 'Node.js',
        type: 'programming-language',
        officialDocsUrl: 'https://nodejs.org/docs/latest/api/',
        version: '22.0.0',
        priority: 1.0,
        lastUpdated: new Date()
      },
      'typescript': {
        name: 'TypeScript',
        type: 'programming-language',
        officialDocsUrl: 'https://www.typescriptlang.org/docs/',
        version: '5.7.0',
        priority: 1.0,
        lastUpdated: new Date()
      },
      'react': {
        name: 'React',
        type: 'web-framework',
        officialDocsUrl: 'https://react.dev/',
        version: '18.0.0',
        priority: 0.9,
        lastUpdated: new Date()
      },
      'express': {
        name: 'Express.js',
        type: 'web-framework',
        officialDocsUrl: 'https://expressjs.com/',
        version: '4.18.0',
        priority: 0.8,
        lastUpdated: new Date()
      },
      'vite': {
        name: 'Vite',
        type: 'build-tool',
        officialDocsUrl: 'https://vitejs.dev/',
        version: '5.0.0',
        priority: 0.7,
        lastUpdated: new Date()
      },
      'nextjs': {
        name: 'Next.js',
        type: 'web-framework',
        officialDocsUrl: 'https://nextjs.org/docs',
        version: '14.0.0',
        priority: 0.8,
        lastUpdated: new Date()
      }
    };

    // Load all web development sources
    for (const [key, source] of Object.entries(webSources)) {
      this.sourceConfigs.set(key, source);
    }

    console.log(`[Context7MCP] Initialized ${this.sourceConfigs.size} web development sources`);
  }

  private getFallbackWebDocumentation(query: WebDocumentationQuery): WebDocumentationResult[] {
    return [
      {
        technology: query.technology,
        topic: query.topic,
        title: `Fallback: ${query.technology} ${query.topic}`,
        content: `Fallback documentation for ${query.technology} ${query.topic}. Check official documentation at the technology's website.`,
        sourceUrl: `https://www.google.com/search?q=${query.technology}+${query.topic}+documentation`,
        version: 'unknown',
        relevanceScore: 0.3,
        bestPractices: ['Consult official documentation', 'Verify version compatibility'],
        codeExamples: [],
        storageTimestamp: new Date()
      }
    ];
  }

  private generateCacheKey(query: WebDocumentationQuery): string {
    return `${query.technology}:${query.topic}:${query.version || 'latest'}:${query.context || ''}`;
  }

  private updateMetrics(responseTime: number): void {
    const total = this.cacheMetrics.totalQueries;
    this.cacheMetrics.averageResponseTime = 
      ((this.cacheMetrics.averageResponseTime * (total - 1)) + responseTime) / total;
  }

  private async cleanupCache(): Promise<void> {
    const maxCacheAge = 3600000; // 1 hour
    const now = Date.now();
    
    for (const [key, results] of Array.from(this.documentationCache.entries())) {
      // Check if any result is older than max age
      const shouldCleanup = results.some(result => 
        (now - result.storageTimestamp.getTime()) > maxCacheAge
      );
      
      if (shouldCleanup) {
        this.documentationCache.delete(key);
      }
    }
    
    this.cacheMetrics.lastCacheCleanup = new Date();
  }

  /**
   * Add or update a web development source
   */
  addWebDevelopmentSource(name: string, source: WebDevelopmentSource): void {
    this.sourceConfigs.set(name, {
      ...source,
      lastUpdated: new Date()
    });
    console.log(`[Context7MCP] Added web development source: ${name}`);
  }

  /**
   * Refresh cache for a specific web technology
   */
  async refreshSourceCache(sourceName: string): Promise<void> {
    // Clear cache entries for this source
    for (const [key, results] of Array.from(this.documentationCache.entries())) {
      const filteredResults = results.filter(result => result.technology !== sourceName);
      if (filteredResults.length !== results.length) {
        if (filteredResults.length === 0) {
          this.documentationCache.delete(key);
        } else {
          this.documentationCache.set(key, filteredResults);
        }
      }
    }
    
    console.log(`[Context7MCP] Refreshed cache for web technology: ${sourceName}`);
  }

  /**
   * Get available web development sources
   */
  getAvailableWebSources(): WebDevelopmentSource[] {
    return Array.from(this.sourceConfigs.values());
  }

  /**
   * Get cache metrics for monitoring
   */
  getCacheMetrics(): CacheMetrics {
    return { ...this.cacheMetrics };
  }

  /**
   * Clear all cached documentation
   */
  clearCache(): void {
    this.documentationCache.clear();
    console.log('[Context7MCP] Web documentation cache cleared');
  }
}

