/**
 * Main MCP endpoint
 */
app.post('/mcp', async (req: any, res: any) => {
  try {
    // Reject batch requests (array input)
    if (Array.isArray(req.body)) {
      res.status(400).json({
        jsonrpc: '2.0',
        id: null,
        error: {
          code: -32600,
          message: 'Batch requests are not supported in MCP 2025-06-18.'
        }
      });
      return;
    }
    const mcpRequest: MCPRequest = req.body;
    
    // Validate MCP request format
    if (!mcpRequest.jsonrpc || mcpRequest.jsonrpc !== '2.0') {
      res.status(400).json({
        jsonrpc: '2.0',
        id: mcpRequest.id || null,
        error: {
          code: -32600,
          message: 'Invalid Request: missing or invalid jsonrpc field'
        }
      });
      return;
    }
    
    if (!mcpRequest.method) {
      res.status(400).json({
        jsonrpc: '2.0',
        id: mcpRequest.id || null,
        error: {
          code: -32600,
          message: 'Invalid Request: missing method field'
        }
      });
      return;
    }
    
    console.log(`üì• MCP Request: ${mcpRequest.method} (ID: ${mcpRequest.id})`);
    
    const response = await handleMCPRequest(mcpRequest);
    // Add protocol version header
    res.setHeader('X-MCP-Protocol-Version', MCP_PROTOCOL_VERSION);
    res.json(response);
    
  } catch (error) {
    console.error('‚ùå MCP endpoint error:', error);
    
    res.status(500).json({
      jsonrpc: '2.0',
      id: req.body?.id || null,
      error: {
        code: -32603,
        message: 'Internal error',
        data: { timestamp: new Date().toISOString() }
      }
    });
  }
});

// Enhance SSE endpoint for MCP notifications
app.get('/mcp', (req: any, res: any) => {
  res.setHeader('Content-Type', 'text/event-stream');
  res.setHeader('Cache-Control', 'no-cache');
  res.setHeader('Connection', 'keep-alive');
  res.flushHeaders(); // Flush headers to open the connection

  // Add to clients list
  sseClients.push(res);

  // Send initial notification
  sendSseEvent(res, 'mcpNotification', {
    jsonrpc: '2.0',
    method: 'notifications/initialized',
    params: { timestamp: new Date().toISOString() }
  });

  // Heartbeat
  const heartbeatInterval = setInterval(() => {
    res.write(':heartbeat\n\n');
  }, 15000);

  // Remove client on disconnect
  req.on('close', () => {
    clearInterval(heartbeatInterval);
    const idx = sseClients.indexOf(res);
    if (idx !== -1) sseClients.splice(idx, 1);
    console.log('Client disconnected from SSE stream');
  });

  console.log('üîó SSE stream opened for client');
  // In a real implementation, you would push events here
  // For now, this just establishes the connection.
});

