    // Add context if provided
    if (options?.context) {
      messages.push({
        role: 'user',
        parts: [{ text: `Context: ${options.context}` }]
      });
    }

    // Add user prompt
    messages.push({
      role: 'user',
      parts: [{ text: userPrompt }]
    });

    return this.generateContent(messages, options);
