  /**
   * Calculate cosine similarity between two embeddings
   */
  static calculateCosineSimilarity(embedding1: number[], embedding2: number[]): number {
    if (embedding1.length !== embedding2.length) {
      throw new Error('Embeddings must have the same dimensions');
    }

    let dotProduct = 0;
    let norm1 = 0;
    let norm2 = 0;

    for (let i = 0; i < embedding1.length; i++) {
      dotProduct += embedding1[i] * embedding2[i];
      norm1 += embedding1[i] * embedding1[i];
      norm2 += embedding2[i] * embedding2[i];
    }

    const magnitude = Math.sqrt(norm1) * Math.sqrt(norm2);
    return magnitude === 0 ? 0 : dotProduct / magnitude;
  }

  /**
   * Find most similar texts using embeddings
   */
  async findSimilarTexts(
    queryText: string, 
    candidateTexts: string[], 
    options?: EmbeddingOptions & { topK?: number }
  ): Promise<Array<{ text: string; similarity: number; index: number }>> {
    console.log(`üîç Finding similar texts for query: "${queryText.substring(0, 50)}..."`);

    // Generate embedding for query
    const queryEmbedding = await this.generateEmbedding(queryText, {
      ...options,
      taskType: 'RETRIEVAL_QUERY'
    });

    // Generate embeddings for candidates
    const candidateEmbeddings = await this.generateEmbeddingBatch(candidateTexts, {
      ...options,
      taskType: 'RETRIEVAL_DOCUMENT'
    });

    // Calculate similarities
    const similarities = candidateEmbeddings.map((candidate, index) => ({
      text: candidate.text,
      similarity: GeminiClient.calculateCosineSimilarity(queryEmbedding.embedding, candidate.embedding),
      index
    }));

    // Sort by similarity (highest first)
    similarities.sort((a, b) => b.similarity - a.similarity);

    // Return top K results
    const topK = options?.topK || similarities.length;
    const results = similarities.slice(0, topK);

    console.log(`üîç Found ${results.length} similar texts, top similarity: ${results[0]?.similarity.toFixed(4) || 0}`);
    return results;
  }

  /**
   * Mock embedding generation for development/testing
   */
  private mockGenerateEmbedding(text: string, options?: EmbeddingOptions): EmbeddingResult {
    console.log(`üî¢ Mock embedding generation for: "${text.substring(0, 50)}..."`);
    
    // Generate a realistic mock embedding (384 dimensions like text-embedding-004)
    const dimensions = 384;
    const embedding = Array.from({ length: dimensions }, () => Math.random() * 2 - 1);
    
    // Normalize the embedding to unit length (common practice)
    const magnitude = Math.sqrt(embedding.reduce((sum, val) => sum + val * val, 0));
    const normalizedEmbedding = embedding.map(val => val / magnitude);

    return {
      embedding: normalizedEmbedding,
      text,
      taskType: options?.taskType,
      dimensions,
      timestamp: new Date().toISOString()
    };
  }
}

/**
 * Gemini Embeddings Tool for OneAgent
 * 
 * Provides semantic search, similarity matching, and embedding-based
 * memory enhancement for the OneAgent system using Google Gemini embeddings.
 * Updated to use UnifiedMemoryClient.
 */

import { GeminiClient } from './geminiClient';
import { OneAgentMemory, OneAgentMemoryConfig } from '../memory/OneAgentMemory';
import { EmbeddingOptions, EmbeddingResult, EmbeddingTaskType } from '../types/gemini';
import { globalProfiler } from '../performance/profiler';

export interface SemanticSearchOptions {
  taskType?: EmbeddingTaskType;
  topK?: number;
  similarityThreshold?: number;
  model?: 'text-embedding-004' | 'embedding-001' | 'gemini-embedding-exp-03-07';
}

export interface MemoryEmbeddingOptions extends SemanticSearchOptions {
  workflowId?: string;
  sessionId?: string;
  memoryType?: 'short_term' | 'long_term' | 'workflow' | 'session';
}

export interface SemanticSearchResult {
  memory: {
    id: string;
    type: string;
    content: string;
    agentId: string;
    relevanceScore: number;
    timestamp: Date;
    metadata?: Record<string, any>;
    summary?: string;
  };
  similarity: number;
  embeddingResult?: EmbeddingResult;
}

export interface EmbeddingAnalytics {
  totalMemories: number;
  searchResults: number;
  averageSimilarity: number;
  topSimilarity: number;
  processingTime: number;
}

/**
 * Gemini Embeddings Tool
 * Integrates Gemini embeddings with UnifiedMemoryClient for semantic operations
 */
export class GeminiEmbeddingsTool {
  private geminiClient: GeminiClient;
  private memorySystem: OneAgentMemory;
  private embeddingCache: Map<string, EmbeddingResult> = new Map();

  constructor(geminiClient: GeminiClient, memorySystem?: OneAgentMemory) {
    this.geminiClient = geminiClient;
    if (memorySystem) {
      this.memorySystem = memorySystem;
    } else {
      const memoryConfig: OneAgentMemoryConfig = {
        apiKey: process.env.MEM0_API_KEY || 'demo-key',
        apiUrl: process.env.MEM0_API_URL
      };
      this.memorySystem = new OneAgentMemory(memoryConfig);
    }
    console.log('üî¢ GeminiEmbeddingsTool initialized with canonical OneAgentMemory');
  }

  /**
   * Perform semantic search across memories using canonical memory system
   */
  async semanticSearch(
    query: string,
    options?: MemoryEmbeddingOptions
  ): Promise<{ results: SemanticSearchResult[]; analytics: EmbeddingAnalytics }> {
    const startTime = Date.now();
    const operationId = `semantic-search-${Date.now()}`;
    try {
      globalProfiler.startOperation(operationId, 'semantic-search');
      // If mem0 supports embedding-based search, delegate to it:
      const searchResults = await this.memorySystem.searchMemory({
        type: 'conversations',
        query,
        topK: options?.topK || 10,
        similarityThreshold: options?.similarityThreshold || 0.1,
        embeddingModel: options?.model || 'gemini-embedding-exp-03-07',
        semanticSearch: true
      });
      // Map results to SemanticSearchResult format
      const results: SemanticSearchResult[] = (searchResults?.results || []).map((memory: any) => ({
        memory: {
          id: memory.id,
          type: memory.type || 'conversation',
          content: memory.content,
          agentId: memory.agentId || 'default',
          relevanceScore: memory.similarity || memory.relevanceScore || 0,
          timestamp: memory.timestamp ? new Date(memory.timestamp) : new Date(),
          metadata: memory.metadata || {},
          summary: memory.summary || undefined
        },
        similarity: memory.similarity || memory.relevanceScore || 0,
        embeddingResult: memory.embeddingResult || undefined
      }));
      const analytics: EmbeddingAnalytics = {
        totalMemories: searchResults?.total || results.length,
        searchResults: results.length,
        averageSimilarity: results.length > 0 ? results.reduce((sum, r) => sum + r.similarity, 0) / results.length : 0,
        topSimilarity: results.length > 0 ? results[0].similarity : 0,
        processingTime: Date.now() - startTime
      };
      globalProfiler.endOperation(operationId, true);
      return { results, analytics };
    } catch (error) {
      globalProfiler.endOperation(operationId, false, error?.toString());
      console.error('‚ùå Semantic search failed:', error);
      return {
        results: [],
        analytics: {
          totalMemories: 0,
          searchResults: 0,
          averageSimilarity: 0,
          topSimilarity: 0,
          processingTime: Date.now() - startTime
        }
      };
    }
  }

  /**
   * Store memory with embedding if required (otherwise use canonical addMemory)
   */
  async storeMemoryWithEmbedding(
    content: string,
    agentId: string,
    userId: string,
    memoryType: 'conversation' | 'learning' | 'pattern' = 'conversation',
    metadata?: Record<string, any>
  ): Promise<{ memoryId: string; embedding?: EmbeddingResult }> {
    const operationId = `store-memory-${Date.now()}`;
    try {
      globalProfiler.startOperation(operationId, 'store-memory-embedding');
      // If mem0 supports embedding, just add memory
      const memoryData: any = {
        id: `${memoryType}_${Date.now()}`,
        agentId,
        userId,
        content,
        timestamp: new Date(),
        metadata: {
          type: memoryType,
          agentId,
          ...(metadata || {})
        }
      };
      await this.memorySystem.addMemory({
        ...memoryData
      });
      globalProfiler.endOperation(operationId, true);
      return { memoryId: memoryData.id };
    } catch (error) {
      globalProfiler.endOperation(operationId, false, error?.toString());
      console.error('‚ùå Memory storage with embedding failed:', error);
