/**
 * UUID Validation and Migration Utilities for OneAgent
 * Provides consistent UUID v4 validation and migration across the system
 */

import { randomUUID } from 'crypto';
import { isValidUUID, generateUUID } from '../types/user';

/**
 * UUID Validation Middleware for Express routes
 * Validates that userId and sessionId parameters are proper UUIDs
 */
export function validateUUIDs(req: any, res: any, next: any) {
  const { userId, sessionId } = req.params;
  const bodyUserId = req.body?.userId;
  const bodySessionId = req.body?.sessionId;
  
  // Validate URL parameters
  if (userId && !isValidUUID(userId)) {
    return res.status(400).json({
      error: 'Invalid userId format - must be UUID v4',
      received: userId
    });
  }
  
  if (sessionId && !isValidUUID(sessionId)) {
    return res.status(400).json({
      error: 'Invalid sessionId format - must be UUID v4',
      received: sessionId
    });
  }
  
  // Validate request body parameters
  if (bodyUserId && !isValidUUID(bodyUserId)) {
    return res.status(400).json({
      error: 'Invalid userId in request body - must be UUID v4',
      received: bodyUserId
    });
  }
  
  if (bodySessionId && !isValidUUID(bodySessionId)) {
    return res.status(400).json({
      error: 'Invalid sessionId in request body - must be UUID v4',
      received: bodySessionId
    });
  }
  
  next();
}

/**
 * Generate a new UUID v4 using crypto.randomUUID() if available,
 * fallback to our custom implementation
 */
export function generateSecureUUID(): string {
  try {
    return randomUUID();
  } catch (error) {
    console.warn('crypto.randomUUID() not available, using fallback implementation');
    return generateUUID();
  }
}

/**
 * UUID Migration utilities for existing data
 */
export class UUIDMigrationUtils {
  
  /**
   * Check if a string looks like a legacy user ID that needs migration
   */
  static isLegacyUserId(id: string): boolean {
    // Common patterns for legacy user IDs
    const legacyPatterns = [
      /^test[_-]?user$/i,
      /^user[_-]?\d+$/i,
      /^[a-zA-Z]+$/i, // Simple alphabetic names
      /^[a-zA-Z0-9]{1,8}$/i // Short alphanumeric IDs
    ];
    
    return !isValidUUID(id) && legacyPatterns.some(pattern => pattern.test(id));
  }
  
  /**
   * Generate a mapping from legacy ID to new UUID
   */
  static createMigrationMapping(legacyIds: string[]): Map<string, string> {
    const mapping = new Map<string, string>();
    
    for (const legacyId of legacyIds) {
      if (this.isLegacyUserId(legacyId)) {
        mapping.set(legacyId, generateSecureUUID());
      }
    }
    
    return mapping;
  }
  
  /**
   * Migrate legacy user IDs to UUIDs in data objects
   */
  static migrateLegacyUserIds(data: any, mapping: Map<string, string>): any {
    if (typeof data !== 'object' || data === null) {
      return data;
    }
    
    if (Array.isArray(data)) {
      return data.map(item => this.migrateLegacyUserIds(item, mapping));
    }
    
    const result = { ...data };
    
    // Migrate common user ID fields
    const userIdFields = ['userId', 'user_id', 'ownerId', 'createdBy'];
    
    for (const field of userIdFields) {
      if (result[field] && mapping.has(result[field])) {
        result[field] = mapping.get(result[field]);
      }
    }
    
    // Recursively migrate nested objects
    for (const key in result) {
      if (typeof result[key] === 'object') {
        result[key] = this.migrateLegacyUserIds(result[key], mapping);
      }
    }
    
    return result;
  }
}

/**
 * Session ID validation and generation utilities
 */
export class SessionManager {
  
  /**
   * Generate a new session ID with proper UUID v4 format
   */
  static generateSessionId(): string {
    return generateSecureUUID();
  }
  
  /**
   * Validate session ID format
   */
  static isValidSessionId(sessionId: string): boolean {
    return isValidUUID(sessionId);
  }
  
  /**
   * Create session metadata with proper UUID validation
   */
  static createSessionMetadata(userId: string, sessionId?: string) {
    if (!isValidUUID(userId)) {
      throw new Error(`Invalid userId format: ${userId}. Must be UUID v4.`);
    }
    
    const finalSessionId = sessionId || this.generateSessionId();
    
    if (!this.isValidSessionId(finalSessionId)) {
      throw new Error(`Invalid sessionId format: ${finalSessionId}. Must be UUID v4.`);
    }
    
    return {
      userId,
      sessionId: finalSessionId,
      createdAt: new Date().toISOString(),
      lastActivity: new Date().toISOString()
    };
  }
}

/**
 * Type guards for UUID validation
 */
export function assertValidUUID(value: string, fieldName = 'ID'): asserts value is string {
  if (!isValidUUID(value)) {
    throw new Error(`Invalid ${fieldName} format: ${value}. Must be UUID v4.`);
  }
}

export function assertValidUserId(userId: string): asserts userId is string {
  assertValidUUID(userId, 'userId');
}

export function assertValidSessionId(sessionId: string): asserts sessionId is string {
  assertValidUUID(sessionId, 'sessionId');
}

/**
 * Constitutional Validator Implementation - ALITA Phase 1
 * 
 * Purpose: Validate content against Constitutional AI principles
 * Why: Safety and compliance are foundational requirements
 * 
 * @version 1.0.0
 * @date 2025-06-15
 */

export interface ConstitutionalResult {
  passed: boolean;
  score: number;
  violations: string[];
}

export interface PrivacyAssessment {
  passed: boolean;
  riskLevel: string;
  sensitiveDataDetected: boolean;
}

export interface ValidationResult {
  passed: boolean;
  reason?: string;
}

/**
 * Constitutional Validator
 * WHY: Ensures all AI interactions meet safety and ethical standards
 */
export class ConstitutionalValidator {
  
  /**
   * Validate content against Constitutional AI principles
   * WHY: Every interaction must meet safety standards
   */
  async validate(content: string): Promise<ConstitutionalResult> {
    const violations: string[] = [];
    let score = 100;

    // Accuracy Check - Look for speculation or uncertain statements
    if (this.containsSpeculation(content)) {
      violations.push('accuracy - contains speculation');
      score -= 20;
    }    // Transparency Check - Look for reasoning explanations
    if (!this.containsReasoning(content)) {
      violations.push('transparency - lacks reasoning explanation');
      score -= 15; // Reduced penalty for helpful content
    }

    // Helpfulness Check - Look for actionable content
    if (!this.isActionable(content)) {
      violations.push('helpfulness - lacks actionable guidance');
      score -= 20;
    }

    // Safety Check - Look for harmful content
    if (this.containsHarmfulContent(content)) {
      violations.push('safety - contains potentially harmful content');
      score -= 35;
    }

    return {
      passed: violations.length === 0,
      score: Math.max(score, 0),
      violations
    };
  }

  /**
   * Assess privacy implications of content
   * WHY: Privacy protection is constitutionally required
   */
  async assessPrivacy(content: string): Promise<PrivacyAssessment> {
    const sensitivePatterns = [
      /\b\d{3}-\d{2}-\d{4}\b/, // SSN
      /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/, // Email
      /password|secret|confidential/i,
      /\b\d{16}\b/, // Credit card
      /api[_\s]?key|token|credential/i
    ];

    const sensitiveDataDetected = sensitivePatterns.some(pattern => pattern.test(content));
    const riskLevel = sensitiveDataDetected ? 'high' : 'low';

    return {
      passed: !sensitiveDataDetected,
      riskLevel,
      sensitiveDataDetected
    };
  }

  /**
   * Validate pattern for ALITA evolution
   * WHY: Evolution patterns must be safe and beneficial
   */
  async validatePattern(pattern: any): Promise<ValidationResult> {
    try {
      // Check if pattern promotes Constitutional AI principles
      if (!pattern.constitutionalCompliant) {
        return {
          passed: false,
          reason: 'Pattern does not meet Constitutional AI standards'
        };
      }

      // Verify pattern has positive impact
      if (pattern.userSatisfactionScore < 0.7) {
        return {
          passed: false,
          reason: 'Pattern does not show sufficient user satisfaction'
        };
      }

      return { passed: true };
    } catch (error) {
      return {
        passed: false,
        reason: `Pattern validation error: ${error instanceof Error ? error.message : 'Unknown error'}`
      };
    }
  }

  // Private validation methods
  private containsSpeculation(content: string): boolean {
    const speculationWords = /maybe|perhaps|possibly|might|could be|i think|probably/i;
    return speculationWords.test(content);
  }

  private containsReasoning(content: string): boolean {
    const reasoningWords = /because|the reason|this is due to|since|therefore|as a result/i;
    return reasoningWords.test(content);
  }

  private isActionable(content: string): boolean {
    const actionableWords = /can|should|will|here's how|follow these steps|you can|try/i;
    return actionableWords.test(content) || content.includes('```') || content.includes('1.');
  }

  private containsHarmfulContent(content: string): boolean {
    const harmfulPatterns = [
      /kill|murder|suicide|bomb|weapon/i,
      /hack|crack|steal|illegal/i,
      /hate|discrimination|bias/i
    ];
    return harmfulPatterns.some(pattern => pattern.test(content));
  }
}

/**
 * RequestValidator - Basic format and size validation for OneAgent
 * Part of Level 2.5 Security Foundation (Phase 1a)
 * 
 * Provides lightweight, performance-aware validation without blocking core functionality.
 */

import { IRequest } from '../types/conversation';

export interface ValidationResult {
  isValid: boolean;
  errors: string[];
  warnings: string[];
}

export interface ValidationConfig {
  maxRequestSize: number;
  maxPromptLength: number;
  allowedAgentTypes: string[];
  requiredFields: string[];
}

export class RequestValidator {
  private config: ValidationConfig;

  constructor(config?: Partial<ValidationConfig>) {
    this.config = {
      maxRequestSize: 10 * 1024 * 1024, // 10MB
      maxPromptLength: 100000, // 100k characters
      allowedAgentTypes: [
        'research',
        'fitness', 
        'generic-gemini',
        'memory-qna',
        'office',
        'dev',
        'stem',
        'medical'
      ],
      requiredFields: ['prompt', 'agentType'],
      ...config
    };
  }

  /**
   * Validates a request object for basic format and size constraints
   */
  validateRequest(request: any): ValidationResult {
    const result: ValidationResult = {
      isValid: true,
      errors: [],
      warnings: []
    };

    // Basic null/undefined check
    if (!request) {
      result.isValid = false;
      result.errors.push('Request cannot be null or undefined');
      return result;
    }

    // Check required fields
    for (const field of this.config.requiredFields) {
      if (!request[field]) {
        result.isValid = false;
        result.errors.push(`Missing required field: ${field}`);
      }
    }

    // Validate prompt length
    if (request.prompt && typeof request.prompt === 'string') {
      if (request.prompt.length > this.config.maxPromptLength) {
        result.isValid = false;
        result.errors.push(`Prompt exceeds maximum length of ${this.config.maxPromptLength} characters`);
      }
      
      if (request.prompt.length === 0) {
        result.isValid = false;
        result.errors.push('Prompt cannot be empty');
      }
    }

    // Validate agent type
    if (request.agentType && !this.config.allowedAgentTypes.includes(request.agentType)) {
      result.warnings.push(`Unknown agent type: ${request.agentType}. Will use fallback.`);
    }

    // Check overall request size (approximate)
    const requestSize = JSON.stringify(request).length;
    if (requestSize > this.config.maxRequestSize) {
      result.isValid = false;
      result.errors.push(`Request size exceeds maximum of ${this.config.maxRequestSize} bytes`);
    }

    // Validate user ID format (if present)
    if (request.userId && typeof request.userId === 'string') {
      // Basic UUID v4 format check
      const uuidPattern = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
      if (!uuidPattern.test(request.userId)) {
        result.warnings.push('User ID does not match UUID v4 format');
      }
    }

    return result;
  }

  /**
   * Quick validation for high-frequency operations
   */
  quickValidate(prompt: string, agentType: string): boolean {
    if (!prompt || !agentType) return false;
    if (prompt.length > this.config.maxPromptLength) return false;
    return true;
  }

  /**
   * Sanitizes user input by removing potentially harmful content
   */
  sanitizeInput(input: string): string {
    if (!input || typeof input !== 'string') return '';
    
    // Remove potential script tags and suspicious patterns
    return input
      .replace(/<script[^>]*>.*?<\/script>/gi, '')
      .replace(/javascript:/gi, '')
      .replace(/on\w+\s*=/gi, '')
      .trim();
  }

  /**
   * Updates validation configuration
   */
  updateConfig(newConfig: Partial<ValidationConfig>): void {
    this.config = { ...this.config, ...newConfig };
  }

  /**
   * Gets current validation statistics
   */
  getValidationStats(): ValidationConfig {
    return { ...this.config };
  }
}

