// Canonical test for dynamic tool registration/removal and SSE notification
const { toolRegistry } = require('./coreagent/dist/tools/tools/ToolRegistry');
const { EventEmitter } = require('events');

// Canonical UnifiedMCPTool interface
class DynamicTestTool extends EventEmitter {
  constructor() {
    super();
    this.name = 'dynamic_test_tool';
    this.description = 'A dynamically registered test tool';
    this.schema = {
      type: 'object',
      properties: { value: { type: 'string' } },
      required: ['value']
    };
    this.constitutionalLevel = 'basic';
  }
  async execute(params) {
    return { echo: params.value };
  }
}

async function testDynamicToolRegistration() {
  let eventFired = false;
  // Listen for toolsChanged event (simulate OneAgentEngine event system)
  toolRegistry.onToolsChanged = () => {
    eventFired = true;
    console.log('[TEST] toolsChanged event fired');
  };

  // Register tool canonically
  const testTool = new DynamicTestTool();
  toolRegistry.registerTool(testTool, { category: 'development', constitutionalLevel: 'basic', priority: 5 });
  if (typeof toolRegistry.onToolsChanged === 'function') toolRegistry.onToolsChanged();
  const tools = toolRegistry.getToolSchemas();
  const found = tools.find(t => t.name === 'dynamic_test_tool');
  if (!found) throw new Error('Dynamic tool not found after registration');
  if (!eventFired) throw new Error('toolsChanged event not fired on register');
  console.log('[TEST] Tool registered and event fired');

  // Call tool
  const result = await testTool.execute({ value: 'hello' });
  if (result.echo !== 'hello') throw new Error('Tool did not echo value');
  console.log('[TEST] Tool executed successfully:', result);

  // Remove tool canonically
  eventFired = false;
  toolRegistry.tools.delete('dynamic_test_tool');
  if (typeof toolRegistry.onToolsChanged === 'function') toolRegistry.onToolsChanged();
  const toolsAfter = toolRegistry.getToolSchemas();
  const stillThere = toolsAfter.find(t => t.name === 'dynamic_test_tool');
  if (stillThere) throw new Error('Dynamic tool not removed');
  if (!eventFired) throw new Error('toolsChanged event not fired on remove');
  console.log('[TEST] Tool removed and event fired');
}

testDynamicToolRegistration().then(() => {
  console.log('[TEST] Dynamic tool registration/removal test PASSED');
  process.exit(0);
}).catch(e => {
  console.error('[TEST] FAILED:', e);
  process.exit(1);
});

