/**
 * Agent-to-Agent Messaging Test Suite
 * 
 * Tests the complete A2A communication pipeline:
 * - Agent registration with canonical interfaces
 * - Agent-to-agent message sending
 * - Constitutional AI validation
 * - Quality scoring and BMAD analysis
 * - Network health monitoring
 */

import { 
  AgentCommunicationProtocol, 
  A2AMessage, 
  A2AResponse, 
  AgentRegistration, 
  AgentCapability,
  A2AMessageType
} from '../coreagent/agents/communication/AgentCommunicationProtocol';
import { oneAgentConfig } from '../coreagent/config';
import { v4 as uuidv4 } from 'uuid';

interface TestResult {
  testName: string;
  success: boolean;
  message: string;
  details?: any;
  duration?: number;
}

/**
 * Comprehensive Agent-to-Agent Messaging Test Suite
 */
export class A2AMessagingTest {
  private protocol: AgentCommunicationProtocol;
  private testResults: TestResult[] = [];
  private sessionId: string = uuidv4();

  constructor() {
    // Initialize protocol with Constitutional AI validation
    this.protocol = AgentCommunicationProtocol.getInstance('TestCore-A2A', true);
  }

  /**
   * Run all A2A messaging tests
   */
  async runAllTests(): Promise<TestResult[]> {
    console.log('üöÄ Starting Agent-to-Agent Messaging Test Suite...\n');

    // Test 1: Protocol Initialization
    await this.testProtocolInitialization();

    // Test 2: Agent Registration with Canonical Interfaces
    await this.testAgentRegistration();

    // Test 3: Basic Agent-to-Agent Messaging
    await this.testBasicA2AMessaging();

    // Test 4: Constitutional AI Validation in Messages
    await this.testConstitutionalAIValidation();

    // Test 5: Quality Scoring and Thresholds
    await this.testQualityScoring();

    // Test 6: Different Message Types
    await this.testMessageTypes();

    // Test 7: Network Health and Agent Discovery
    await this.testNetworkHealth();

    // Test 8: Capability Queries
    await this.testCapabilityQueries();

    // Test 9: Agent Coordination
    await this.testAgentCoordination();

    // Test 10: Error Handling and Edge Cases
    await this.testErrorHandling();

    this.printTestSummary();
    return this.testResults;
  }

  /**
   * Test 1: Protocol Initialization
   */
  private async testProtocolInitialization(): Promise<void> {
    const startTime = Date.now();
    try {
      const isInstance = this.protocol instanceof AgentCommunicationProtocol;
      
      if (isInstance) {
        this.addTestResult({
          testName: 'Protocol Initialization',
          success: true,
          message: 'AgentCommunicationProtocol initialized successfully',
          duration: Date.now() - startTime
        });
      } else {
        throw new Error('Protocol initialization failed');
      }
    } catch (error) {
      this.addTestResult({
        testName: 'Protocol Initialization',
        success: false,
        message: `Failed: ${error}`,
        duration: Date.now() - startTime
      });
    }
  }

  /**
   * Test 2: Agent Registration with Canonical Interfaces
   */
  private async testAgentRegistration(): Promise<void> {
    const startTime = Date.now();
    try {
      // Clear any existing agents
      const clearResult = this.protocol.clearPhantomAgents();
      console.log(`üßπ Cleared ${clearResult.cleared} phantom agents`);

      // Create test agent with canonical interface
      const testCapabilities: AgentCapability[] = [
        {
          name: 'test_communication',
          description: 'Testing A2A communication capabilities',
          version: '1.0.0',
          parameters: { maxMessages: 100 },
          qualityThreshold: 85,
          constitutionalCompliant: true
        },
        {
          name: 'response_generation',
          description: 'Generate appropriate responses with Constitutional AI',
          version: '1.0.0',
          parameters: { responseFormat: 'natural_language' },
          qualityThreshold: 90,
          constitutionalCompliant: true
        }
      ];

      const testAgent1: AgentRegistration = {
        agentId: 'TestAgent1',
        agentType: 'testing',
        capabilities: testCapabilities,
        endpoint: `${oneAgentConfig.mcpUrl}/agent/TestAgent1`,
        status: 'online',
        loadLevel: 0,
        qualityScore: 95,
        lastSeen: new Date()
      };

      const testAgent2: AgentRegistration = {
        agentId: 'TestAgent2',
        agentType: 'testing',
        capabilities: testCapabilities,
        endpoint: `${oneAgentConfig.mcpUrl}/agent/TestAgent2`,
        status: 'online',
        loadLevel: 10,
        qualityScore: 92,
        lastSeen: new Date()
      };

      // Register both agents
      const reg1Success = await this.protocol.registerAgent(testAgent1);
      const reg2Success = await this.protocol.registerAgent(testAgent2);

      if (reg1Success && reg2Success) {
        this.addTestResult({
          testName: 'Agent Registration',
          success: true,
          message: 'Both test agents registered successfully with canonical interfaces',
          details: { agent1: testAgent1.agentId, agent2: testAgent2.agentId },
          duration: Date.now() - startTime
        });
      } else {
        throw new Error(`Registration failed: Agent1=${reg1Success}, Agent2=${reg2Success}`);
      }
    } catch (error) {
      this.addTestResult({
        testName: 'Agent Registration',
        success: false,
        message: `Failed: ${error}`,
        duration: Date.now() - startTime
      });
    }
  }

