    const startTime = Date.now();
    try {
      const message: A2AMessage = {
        id: uuidv4(),
        type: 'coordination_request',
        sourceAgent: 'TestAgent1',
        targetAgent: 'TestAgent2',
        content: 'Hello TestAgent2! This is a test message to verify A2A communication is working properly.',
        metadata: {
          priority: 'medium',
          requiresResponse: true,
          confidenceLevel: 0.95,
          constitutionalValidated: false,
          qualityScore: 88
        },
        timestamp: new Date(),
        sessionId: this.sessionId
      };

      const response: A2AResponse = await this.protocol.sendMessage(message);

      if (response.success) {
        this.addTestResult({
          testName: 'Basic A2A Messaging',
          success: true,
          message: 'Message sent successfully between agents',
          details: { 
            messageId: message.id,
            processingTime: response.metadata.processingTime,
            qualityScore: response.metadata.qualityScore,
            constitutionalCompliant: response.metadata.constitutionalCompliant
