  /**
   * Test 6: Different Message Types
   */
  private async testMessageTypes(): Promise<void> {
    const startTime = Date.now();
    try {
      const messageTypes: A2AMessageType[] = [
        'status_update',
        'resource_share',
        'collaboration_invite'
      ];

      let successCount = 0;

      for (const messageType of messageTypes) {
