          successCount++;
        }
      }

      if (successCount === messageTypes.length) {
        this.addTestResult({
          testName: 'Message Types',
          success: true,
          message: `All ${messageTypes.length} message types processed successfully`,
          details: { testedTypes: messageTypes, successCount },
          duration: Date.now() - startTime
        });
