  /**
   * Test 10: Error Handling and Edge Cases
   */
  private async testErrorHandling(): Promise<void> {
    const startTime = Date.now();
    try {
      // Test message to non-existent agent
      const invalidMessage: A2AMessage = {
        id: uuidv4(),
        type: 'task_delegation',
        sourceAgent: 'TestAgent1',
        targetAgent: 'NonExistentAgent',
        content: 'This should fail gracefully.',
        metadata: {
          priority: 'low',
          requiresResponse: false,
          confidenceLevel: 0.5,
          constitutionalValidated: false
        },
        timestamp: new Date(),
        sessionId: this.sessionId
      };

      const response = await this.protocol.sendMessage(invalidMessage);

      // Should fail but handle error gracefully
      if (!response.success && response.content.includes('not found')) {
        this.addTestResult({
          testName: 'Error Handling',
          success: true,
          message: 'Error handled gracefully for non-existent agent',
          details: { errorMessage: response.content },
          duration: Date.now() - startTime
        });
      } else {
        throw new Error(`Expected graceful failure, got: ${response.content}`);
      }
    } catch (error) {
      this.addTestResult({
        testName: 'Error Handling',
        success: false,
        message: `Failed: ${error}`,
        duration: Date.now() - startTime
      });
    }
  }

  /**
   * Helper method to add test results
   */
  private addTestResult(result: TestResult): void {
    this.testResults.push(result);
    const status = result.success ? '‚úÖ' : '‚ùå';
    const duration = result.duration ? ` (${result.duration}ms)` : '';
    console.log(`${status} ${result.testName}: ${result.message}${duration}`);
    if (result.details) {
      console.log(`   Details: ${JSON.stringify(result.details, null, 2)}`);
    }
  }

  /**
   * Print comprehensive test summary
   */
  private printTestSummary(): void {
    const passed = this.testResults.filter(r => r.success).length;
    const failed = this.testResults.filter(r => !r.success).length;
    const totalDuration = this.testResults.reduce((sum, r) => sum + (r.duration || 0), 0);

    console.log('\nüìä A2A Messaging Test Suite Summary:');
    console.log(`‚úÖ Passed: ${passed}`);
    console.log(`‚ùå Failed: ${failed}`);
    console.log(`‚è±Ô∏è  Total Duration: ${totalDuration}ms`);
    console.log(`üìà Success Rate: ${((passed / this.testResults.length) * 100).toFixed(1)}%`);

    if (failed > 0) {
      console.log('\n‚ùå Failed Tests:');
      this.testResults.filter(r => !r.success).forEach(r => {
        console.log(`   - ${r.testName}: ${r.message}`);
      });
    }
  }
}

