    console.log('üìà First 5 values:', embedding.embedding.slice(0, 5));    // Test 3: Batch embeddings
    console.log('\nüß™ Test 3: Batch Embedding Generation');
    const batchEmbeddings = await client.generateEmbeddingBatch([
      'First test document for batch embedding',
