      'Third test document for batch embedding'
    ]);
    console.log('‚úÖ Batch embeddings successful:');
    console.log('üìä Number of embeddings:', batchEmbeddings.length);
    console.log('üìä Each embedding dimensions:', batchEmbeddings[0]?.dimensions);

    // Test 4: Similarity calculation
    if (batchEmbeddings.length >= 2) {
      console.log('\nüß™ Test 4: Similarity Calculation');
      const similarity = GeminiClient.calculateCosineSimilarity(
        batchEmbeddings[0].embedding, 
        batchEmbeddings[1].embedding
      );
      console.log('‚úÖ Similarity calculation successful:');
      console.log('üìà Similarity score:', similarity.toFixed(4));
    }

    console.log('\nüéâ ALL TESTS PASSED! Google AI Studio API is fully working.');
    console.log('üöÄ Gemini embeddings integration is production-ready.');

  } catch (error) {
    console.error('\n‚ùå API Test Failed:');
    if (error instanceof Error) {
      console.error('üìù Error message:', error.message);
      
      // Check for specific API errors
      if (error.message.includes('API_KEY_INVALID')) {
        console.error('üîë The API key appears to be invalid');
      } else if (error.message.includes('QUOTA_EXCEEDED')) {
        console.error('üí∞ API quota has been exceeded');
      } else if (error.message.includes('permission')) {
        console.error('üîí API key lacks required permissions');
      }
    }
    console.error('üîß Full error:', error);
  }
}

// Helper function for similarity calculation
function calculateCosineSimilarity(a: number[], b: number[]): number {
  if (a.length !== b.length) {
    throw new Error('Vectors must have the same length');
  }

  let dotProduct = 0;
  let normA = 0;
  let normB = 0;

  for (let i = 0; i < a.length; i++) {
    dotProduct += a[i] * b[i];
    normA += a[i] * a[i];
    normB += b[i] * b[i];
  }

  const similarity = dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
  return similarity;
}

