/**
 * Comprehensive Test Suite for Gemini Cost Monitoring System
 * Part of OneAgent Phase 3A Enhancement Implementation
 * 
 * Tests all cost monitoring, analytics, and budget management features.
 */

import { costMonitoringService } from './config/gemini-cost-monitoring';
import { AgentFactory } from './coreagent/agents/base/AgentFactory';
import { GEMINI_MODEL_REGISTRY } from './config/gemini-model-registry';

// =============================================================================
// TEST UTILITIES
// =============================================================================

function sleep(ms: number): Promise<void> {
  return new Promise(resolve => setTimeout(resolve, ms));
}

function logTestResult(testName: string, passed: boolean, details?: string): void {
  const status = passed ? '‚úÖ PASS' : '‚ùå FAIL';
  console.log(`${status} ${testName}`);
  if (details) {
    console.log(`   ${details}`);
  }
}

// =============================================================================
// COST MONITORING TESTS
// =============================================================================

async function testCostTrackingBasics(): Promise<boolean> {
  console.log('\nüß™ Testing Cost Tracking Basics...');
  
  try {
    // Clear any existing history
    costMonitoringService.clearUsageHistory();
    
    // Test tracking token usage
    costMonitoringService.trackTokenUsage({
      agentId: 'test-agent-1',
      agentType: 'development',
      modelId: 'gemini-2.5-flash',
      inputTokens: 1000,
      outputTokens: 2000,
      timestamp: new Date(),
      taskType: 'code_generation'
    });
    
    await sleep(100);
    
    // Test cost summary
    const dayCost = costMonitoringService.getCurrentCost('day');
    const expectedCost = (1000 / 1_000_000) * 0.30 + (2000 / 1_000_000) * 2.50; // Flash pricing
    
    const costMatch = Math.abs(dayCost.totalCost - expectedCost) < 0.001;
    logTestResult('Token usage tracking', costMatch, 
      `Expected: $${expectedCost.toFixed(6)}, Got: $${dayCost.totalCost.toFixed(6)}`);
    
    const hasBreakdown = Object.keys(dayCost.modelBreakdown).length > 0;
    logTestResult('Cost breakdown generation', hasBreakdown);
    
    return costMatch && hasBreakdown;
  } catch (error) {
    logTestResult('Cost tracking basics', false, `Error: ${error}`);
    return false;
  }
}

async function testBudgetAlerts(): Promise<boolean> {
  console.log('\nüö® Testing Budget Alert System...');
  
  try {
    // Configure budget
    costMonitoringService.configureBudget({
      dailyLimit: 10.00,
      monthlyLimit: 100.00,
      warningThreshold: 50,
      criticalThreshold: 80
    });
    
    // Clear history and add usage that triggers alert
    costMonitoringService.clearUsageHistory();
    
    // Add usage that's 60% of daily budget
    costMonitoringService.trackTokenUsage({
      agentId: 'test-agent-budget',
      agentType: 'development',
      modelId: 'gemini-2.5-pro',
      inputTokens: 480000, // This should cost about $6
      outputTokens: 0,
      timestamp: new Date()
    });
    
    await sleep(100);
    
    const alerts = costMonitoringService.getBudgetAlerts();
    const hasWarningAlert = alerts.some(alert => alert.type === 'warning');
    
    logTestResult('Budget warning alert generation', hasWarningAlert,
      `Generated ${alerts.length} alerts`);
    
    // Test critical alert
    costMonitoringService.trackTokenUsage({
      agentId: 'test-agent-budget-2',
      agentType: 'development',
      modelId: 'gemini-2.5-pro',
      inputTokens: 400000, // Additional $5, total ~$11 (over $10 limit)
      outputTokens: 0,
      timestamp: new Date()
    });
    
    await sleep(100);
    
    const criticalAlerts = costMonitoringService.getBudgetAlerts();
    const hasCriticalAlert = criticalAlerts.some(alert => alert.type === 'critical');
    
    logTestResult('Budget critical alert generation', hasCriticalAlert,
      `Generated ${criticalAlerts.length} alerts`);
    
    return hasWarningAlert && hasCriticalAlert;
  } catch (error) {
    logTestResult('Budget alerts', false, `Error: ${error}`);
    return false;
  }
}

async function testCostForecasting(): Promise<boolean> {
  console.log('\nüìä Testing Cost Forecasting...');
  
  try {
    // Clear and add historical data
    costMonitoringService.clearUsageHistory();
    
    // Add several days of usage data
    const now = new Date();
    for (let i = 0; i < 5; i++) {
      const timestamp = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
      
      costMonitoringService.trackTokenUsage({
        agentId: `test-agent-forecast-${i}`,
        agentType: 'development',
        modelId: 'gemini-2.5-flash',
        inputTokens: 10000 + i * 1000,
        outputTokens: 20000 + i * 2000,
        timestamp
      });
    }
    
    await sleep(100);
    
    // Test forecasting
    const forecast = costMonitoringService.forecastCosts(7);
    
    const hasProjection = forecast.projectedDailyCost > 0;
    const hasConfidence = forecast.confidenceLevel > 0;
    const hasRecommendations = forecast.recommendations.length > 0;
    
    logTestResult('Cost projection generation', hasProjection,
      `Daily: $${forecast.projectedDailyCost.toFixed(4)}, Monthly: $${forecast.projectedMonthlyCost.toFixed(2)}`);
    
