      `Confidence: ${forecast.confidenceLevel}%`);
    
    logTestResult('Forecast recommendations', hasRecommendations,
      `${forecast.recommendations.length} recommendations`);
    
    return hasProjection && hasConfidence;
  } catch (error) {
    logTestResult('Cost forecasting', false, `Error: ${error}`);
    return false;
  }
}

