  console.log(`   Forecasting: ${forecastTime}ms for 50 forecasts (${(forecastTime/50).toFixed(2)}ms per forecast)`);
  
  const acceptable = trackingTime < 5000 && calcTime < 1000 && forecastTime < 2000;
  console.log(`‚ö° Performance: ${acceptable ? 'ACCEPTABLE' : 'NEEDS OPTIMIZATION'}`);
