} from '../coreagent/agents/communication/AgentCommunicationProtocol';
import { v4 as uuidv4 } from 'uuid';

interface EnhancedTestResult {
  testName: string;
  success: boolean;
  message: string;
  conversationLogs?: ConversationLog[];
  actionableOutputs?: ActionableOutput[];
  analytics?: any;
  duration?: number;
}

export class EnhancedA2AConversationTest {
  private protocol: AgentCommunicationProtocol;
  private testResults: EnhancedTestResult[] = [];
  private conversationId: string = '';

  constructor() {
    this.protocol = AgentCommunicationProtocol.getInstance('Enhanced-A2A-Test', true);
  }

  async runEnhancedTests(): Promise<EnhancedTestResult[]> {
    console.log('üöÄ Starting Enhanced A2A Conversation Test Suite...\n');

    // Test 1: Conversation Logging
    await this.testConversationLogging();

    // Test 2: Actionable Output Generation
    await this.testActionableOutputGeneration();

    // Test 3: Workflow Integration
    await this.testWorkflowIntegration();

    // Test 4: Conversation Analytics
    await this.testConversationAnalytics();

    // Test 5: Real Agent Coordination with Full Logging
    await this.testRealAgentCoordinationWithLogging();

    this.printEnhancedTestSummary();
    return this.testResults;
  }

  /**
   * Test 1: Conversation Logging
   */
  private async testConversationLogging(): Promise<void> {
    const startTime = Date.now();
    try {
      // Clear agents and register test agents
      this.protocol.clearPhantomAgents();
      await this.registerTestAgents();

      // Start a conversation
      this.conversationId = this.protocol.startConversation(
        'logging-test-session',
        ['TestDevAgent', 'TestOfficeAgent', 'TestTriageAgent']
      );

      // Send several messages in a conversation
      const messages = [
        {
          type: 'coordination_request' as const,
          sourceAgent: 'TestDevAgent',
          targetAgent: 'TestOfficeAgent',
          content: 'I need help creating user documentation for our new A2A conversation features. Can you assist?'
        },
        {
          type: 'capability_query' as const,
          sourceAgent: 'TestOfficeAgent',
          targetAgent: 'TestTriageAgent',
          content: 'What technical specifications do we need for the documentation?'
        },
        {
          type: 'task_delegation' as const,
          sourceAgent: 'TestTriageAgent',
          targetAgent: 'TestDevAgent',
          content: 'Please provide the API documentation and technical details for the conversation logging features.'
        }
      ];

      // Send messages and log them
      for (const msgData of messages) {
        const message: A2AMessage = {
          id: uuidv4(),
          type: msgData.type,
          sourceAgent: msgData.sourceAgent,
          targetAgent: msgData.targetAgent,
          content: msgData.content,
          metadata: {
            priority: 'medium',
            requiresResponse: true,
            confidenceLevel: 0.9,
            constitutionalValidated: false
          },
          timestamp: new Date(),
          sessionId: this.conversationId
        };

        const response = await this.protocol.sendMessage(message);
        if (response.success) {
          this.protocol.logConversationExchange(this.conversationId, message, response);
        }
      }

      // End conversation
      const conversationLog = this.protocol.endConversation(this.conversationId, {
        type: 'completed',
        summary: 'Successfully coordinated documentation creation for A2A conversation features',
        decisions: ['Create technical API docs', 'Include user examples', 'Add troubleshooting guide'],
        nextSteps: ['Draft documentation', 'Review with team', 'Publish to docs site'],
        requiresHumanIntervention: false
      });

      if (conversationLog && conversationLog.messages.length === 3) {
        this.addTestResult({
          testName: 'Conversation Logging',
          success: true,
          message: `Successfully logged conversation with ${conversationLog.messages.length} messages`,
          conversationLogs: [conversationLog],
          duration: Date.now() - startTime
        });
      } else {
        throw new Error('Conversation logging failed or incomplete');
      }

    } catch (error) {
      this.addTestResult({
        testName: 'Conversation Logging',
        success: false,
        message: `Failed: ${error}`,
        duration: Date.now() - startTime
      });
    }
  }

