// Helper function to create proper Message objects
function createMessage(content: string, sender: 'user' | 'agent' = 'user'): Message {
  return {
    id: `msg-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
    content,
    sender,
    timestamp: new Date()
  };
}

// Helper function to create proper User objects
function createUser(id: string, name: string): User {
  const currentTime = new Date().toISOString();
  return {
    id,
    name,
    createdAt: currentTime,
    lastActiveAt: currentTime
  };
}

// Helper function to create proper AgentContext
function createContext(userId: string, userName: string, sessionId: string, userMessage: string): AgentContext {
  return {
    user: createUser(userId, userName),
    sessionId,
    conversationHistory: [createMessage(userMessage)]
  };
}

