    const found = await registry.getAgent('test-agent');
    expect(found).not.toBeNull();
    expect(found?.agentId).toBe('test-agent');
  });

  it('discovers agent by type (MCP-first, fallback in-memory)', async () => {
    const agent: AgentRegistration = {
      agentId: 'type-agent',
      agentType: 'discovery',
      capabilities: ['find'],
      version: '1.0.0',
      status: 'active',
      health: 'healthy',
      lastHeartbeat: Date.now(),
