    expect(found.length).toBeGreaterThan(0);
    expect(found[0].agentType).toBe('discovery');
  });

  it('handles MCP unavailable (returns in-memory only)', async () => {
    // Simulate MCP failure by setting invalid URL
    (registry as any).mcpBaseUrl = 'http://localhost:9999/invalid';
    const agent: AgentRegistration = {
      agentId: 'fail-agent',
      agentType: 'fail',
      capabilities: ['fail'],
      version: '1.0.0',
      status: 'active',
      health: 'healthy',
      lastHeartbeat: Date.now(),
      qualityScore: 90
    };
    await registry.registerAgent(agent);
    const found = await discovery.findAgentsByType('fail');
    expect(found.length).toBeGreaterThan(0);
    expect(found[0].agentId).toBe('fail-agent');
  });

  it('should update and remove an agent', async () => {
    const agent: AgentRegistration = {
      agentId: 'update-remove-agent',
      agentType: 'test',
      capabilities: ['test'],
      version: '1.0.0',
      status: 'active',
      health: 'healthy',
      lastHeartbeat: Date.now(),
      qualityScore: 90
    };
    await registry.registerAgent(agent);
    // Update
    const updated = { ...agent, displayName: 'Updated Agent' };
    await registry.updateAgent(updated);
    const found = await registry.getAgent(agent.agentId);
    expect(found?.displayName).toBe('Updated Agent');
    // Remove
    await registry.removeAgent(agent.agentId);
    const removed = await registry.getAgent(agent.agentId);
    expect(removed).toBeNull();
  });

  it('should log and resolve registration conflict in favor of MCP', async () => {
    const agent: AgentRegistration = {
      agentId: 'conflict-agent',
      agentType: 'conflict',
      capabilities: ['conflict'],
      version: '1.0.0',
      status: 'active',
      health: 'healthy',
      lastHeartbeat: Date.now(),
      qualityScore: 90
    };
    await registry.registerAgent(agent);
    // Simulate MCP returns different data for the same agentId
    const mcpAgent = { ...agent, displayName: 'MCP Agent', version: '2.0.0' };
    let logCalled = false;
    (registry as any).mcpRequest = async (method: string, path: string) => {
      if (method === 'GET' && path.startsWith('?')) {
        return [mcpAgent];
      }
      return null;
    };
    // Patch console.warn to detect log
    const origWarn = console.warn;
    console.warn = (...args: any[]) => { logCalled = true; origWarn(...args); };
    const agents = await registry.listAgents();
    expect(agents.some(a => a.displayName === 'MCP Agent')).toBe(true);
    expect(logCalled).toBe(true);
    console.warn = origWarn;
  });

  it('falls back to in-memory and logs when MCP is unavailable (discovery)', async () => {
