      capabilities: ['compute'],
      skills: ['fallback'],
      health: 'healthy',
      lastHeartbeat: Date.now(),
      credentials: {},
      authorization: {},
      qualityScore: 90
    });
    let logCalled = false;
    const origWarn = console.warn;
    console.warn = (...args: any[]) => { logCalled = true; origWarn(...args); };
    const found = await discovery.findAgentsBySkill('fallback');
    expect(found.length).toBeGreaterThan(0);
    expect(found[0].agentId).toBe('fallback-agent');
    expect(logCalled).toBe(true);
    console.warn = origWarn;
  });

  it('logs and handles network errors in registry listAgents', async () => {
    (registry as any).mcpRequest = async () => { throw new Error('network error'); };
    await registry.registerAgent({
      agentId: 'neterr-agent',
      displayName: 'NetErr Agent',
      agentType: 'worker',
      version: '1.0.0',
      status: 'active',
      capabilities: ['compute'],
      skills: ['neterr'],
      health: 'healthy',
      lastHeartbeat: Date.now(),
      credentials: {},
      authorization: {},
      qualityScore: 90
    });
    let logCalled = false;
    const origWarn = console.warn;
    console.warn = (...args: any[]) => { logCalled = true; origWarn(...args); };
    const agents = await registry.listAgents();
    expect(agents.some(a => a.agentId === 'neterr-agent')).toBe(true);
    expect(logCalled).toBe(true);
    console.warn = origWarn;
