/**
 * NLACS Domain-Agnostic Test: Budget Management
 * Demonstrates NLACS working with financial planning (not coding)
 */

// Load environment first
require('dotenv').config();

const { EventEmitter } = require('events');

// =============================================================================
// DOMAIN-AGNOSTIC NLACS TEST: BUDGETING
// =============================================================================

class DomainAgnosticNLACS extends EventEmitter {
  constructor() {
    super();
    this.conversations = new Map();
    this.startTime = new Date();
    
    // Configuration from .env
    this.NLACS_ENABLED = process.env.NLACS_ENABLED === 'true';
    this.NLACS_MAX_PARTICIPANTS = parseInt(process.env.NLACS_MAX_PARTICIPANTS_PER_CONVERSATION || '10');
    this.NLACS_MAX_MESSAGES = parseInt(process.env.NLACS_MAX_MESSAGES_PER_CONVERSATION || '100');
  }

  async initialize() {
    if (!this.NLACS_ENABLED) {
      console.log('‚ö†Ô∏è  NLACS is disabled. Set NLACS_ENABLED=true in .env to enable.');
      return false;
    }

    console.log('üöÄ Domain-Agnostic NLACS initializing...');
    console.log('   Ready for ANY domain: finance, health, career, home, travel, etc.');
    return true;
  }

  async initiateConversation(topic, requiredPerspectives, userId, projectContext) {
    const conversationId = `nlacs_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    
    const conversation = {
      conversationId,
      userId,
      topic,
      participants: [],
      messages: [],
      status: 'active',
      createdAt: new Date(),
      lastActivity: new Date(),
      ...(projectContext && { projectContext })
    };

    this.conversations.set(conversationId, conversation);

    // Add agents
    for (const perspective of requiredPerspectives) {
      await this.addAgentToConversation(conversationId, perspective, 'primary', userId);
    }

    console.log(`üé¨ ${projectContext?.contextTags?.includes('WORKPLACE') ? 'üíº WORKPLACE' : 'üè† PRIVATE'} Conversation: "${topic}"`);
    console.log(`   Domain: ${projectContext?.contextTags?.filter(tag => !['WORKPLACE', 'PRIVATE'].includes(tag)).join(', ')}`);
    console.log(`   Agents: ${conversation.participants.map(p => p.agentType).join(', ')}`);
    
    return conversation;
  }

  async addAgentToConversation(conversationId, agentType, role, userId) {
    const conversation = this.conversations.get(conversationId);
    if (!conversation) throw new Error(`Conversation ${conversationId} not found`);
    if (conversation.userId !== userId) throw new Error('Access denied');

    const agentId = `${agentType}_${Date.now()}`;
    conversation.participants.push({
      agentId, agentType, role, joinedAt: new Date()
    });

    conversation.lastActivity = new Date();
    return true;
  }

  async sendMessage(conversationId, agentId, content, messageType, userId) {
    const conversation = this.conversations.get(conversationId);
    if (!conversation) throw new Error(`Conversation ${conversationId} not found`);
    if (conversation.userId !== userId) throw new Error('Access denied');

    const agent = conversation.participants.find(p => p.agentId === agentId);
    if (!agent) throw new Error(`Agent ${agentId} not found`);

    const message = {
      messageId: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 6)}`,
      conversationId, agentId, agentType: agent.agentType, content, messageType,
      timestamp: new Date(), userId, confidence: 0.85, referencesTo: []
    };

    conversation.messages.push(message);
    conversation.lastActivity = new Date();

    const contextType = conversation.projectContext?.contextTags?.includes('WORKPLACE') ? 'üíº' : 'üè†';
    console.log(`${contextType} ${agent.agentType}: "${content.substring(0, 60)}..."`);
    
    return message;
  }

  async getConversation(conversationId, userId) {
    const conversation = this.conversations.get(conversationId);
    if (!conversation) throw new Error(`Conversation ${conversationId} not found`);
    if (conversation.userId !== userId) throw new Error('Access denied');
    return conversation;
  }

  async getSystemStatus() {
    const totalMessages = Array.from(this.conversations.values())
      .reduce((sum, conv) => sum + conv.messages.length, 0);

    return {
      activeConversations: this.conversations.size,
      totalMessages,
      uptime: Date.now() - this.startTime.getTime(),
      isEnabled: this.NLACS_ENABLED
    };
  }
}

