/**
 * Unified MCP Tool Registry
 * Central registry for all unified MCP tools with categorized organization
 * NLACS Integration: Modern agent coordination via NLACS orchestrator
 */

import { UnifiedMCPTool } from './UnifiedMCPTool';
import { EnhancedSearchTool } from './EnhancedSearchTool';
import { SystemHealthTool } from './SystemHealthTool';
import { UnifiedWebSearchTool } from './UnifiedWebSearchTool';
import { UnifiedWebFetchTool } from './UnifiedWebFetchTool';
import { UnifiedContext7QueryTool } from './UnifiedContext7QueryTool';

// REMOVED: EnhancedAIAssistantTool - maintaining clear separation of concerns
import { CodeAnalysisTool } from './CodeAnalysisTool';
import { Context7MCPIntegration } from '../mcp/Context7MCPIntegration';
import { ConversationRetrievalTool } from './ConversationRetrievalTool';
import { ConversationSearchTool } from './ConversationSearchTool';
import { OneAgentMemorySearchTool } from './OneAgentMemorySearchTool';
import { OneAgentMemoryAddTool } from './OneAgentMemoryAddTool';
import { OneAgentMemoryEditTool } from './OneAgentMemoryEditTool';
import { OneAgentMemoryDeleteTool } from './OneAgentMemoryDeleteTool';
import { OneAgentMemory } from '../memory/OneAgentMemory';

export enum ToolCategory {
  CORE_SYSTEM = 'core_system',
  MEMORY_CONTEXT = 'memory_context', 
  WEB_RESEARCH = 'web_research',
  AGENT_COMMUNICATION = 'agent_communication',
  DEVELOPMENT = 'development'
}

export interface ToolMetadata {
  category: ToolCategory;
  constitutionalLevel: 'basic' | 'enhanced' | 'critical';
  dependencies?: string[];
  priority: number; // 1-10, higher = more important
}

export interface ToolRegistration {
  tool: UnifiedMCPTool;
  metadata: ToolMetadata;
  registeredAt: Date;
  lastUsed?: Date;
  usageCount: number;
}

export class ToolRegistry {
  private tools: Map<string, ToolRegistration> = new Map();
  private categories: Map<ToolCategory, string[]> = new Map();
  private initialized = false;

  constructor() {
    this.initializeCategories();
    this.registerDefaultTools();
    this.initialized = true;
  }

  /**
   * Initialize tool categories
   */
  private initializeCategories(): void {
    for (const category of Object.values(ToolCategory)) {
      this.categories.set(category as ToolCategory, []);
    }
  }  /**
   * Register default unified tools with metadata
   */
  private registerDefaultTools(): void {
    // Web Research Tools  
    this.registerTool(new EnhancedSearchTool(), {
      category: ToolCategory.WEB_RESEARCH,
      constitutionalLevel: 'enhanced',
      priority: 7
    });

    this.registerTool(new UnifiedWebSearchTool(), {
      category: ToolCategory.WEB_RESEARCH,
      constitutionalLevel: 'enhanced',
      priority: 8
    });
      this.registerTool(new UnifiedWebFetchTool(), {
      category: ToolCategory.WEB_RESEARCH,
      constitutionalLevel: 'enhanced',
      priority: 7
    });
    
    this.registerTool(new ConversationRetrievalTool(), {
      category: ToolCategory.AGENT_COMMUNICATION,
      constitutionalLevel: 'enhanced',
      priority: 7
    });
    
    this.registerTool(new ConversationSearchTool(), {
      category: ToolCategory.AGENT_COMMUNICATION,
      constitutionalLevel: 'enhanced',
      priority: 7
    });
    
    // System Health and Monitoring
    this.registerTool(new SystemHealthTool(), {
      category: ToolCategory.CORE_SYSTEM,
      constitutionalLevel: 'basic',
      priority: 6
    });

    // Context7 documentation tools (now with real integration)
    const context7Integration = new Context7MCPIntegration();
    
    this.registerTool(new UnifiedContext7QueryTool(context7Integration), {
      category: ToolCategory.MEMORY_CONTEXT,
      constitutionalLevel: 'enhanced',
      priority: 8
    });

    // TODO: Fix UnifiedContext7StoreTool - currently has broken imports/stubs causing undefined registration
    // this.registerTool(new UnifiedContext7StoreTool(), {
    //   category: ToolCategory.MEMORY_CONTEXT,
    //   constitutionalLevel: 'enhanced',
    //   priority: 7
    // });
    
    // Development and Professional Tools
    this.registerTool(new CodeAnalysisTool(), {
      category: ToolCategory.DEVELOPMENT,
      constitutionalLevel: 'enhanced',
      priority: 8
    });
    
    // Canonical OneAgent memory tools (the only standard, best-practice memory tools)
    const canonicalMemoryClient = new OneAgentMemory({});
    this.registerTool(new OneAgentMemorySearchTool(canonicalMemoryClient), {
      category: ToolCategory.MEMORY_CONTEXT,
      constitutionalLevel: 'critical',
      priority: 10
    });
    this.registerTool(new OneAgentMemoryAddTool(canonicalMemoryClient), {
      category: ToolCategory.MEMORY_CONTEXT,
      constitutionalLevel: 'critical',
      priority: 10
    });
    this.registerTool(new OneAgentMemoryEditTool(canonicalMemoryClient), {
      category: ToolCategory.MEMORY_CONTEXT,
      constitutionalLevel: 'critical',
      priority: 10
    });
    this.registerTool(new OneAgentMemoryDeleteTool(canonicalMemoryClient), {
      category: ToolCategory.MEMORY_CONTEXT,
      constitutionalLevel: 'critical',
      priority: 10
    });
    
    // NOTE: EnhancedAIAssistantTool REMOVED to maintain clear separation of concerns
    // Memory operations are handled by dedicated MemoryCreateTool and MemorySearchTool
    // AI assistance should be separate from memory management for clarity
    
    console.log(`[ToolRegistry] Registered ${this.tools.size} unified tools across ${this.categories.size} categories`);
    this.logCategoryStatus();
  }

  /**
   * Register a new tool with metadata
   */
  public registerTool(tool: UnifiedMCPTool, metadata?: Partial<ToolMetadata>): void {
    const fullMetadata: ToolMetadata = {
      category: metadata?.category || ToolCategory.DEVELOPMENT,
      constitutionalLevel: metadata?.constitutionalLevel || tool.constitutionalLevel,
      dependencies: metadata?.dependencies || [],
      priority: metadata?.priority || 5
    };

    const registration: ToolRegistration = {
      tool,
      metadata: fullMetadata,
      registeredAt: new Date(),
      usageCount: 0
    };

    this.tools.set(tool.name, registration);
    
    // Add to category
    const categoryTools = this.categories.get(fullMetadata.category) || [];
    categoryTools.push(tool.name);
    this.categories.set(fullMetadata.category, categoryTools);
    
    console.log(`[ToolRegistry] Registered ${tool.name} in ${fullMetadata.category} (priority: ${fullMetadata.priority})`);
  }

  /**
   * Get a tool by name
   */
  public getTool(name: string): UnifiedMCPTool | undefined {
    const registration = this.tools.get(name);
    return registration?.tool;
  }

  /**
   * Get tool registration (includes metadata)
   */
  public getToolRegistration(name: string): ToolRegistration | undefined {
    return this.tools.get(name);
  }

  /**
   * Check if a tool is registered
   */
  public hasTool(name: string): boolean {
    return this.tools.has(name);
  }

  /**
   * Get all registered tool names
   */
  public getToolNames(): string[] {
    return Array.from(this.tools.keys());
  }

  /**
   * Get tools by category
   */
  public getToolsByCategory(category: ToolCategory): UnifiedMCPTool[] {
    const toolNames = this.categories.get(category) || [];
    return toolNames.map(name => this.getTool(name)).filter(Boolean) as UnifiedMCPTool[];
  }

  /**
   * Get tool schema for MCP registration
   */
  public getToolSchemas(): Array<{name: string, description: string, inputSchema: any}> {
    return Array.from(this.tools.values()).map(registration => ({
      name: registration.tool.name,
      description: registration.tool.description,
      inputSchema: registration.tool.schema
    }));
  }

  /**
   * Execute a tool by name with usage tracking
   */
  public async executeTool(name: string, args: any): Promise<any> {
    const registration = this.tools.get(name);
    if (!registration) throw new Error(`Tool not found: ${name}`);

    // Update usage tracking
    registration.usageCount++;
    registration.lastUsed = new Date();

    console.log(`[ToolRegistry] Executing ${name} (category: ${registration.metadata.category}, usage: ${registration.usageCount})`);
    return await registration.tool.execute(args); // Only pass one argument as required
  }

  /**
   * Get comprehensive tool status and analytics
   */
  public getStatus(): {
    totalTools: number;
    toolNames: string[];
    categories: Record<string, number>;
    framework: string;
    constitutionalCompliant: boolean;
    analytics: {
      mostUsed: string[];
      byCategory: Record<string, string[]>;
      priorityDistribution: Record<number, number>;
    };
  } {
    const categoryStats: Record<string, number> = {};
    const priorityDistribution: Record<number, number> = {};
    const usageStats: Array<{name: string, count: number}> = [];
    const categoryTools: Record<string, string[]> = {};

    for (const [name, registration] of Array.from(this.tools)) {
      const category = registration.metadata.category;
      const priority = registration.metadata.priority;
      
      categoryStats[category] = (categoryStats[category] || 0) + 1;
      priorityDistribution[priority] = (priorityDistribution[priority] || 0) + 1;
      usageStats.push({ name, count: registration.usageCount });
      
      if (!categoryTools[category]) categoryTools[category] = [];
      categoryTools[category].push(name);
    }

    const mostUsed = usageStats
      .sort((a, b) => b.count - a.count)
      .slice(0, 5)
      .map(item => item.name);

    return {
      totalTools: this.tools.size,
      toolNames: this.getToolNames(),
      categories: categoryStats,
      framework: 'unified_mcp_v1.0',
      constitutionalCompliant: true,
      analytics: {
        mostUsed,
        byCategory: categoryTools,
        priorityDistribution
      }
    };
  }

  /**
   * Log category status for debugging
   */
  private logCategoryStatus(): void {
    console.log(`[ToolRegistry] Category distribution:`);
    for (const [category, tools] of Array.from(this.categories)) {
      console.log(`  ${category}: ${tools.length} tools`);
    }
  }
}

// Export singleton instance
export const toolRegistry = new ToolRegistry();

/**
 * Unified Context7 Query Tool
 * 
 * Constitutional AI-compliant tool for documentation and context retrieval
 * through the Context7 MCP integration system.
 */

import { UnifiedMCPTool, ToolExecutionResult, InputSchema } from './UnifiedMCPTool';
import { Context7MCPIntegration, WebDocumentationQuery, WebDocumentationResult } from '../mcp/Context7MCPIntegration';
import { OneAgentMemory, OneAgentMemoryConfig } from '../memory/OneAgentMemory';

export interface Context7QueryParams {
  source?: string;
  query: string;
  context?: string;
  maxResults?: number;
  cacheOnly?: boolean;
}

export interface Context7QueryResult extends ToolExecutionResult {
  // The main results are in the 'data' property as required by ToolExecutionResult
}

/**
 * Unified Context7 Query Tool for documentation retrieval
 */
export class UnifiedContext7QueryTool extends UnifiedMCPTool {
  private context7Integration: Context7MCPIntegration;
  private memorySystem: OneAgentMemory;
  public name: string;

  constructor(context7Integration: Context7MCPIntegration) {
    const schema: InputSchema = {
      type: 'object',
      properties: {
        source: { type: 'string', description: 'Documentation source to query (optional)' },
        query: { type: 'string', description: 'Search query for documentation' },
        context: { type: 'string', description: 'Additional context for the search (optional)' },
        maxResults: { type: 'number', description: 'Maximum number of results to return (default: 5)' },
        cacheOnly: { type: 'boolean', description: 'Only return cached results (optional)' }
      },
      required: ['query']
    };

    super(
      'oneagent_context7_query',
      'Query documentation and context from various sources with Constitutional AI validation',
      schema,
      'enhanced'
    );
    
    this.context7Integration = context7Integration;
    const memoryConfig: OneAgentMemoryConfig = {
      apiKey: process.env.MEM0_API_KEY || 'demo-key',
      apiUrl: process.env.MEM0_API_URL
    };
    this.memorySystem = new OneAgentMemory(memoryConfig);
    this.name = 'oneagent_context7_query';
  }

  /**
   * Core execution method implementing documentation search
   */
  public async executeCore(args: Context7QueryParams): Promise<ToolExecutionResult> {
    const startTime = Date.now();

    try {
      // Prepare web documentation query with proper type handling
      const docQuery: WebDocumentationQuery = {
        technology: args.source || 'all',
        topic: args.query,
        maxResults: args.maxResults || 5
      };

      // Add context and version only if provided
      if (args.context) {
        docQuery.context = args.context;
      }

      // Execute web documentation search
      const results = await this.context7Integration.queryWebDocumentation(docQuery);
      const queryTime = Date.now() - startTime;

      // Apply Constitutional AI validation to results
      const validatedResults = await this.validateResults(results, args.query);

      // Store learning in memory
      await this.storeLearning(args, validatedResults, queryTime);

      // Calculate quality score
      const qualityScore = await this.calculateQualityScore(validatedResults);      // Create response data
      const responseData: Context7QueryResult = {
        success: true,
        data: {
          results: validatedResults,
          source: args.source || 'multiple',
          cached: false, // WebDocumentationResult doesn't have cached property
          totalResults: validatedResults.length,
          queryTime,
          metadata: {
            queryType: 'documentation',
            sourcesQueried: args.source ? 1 : this.context7Integration.getAvailableWebSources().length,
            cacheHitRatio: 0, // Will be implemented with getCacheMetrics
            averageResponseTime: queryTime
          }
        },
        qualityScore
      };

      return responseData;

    } catch (error) {
      throw new Error(`Context7 query failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Validate input parameters
   */
  private async validateParams(params: Context7QueryParams): Promise<void> {
    if (!params.query || params.query.trim().length === 0) {
      throw new Error('Query parameter is required and cannot be empty');
    }

    if (params.query.length > 500) {
      throw new Error('Query parameter too long (max 500 characters)');
    }

    if (params.maxResults && (params.maxResults < 1 || params.maxResults > 50)) {
      throw new Error('maxResults must be between 1 and 50');
    }

    // Constitutional AI: Safety check for query content
    const unsafePatterns = [
      /\b(password|secret|token|key)\b/i,
      /\b(hack|exploit|vulnerability)\b/i,
      /\b(malicious|dangerous|harmful)\b/i
    ];

    for (const pattern of unsafePatterns) {
      if (pattern.test(params.query)) {
        throw new Error('Query contains potentially unsafe content');
      }
    }
  }

  /**
   * Validate and filter results using Constitutional AI principles
   */
  private async validateResults(results: WebDocumentationResult[], originalQuery: string): Promise<WebDocumentationResult[]> {
    const validatedResults: WebDocumentationResult[] = [];

    for (const result of results) {
      try {
        // Constitutional AI: Accuracy check
        if (result.relevanceScore < 0.3) {
          continue; // Skip low-relevance results
        }

        // Constitutional AI: Safety check
        if (await this.containsUnsafeContent(result.content)) {
          continue; // Skip potentially unsafe content
        }

        // Constitutional AI: Helpfulness check
        if (await this.isHelpfulForQuery(result, originalQuery)) {
          validatedResults.push(result);
        }

      } catch (error) {
        // Constitutional AI: Transparency - log validation errors        console.warn(`Context7 result validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
        continue;
      }
    }

    return validatedResults;
  }

  /**
   * Check if content contains unsafe information
   */
  private async containsUnsafeContent(content: string): Promise<boolean> {
    const unsafePatterns = [
      /\b(password|secret|token|api[_\-]?key)\s*[:=]\s*\S+/i,
      /\b(private|confidential|internal)\s+(key|token|secret)/i,
      /\bDO\s+NOT\s+(SHARE|DISTRIBUTE|COPY)/i
    ];

    return unsafePatterns.some(pattern => pattern.test(content));
  }

  /**
   * Check if result is helpful for the original query
   */
  private async isHelpfulForQuery(result: WebDocumentationResult, query: string): Promise<boolean> {
    const queryWords = query.toLowerCase().split(/\s+/);
    const contentWords = result.content.toLowerCase().split(/\s+/);
    const titleWords = result.title.toLowerCase().split(/\s+/);

    // Calculate relevance based on word overlap
    const titleMatches = queryWords.filter(word => titleWords.some((tw: string) => tw.includes(word) || word.includes(tw)));
    const contentMatches = queryWords.filter(word => contentWords.some((cw: string) => cw.includes(word) || word.includes(cw)));

    // Require minimum relevance threshold
    const relevanceRatio = (titleMatches.length * 2 + contentMatches.length) / (queryWords.length * 3);
    return relevanceRatio >= 0.3; // 30% relevance threshold
  }

  /**
   * Store learning and context in memory
   */
  private async storeLearning(params: Context7QueryParams, results: WebDocumentationResult[], queryTime: number): Promise<void> {
    try {
      const learning: any = {
        id: `learning_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        agentId: this.name, // Use tool name as agentId for now
        learningType: 'documentation_context',
        content: JSON.stringify({
          query: params.query,
          source: params.source || 'multiple',
          resultsCount: results.length,
          queryTime,
          timestamp: new Date().toISOString(),
          quality: {
            averageRelevance: results.reduce((sum, r) => sum + r.relevanceScore, 0) / (results.length || 1),
            sourcesCovered: results.map(r => r.technology).filter((s, i, arr) => arr.indexOf(s) === i).length,
            cached: 0 // WebDocumentationResult doesn't have cached property
          },
          topResults: results.slice(0, 3).map(r => ({
            title: r.title,
            source: r.technology,
            relevanceScore: r.relevanceScore,
            url: r.sourceUrl
          }))
        }),
        confidence: 0.9,
        applicationCount: 0,
        lastApplied: new Date(),
        sourceConversations: [],
        metadata: {
          tool: 'context7_query',
          source: params.source || 'multiple',
          resultsCount: results.length,
          query: params.query
        }
      };
      await this.memorySystem.addMemory({
        ...learning,
        type: 'learnings'
      });
    } catch (error) {
      // Non-critical error - log but don't fail the main operation
      console.warn(`Failed to store Context7 learning: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Calculate quality score for the results
   */
  private async calculateQualityScore(results: WebDocumentationResult[]): Promise<number> {
    if (results.length === 0) {
      return 0;
    }

    // Base score from result count and relevance
    const avgRelevance = results.reduce((sum, r) => sum + r.relevanceScore, 0) / results.length;
    const countScore = Math.min(results.length / 5, 1); // Normalize to max 5 results
    
    // Bonus for diverse sources
    const uniqueSources = new Set(results.map(r => r.technology)).size;
    const diversityBonus = Math.min(uniqueSources / 3, 0.2); // Max 20% bonus for 3+ sources

    // Cache efficiency bonus
    const cacheRatio = 0; // WebDocumentationResult doesn't have cached property
    const cacheBonus = cacheRatio * 0.1; // Max 10% bonus for full cache hits

    // Calculate final score (0-100)
    const finalScore = (avgRelevance * 60 + countScore * 20 + diversityBonus * 100 + cacheBonus * 100);
    
    return Math.round(Math.min(finalScore, 100));
  }

  /**
   * Get available documentation sources
   */
  public getAvailableSources(): string[] {
    return this.context7Integration.getAvailableWebSources().map((s: any) => s.name);
  }

  /**
   * Get Context7 performance metrics
   */
  public getPerformanceMetrics() {
    return this.context7Integration.getCacheMetrics();
  }
}

/**
 * Unified Context7 Store Tool
 * 
 * Constitutional AI-compliant tool for storing documentation and context
 * through the Context7 MCP integration system.
 */

import { Context7MCPIntegration, WebDevelopmentSource } from '../mcp/Context7MCPIntegration';
import { OneAgentMemory, OneAgentMemoryConfig } from '../memory/OneAgentMemory';

// Removed missing imports: ToolExecutionResult, InputSchema, UnifiedMCPTool, LearningMemory
// Use 'any' for types and add a 'name' property for compatibility

// Minimal stubs for missing types (to be replaced with real types)
type ToolExecutionResult = any;
type InputSchema = any;
class UnifiedMCPTool {
}

export interface Context7StoreParams {
  source: string;
  title: string;
  content: string;
  url?: string;
  version?: string;
  metadata?: Record<string, any>;
  qualityCheck?: boolean;
}

export interface Context7StoreResult extends ToolExecutionResult {
  // The main results are in the 'data' property as required by ToolExecutionResult
}

/**
 * Unified Context7 Store Tool for documentation storage and indexing
 */
export class UnifiedContext7StoreTool extends UnifiedMCPTool {
  private context7Integration: any;
  private memorySystem!: OneAgentMemory;
  public name!: string;
  public readonly description: string = 'Store documentation and context with Constitutional AI validation and quality scoring';
  public readonly schema: InputSchema = {};
  public readonly category: string = 'enhanced';
  public readonly constitutionalLevel: 'basic' | 'enhanced' | 'critical' = 'enhanced';

  /**
   * Core execution method implementing documentation storage
   */
  public async executeCore(args: Context7StoreParams): Promise<ToolExecutionResult> {
    const startTime = Date.now();

    try {
      // Apply Constitutional AI validation to content
      await this.validateContentSafety(args);

      // Perform quality check if requested (default: true)
      const qualityScore = args.qualityCheck !== false ? 
        await this.performQualityCheck(args) : 80;

      // Prepare documentation entry
      const documentationEntry = {
        source: args.source,
        title: args.title,
        content: args.content,
        url: args.url,
        version: args.version,
        metadata: {
          ...args.metadata,
          storedAt: new Date().toISOString(),
          qualityScore,
          constitutionalCompliant: true,
          toolName: this.name
        },
        relevanceScore: qualityScore / 100 // Convert to 0-1 scale for relevance
      };

      // Store in Context7 cache/index
      const storeResult = await this.storeInContext7(documentationEntry);

      // Store learning in unified memory
      await this.storeLearning(args, storeResult, Date.now() - startTime);

      // Create response
      const responseData: Context7StoreResult = {
        success: true,
        data: {
          stored: true,
          documentId: storeResult.documentId,
          source: args.source,
          qualityScore,
          cached: true,
          indexUpdated: storeResult.indexUpdated,
          storageTime: Date.now() - startTime,
          metadata: {
            operation: 'documentation_store',
            contentLength: args.content.length,
            titleLength: args.title.length,
            hasUrl: !!args.url,
            hasVersion: !!args.version,
            qualityValidation: args.qualityCheck !== false
          }
        },
        qualityScore
      };

      return responseData;

    } catch (error) {
      throw new Error(`Context7 store failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Validate content safety using Constitutional AI principles
   */
  private async validateContentSafety(args: Context7StoreParams): Promise<void> {
    // Constitutional AI: Safety validation
    const unsafePatterns = [
      /\b(password|secret|token|api[_\-]?key)\s*[:=]\s*\S+/i,
      /\b(private|confidential|internal)\s+(key|token|secret)/i,
      /\b(malicious|harmful|dangerous)\s+(code|script|command)/i,
      /\bDO\s+NOT\s+(SHARE|DISTRIBUTE|COPY)/i
    ];

    const fullContent = `${args.title} ${args.content} ${args.url || ''}`;
    
    for (const pattern of unsafePatterns) {
      if (pattern.test(fullContent)) {
        throw new Error('Content contains potentially unsafe information and cannot be stored');
      }
    }

    // Content length validation
    if (args.content.length > 100000) { // 100KB limit
      throw new Error('Content too large (max 100KB)');
    }

    if (args.title.length > 500) {
      throw new Error('Title too long (max 500 characters)');
    }

    // Constitutional AI: Accuracy validation - ensure content has substance
    if (args.content.trim().length < 50) {
      throw new Error('Content too short to be meaningful (min 50 characters)');
    }
  }

  /**
   * Perform quality assessment of the documentation content
   */
  private async performQualityCheck(args: Context7StoreParams): Promise<number> {
    let score = 100;

    // Content quality factors
    const contentWords = args.content.split(/\s+/).length;
    const titleWords = args.title.split(/\s+/).length;

    // Penalize very short content
    if (contentWords < 20) {
      score -= 20;
    } else if (contentWords < 50) {
      score -= 10;
    }

    // Penalize poor titles
    if (titleWords < 2) {
      score -= 15;
    } else if (titleWords > 15) {
      score -= 5;
    }

    // Reward structured content
    if (args.content.includes('\n') && args.content.includes('```')) {
      score += 10; // Code examples
    }

    if (args.content.match(/^#+\s/m)) {
      score += 5; // Markdown headers
    }

    // Reward metadata completeness
    if (args.url) score += 5;
    if (args.version) score += 5;
    if (args.metadata && Object.keys(args.metadata).length > 0) score += 5;

    // Constitutional AI: Helpfulness assessment
    const helpfulnessScore = await this.assessHelpfulness(args);
    score = Math.floor((score + helpfulnessScore) / 2);

    return Math.max(0, Math.min(100, score));
  }

  /**
   * Assess content helpfulness
   */
  private async assessHelpfulness(args: Context7StoreParams): Promise<number> {
    let helpfulnessScore = 80; // Base score

    // Check for common helpful patterns
    const helpfulPatterns = [
      /\b(example|sample|demo|tutorial)\b/i,
      /\b(how\s+to|step\s+by\s+step|guide)\b/i,
      /\b(api|function|method|class)\b/i,
      /\b(parameter|argument|return|throws)\b/i,
      /```[\s\S]*?```/g, // Code blocks
      /\n\s*[-*+]\s+/g // Lists
    ];

    const matches = helpfulPatterns.reduce((count, pattern) => {
      const match = args.content.match(pattern);
      return count + (match ? match.length : 0);
    }, 0);

    helpfulnessScore += Math.min(20, matches * 3); // Bonus for helpful patterns

    return Math.min(100, helpfulnessScore);
  }
  /**
   * Store documentation in Context7 integration
   */
  private async storeInContext7(_entry: any): Promise<{ documentId: string; indexUpdated: boolean }> {
